import React, { useEffect, useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  FaClipboardList,
  FaCheckCircle,
  FaChartBar,
  FaChartPie,
  FaArrowRight,
  FaSpinner,
  FaArrowLeft,
  FaClock,
  FaTimesCircle,
  FaCalendarAlt,
  FaUserTie,
  FaFileContract,
  FaSearch
} from 'react-icons/fa';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, registerables } from 'chart.js';
import { DarkModeContext } from '../../Components/DarkModeContext';

ChartJS.register(...registerables);

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

const DashboardEntreprise = () => {
  const { darkMode } = useContext(DarkModeContext);
  const navigate = useNavigate();
  
  const [dashboardData, setDashboardData] = useState({
    stats: {
      totalOffres: 0,
      totalCandidatures: 0,
      candidaturesEnAttente: 0,
      candidaturesValides: 0,
      candidaturesRejetees: 0
    },
    latestOffers: [],
    recentCandidatures: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const [statsRes, offersRes, candidaturesRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/entreprise/stats`, {
            headers: { Authorization: `Bearer ${token}` }
          }),
          axios.get(`${API_BASE_URL}/api/offres/recent`, {
            headers: { Authorization: `Bearer ${token}` }
          }),
          axios.get(`${API_BASE_URL}/api/candidatures/recent`, {
            headers: { Authorization: `Bearer ${token}` }
          })
        ]);

        const data = {
          stats: statsRes.data,
          latestOffers: offersRes.data,
          recentCandidatures: candidaturesRes.data
        };

        setDashboardData(data);
        prepareChartData(data.stats);
      } catch (err) {
        console.error('Erreur chargement dashboard:', err);
        setError(err.response?.data?.message || 'Erreur de chargement');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [navigate]);

  const prepareChartData = (stats) => {
    const colors = darkMode ? {
      success: 'rgba(74, 222, 128, 0.8)',
      warning: 'rgba(250, 204, 21, 0.8)',
      danger: 'rgba(248, 113, 113, 0.8)',
      primary: 'rgba(96, 165, 250, 0.8)',
      secondary: 'rgba(192, 132, 252, 0.8)'
    } : {
      success: 'rgba(34, 197, 94, 0.7)',
      warning: 'rgba(234, 179, 8, 0.7)',
      danger: 'rgba(239, 68, 68, 0.7)',
      primary: 'rgba(59, 130, 246, 0.7)',
      secondary: 'rgba(168, 85, 247, 0.7)'
    };

    setChartData({
      bar: {
        labels: ['Acceptées', 'En attente', 'Rejetées'],
        datasets: [{
          label: 'Statut des Candidatures',
          data: [
            stats.candidaturesValides,
            stats.candidaturesEnAttente,
            stats.candidaturesRejetees
          ],
          backgroundColor: [colors.success, colors.warning, colors.danger],
          borderColor: [colors.success, colors.warning, colors.danger],
          borderWidth: 1
        }]
      },
      pie: {
        labels: ['Offres disponibles', 'Candidatures envoyées'],
        datasets: [{
          data: [stats.totalOffres, stats.totalCandidatures],
          backgroundColor: [colors.primary, colors.secondary],
          borderColor: [colors.primary, colors.secondary],
          borderWidth: 1
        }]
      }
    });
  };

  const refreshData = async () => {
    setLoading(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${API_BASE_URL}/api/entreprise/dashboard`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDashboardData(response.data);
      prepareChartData(response.data.stats);
    } catch (err) {
      setError(err.response?.data?.message || 'Erreur de rafraîchissement');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className={`min-h-screen flex items-center justify-center ${darkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
        <div className="text-center">
          <FaSpinner className="animate-spin text-4xl text-blue-600 dark:text-blue-400 mx-auto mb-4" />
          <p className={`text-lg ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
            Chargement des données...
          </p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`min-h-screen flex items-center justify-center ${darkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
        <div className="text-center max-w-md p-6 rounded-lg shadow-lg">
          <div className={`p-4 rounded-lg ${darkMode ? 'bg-gray-800 text-red-400' : 'bg-red-50 text-red-700'}`}>
            <h3 className="font-bold text-lg mb-2">Erreur</h3>
            <p className="mb-4">{error}</p>
            <button
              onClick={refreshData}
              className={`px-4 py-2 rounded-md font-medium ${
                darkMode 
                  ? 'bg-blue-600 hover:bg-blue-700 text-white' 
                  : 'bg-blue-100 hover:bg-blue-200 text-blue-800'
              }`}
            >
              <FaSpinner className="inline mr-2" />
              Réessayer
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
      {/* Header */}
      <header className={`sticky top-0 z-10 ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-sm`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate(-1)}
                className={`p-2 rounded-full ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
              >
                <FaArrowLeft className={`h-5 w-5 ${darkMode ? 'text-gray-300' : 'text-gray-600'}`} />
              </button>
              <h1 className={`text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                Tableau de Bord
              </h1>
            </div>
            <button
              onClick={refreshData}
              className={`p-2 rounded-full ${darkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-100'}`}
              title="Rafraîchir les données"
            >
              <FaSpinner className={`h-5 w-5 ${darkMode ? 'text-blue-400' : 'text-blue-600'}`} />
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatCard
            icon={<FaFileContract className="text-blue-500" size={20} />}
            title="Offres Actives"
            value={dashboardData.stats.totalOffres}
            link="/entreprise/offres-disponibles"
            linkText="Voir offres"
            darkMode={darkMode}
          />
          <StatCard
            icon={<FaUserTie className="text-green-500" size={20} />}
            title="Candidatures"
            value={dashboardData.stats.totalCandidatures}
            link="/entreprise/mescandidatures"
            linkText="Voir toutes"
            darkMode={darkMode}
          />
          <StatCard
            icon={<FaCheckCircle className="text-teal-500" size={20} />}
            title="Acceptées"
            value={dashboardData.stats.candidaturesValides}
            link="/entreprise/mescandidatures?status=valide"
            linkText="Voir détails"
            darkMode={darkMode}
          />
          <StatCard
            icon={<FaTimesCircle className="text-red-500" size={20} />}
            title="Rejetées"
            value={dashboardData.stats.candidaturesRejetees}
            link="/entreprise/mescandidatures?status=rejetee"
            linkText="Voir détails"
            darkMode={darkMode}
          />
        </div>

        {/* Recent Activity */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-8">
          {/* Dernières offres */}
          <div className={`rounded-xl shadow-lg overflow-hidden ${darkMode ? 'bg-gray-800' : 'bg-white'} lg:col-span-2`}>
            <div className={`p-6 border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}`}>
              <div className="flex items-center justify-between">
                <h2 className={`text-xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                  <FaClipboardList className="inline mr-2 text-blue-500" />
                  Dernières Offres
                </h2>
                <Link
                  to="/entreprise/offres-disponibles"
                  className={`text-sm flex items-center ${darkMode ? 'text-blue-400 hover:text-blue-300' : 'text-blue-600 hover:text-blue-800'}`}
                >
                  Voir plus <FaArrowRight className="ml-1" />
                </Link>
              </div>
            </div>
            <div className="divide-y divide-gray-200 dark:divide-gray-700">
              {dashboardData.latestOffers.length > 0 ? (
                dashboardData.latestOffers.slice(0, 5).map(offer => (
                  <OfferItem key={offer.id} offer={offer} darkMode={darkMode} />
                ))
              ) : (
                <div className="p-6 text-center text-gray-500 dark:text-gray-400">
                  Aucune offre récente
                </div>
              )}
            </div>
          </div>

          {/* Candidatures récentes */}
          <div className={`rounded-xl shadow-lg overflow-hidden ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
            <div className={`p-6 border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}`}>
              <div className="flex items-center justify-between">
                <h2 className={`text-xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                  <FaSearch className="inline mr-2 text-purple-500" />
                  Candidatures Récentes
                </h2>
                <Link
                  to="/entreprise/mescandidatures"
                  className={`text-sm flex items-center ${darkMode ? 'text-blue-400 hover:text-blue-300' : 'text-blue-600 hover:text-blue-800'}`}
                >
                  Voir plus <FaArrowRight className="ml-1" />
                </Link>
              </div>
            </div>
            <div className="divide-y divide-gray-200 dark:divide-gray-700">
              {dashboardData.recentCandidatures.length > 0 ? (
                dashboardData.recentCandidatures.slice(0, 5).map(candidature => (
                  <CandidatureItem key={candidature.id} candidature={candidature} darkMode={darkMode} />
                ))
              ) : (
                <div className="p-6 text-center text-gray-500 dark:text-gray-400">
                  Aucune candidature récente
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Charts Section */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <ChartContainer 
            title="Statut des Candidatures" 
            icon={<FaChartBar className="text-blue-500" />}
            darkMode={darkMode}
          >
            {chartData && (
              <Bar 
                data={chartData.bar} 
                options={getChartOptions('bar', darkMode)} 
                height={300}
              />
            )}
          </ChartContainer>

          <ChartContainer 
            title="Répartition Offres/Candidatures" 
            icon={<FaChartPie className="text-purple-500" />}
            darkMode={darkMode}
          >
            {chartData && (
              <Pie 
                data={chartData.pie} 
                options={getChartOptions('pie', darkMode)} 
                height={300}
              />
            )}
          </ChartContainer>
        </div>
      </main>
    </div>
  );
};

// Composants réutilisables
const StatCard = ({ icon, title, value, link, linkText, darkMode }) => (
  <div className={`rounded-xl shadow-lg overflow-hidden ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
    <div className="p-6">
      <div className="flex items-center space-x-4">
        <div className={`p-3 rounded-full ${darkMode ? 'bg-gray-700' : 'bg-gray-100'}`}>
          {icon}
        </div>
        <div>
          <h3 className={`text-sm font-medium ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>
            {title}
          </h3>
          <p className={`text-2xl font-bold mt-1 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            {value}
          </p>
        </div>
      </div>
      <Link
        to={link}
        className={`mt-4 inline-flex items-center text-sm font-medium ${
          darkMode ? 'text-blue-400 hover:text-blue-300' : 'text-blue-600 hover:text-blue-800'
        }`}
      >
        {linkText} <FaArrowRight className="ml-1" />
      </Link>
    </div>
  </div>
);

const OfferItem = ({ offer, darkMode }) => {
  const isExpired = new Date(offer.dateLimite) < new Date();
  
  return (
    <div className={`p-4 hover:${darkMode ? 'bg-gray-700' : 'bg-gray-50'} transition-colors`}>
      <div className="flex items-start space-x-4">
        <div className={`flex-shrink-0 p-2 rounded-lg ${darkMode ? 'bg-gray-700' : 'bg-gray-100'}`}>
          <FaFileContract className={`h-5 w-5 ${darkMode ? 'text-blue-400' : 'text-blue-600'}`} />
        </div>
        <div className="flex-1 min-w-0">
          <h4 className={`text-sm font-semibold truncate ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            {offer.title}
          </h4>
          <p className={`text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'} mt-1`}>
            {offer.domaine}
          </p>
          <div className="flex items-center mt-2 text-xs">
            <FaCalendarAlt className={`mr-1 ${isExpired ? 'text-red-500' : darkMode ? 'text-gray-400' : 'text-gray-500'}`} />
            <span className={isExpired ? 'text-red-500' : darkMode ? 'text-gray-400' : 'text-gray-500'}>
              {new Date(offer.dateLimite).toLocaleDateString('fr-FR')}
              {isExpired && ' (Expirée)'}
            </span>
          </div>
        </div>
        {!isExpired && (
          <Link
            to={`/entreprise/postuler/${offer.id}`}
            className="inline-flex items-center px-3 py-1 border border-transparent text-xs font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700"
          >
            Postuler
          </Link>
        )}
      </div>
    </div>
  );
};

const CandidatureItem = ({ candidature, darkMode }) => {
  const getStatusInfo = (status) => {
    switch (status) {
      case 'valide':
        return { text: 'Acceptée', color: 'text-green-500', bg: 'bg-green-100 dark:bg-green-900' };
      case 'rejetee':
        return { text: 'Rejetée', color: 'text-red-500', bg: 'bg-red-100 dark:bg-red-900' };
      default:
        return { text: 'En attente', color: 'text-yellow-500', bg: 'bg-yellow-100 dark:bg-yellow-900' };
    }
  };

  const statusInfo = getStatusInfo(candidature.status);

  return (
    <div className={`p-4 hover:${darkMode ? 'bg-gray-700' : 'bg-gray-50'} transition-colors`}>
      <div className="flex items-start space-x-4">
        <div className={`flex-shrink-0 p-2 rounded-lg ${statusInfo.bg}`}>
          <FaUserTie className={`h-5 w-5 ${statusInfo.color}`} />
        </div>
        <div className="flex-1 min-w-0">
          <h4 className={`text-sm font-semibold truncate ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            {candidature.offer?.title || `Offre #${candidature.offerId}`}
          </h4>
          <p className={`text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'} mt-1`}>
            {new Date(candidature.createdAt).toLocaleDateString('fr-FR')}
          </p>
        </div>
        <span className={`text-xs font-medium px-2 py-1 rounded-full ${statusInfo.color} ${statusInfo.bg}`}>
          {statusInfo.text}
        </span>
      </div>
    </div>
  );
};

const ChartContainer = ({ title, icon, children, darkMode }) => (
  <div className={`rounded-xl shadow-lg overflow-hidden ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
    <div className={`p-6 border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}`}>
      <h2 className={`text-xl font-semibold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
        {icon} {title}
      </h2>
    </div>
    <div className="p-6">
      {children}
    </div>
  </div>
);

// Options des graphiques
const getChartOptions = (type, darkMode) => {
  const textColor = darkMode ? '#e5e7eb' : '#374151';
  const gridColor = darkMode ? 'rgba(75, 85, 99, 0.3)' : 'rgba(209, 213, 219, 0.3)';

  const baseOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          color: textColor,
          font: {
            size: 12
          },
          padding: 20
        }
      },
      tooltip: {
        backgroundColor: darkMode ? '#1f2937' : '#ffffff',
        titleColor: darkMode ? '#ffffff' : '#111827',
        bodyColor: darkMode ? '#d1d5db' : '#4b5563',
        borderColor: darkMode ? '#4b5563' : '#d1d5db',
        borderWidth: 1
      }
    }
  };

  if (type === 'bar') {
    return {
      ...baseOptions,
      scales: {
        y: {
          beginAtZero: true,
          ticks: {
            color: textColor,
            stepSize: 1
          },
          grid: {
            color: gridColor,
            drawBorder: false
          }
        },
        x: {
          ticks: {
            color: textColor
          },
          grid: {
            color: gridColor,
            drawBorder: false
          }
        }
      }
    };
  }

  return baseOptions;
};

export default DashboardEntreprise;
