import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';

import Accueil from './Pages/Home/Accueil';
import Home from './Pages/Home/Home';
import Login from './Pages/Auth/Login';
import Register from './Pages/Auth/Register';

import OffreDetail from './Pages/Offre/OffreDetail';
import Postuler from './Pages/Offre/Postuler';
import MesCandidatures from './Pages/Offre/MesCandidatures';

import DashboardAdmin from './Pages/Dashbord/DashboardAdmin';
import DashboardEntreprise from './Pages/Dashbord/DashboardEntreprise';
import DashboardMinistere from './Pages/Dashbord/DashboardMinistere';

import Navbar from './Components/Navbar';
import Logout from './Components/Logout';

function App() {
  return (
    <BrowserRouter>
      {/* Afficher le Navbar seulement sur la page d'accueil (/) et pas sur /login */}
      {(window.location.pathname === '/' ) && <Navbar />}
      <Routes>
        {/* Authentification */}
        <Route path="/" element={<Accueil />} />
        <Route path="/home" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Dashboards */}
        <Route path="/dashboard/admin" element={<DashboardAdmin />} />
        <Route path="/dashboard/ministere" element={<DashboardMinistere />} />
        <Route path="/dashboard/entreprise" element={<DashboardEntreprise />} />

        {/* Offres */}
        <Route path="/offre/:id" element={<OffreDetail />} />
        <Route path="/postuler/:id" element={<Postuler />} />
        <Route path="/mes-candidatures" element={<MesCandidatures />} />

        {/* Redirection par d√©faut */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
