import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  FaHome, FaUsers, FaBriefcase, FaFileAlt, FaCog, FaSignOutAlt,
  FaChartBar, FaChartLine, FaArrowUp, FaUserFriends,
  FaBuilding, FaClipboardList, FaEye, FaEdit, FaTrash
} from "react-icons/fa";
import { Line, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement,
  Title, Tooltip, Legend, ArcElement
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, ArcElement);

const Sidebar = ({ darkMode }) => (
  <div className="min-h-screen w-64 bg-gray-900 text-white shadow-2xl">
    {/* Header */}
    <div className="p-6 border-b border-gray-700">
      <div className="flex items-center space-x-3">
        <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-pink-500 rounded-lg flex items-center justify-center">
          <FaHome className="text-white text-lg" />
        </div>
        <h1 className="text-xl font-bold">Fusion</h1>
      </div>
    </div>

    {/* Main Navigation */}
    <div className="p-4">
      <div className="mb-6">
        <h3 className="text-xs uppercase tracking-wide text-gray-400 mb-3 px-3">MAIN NAVIGATION</h3>
        <nav className="space-y-1">
          <a href="#" className="flex items-center space-x-3 px-3 py-2 rounded-lg bg-gray-800 text-white">
            <FaHome className="text-lg" />
            <span>Dashboard</span>
          </a>
          <a href="#" className="flex items-center space-x-3 px-3 py-2 rounded-lg text-gray-300 hover:bg-gray-800 hover:text-white transition-colors">
            <FaUsers className="text-lg" />
            <span>Users</span>
          </a>
          <a href="#" className="flex items-center space-x-3 px-3 py-2 rounded-lg text-gray-300 hover:bg-gray-800 hover:text-white transition-colors">
            <FaBriefcase className="text-lg" />
            <span>Job Offers</span>
          </a>
          <a href="#" className="flex items-center space-x-3 px-3 py-2 rounded-lg text-gray-300 hover:bg-gray-800 hover:text-white transition-colors">
            <FaFileAlt className="text-lg" />
            <span>Applications</span>
          </a>
        </nav>
      </div>

      <div>
        <h3 className="text-xs uppercase tracking-wide text-gray-400 mb-3 px-3">ACCOUNT</h3>
        <nav className="space-y-1">
          <a href="#" className="flex items-center space-x-3 px-3 py-2 rounded-lg text-gray-300 hover:bg-gray-800 hover:text-white transition-colors">
            <FaCog className="text-lg" />
            <span>Paramètres</span>
          </a>
          <a href="#" className="flex items-center space-x-3 px-3 py-2 rounded-lg text-gray-300 hover:bg-gray-800 hover:text-white transition-colors">
            <FaSignOutAlt className="text-lg" />
            <span>Déconnexion</span>
          </a>
        </nav>
      </div>
    </div>
  </div>
);

const StatCard = ({ title, value, icon, gradient, percentage, trend }) => (
  <div className={`relative p-6 rounded-2xl text-white ${gradient} overflow-hidden`}>
    <div className="relative z-10">
      <div className="flex items-center justify-between mb-4">
        <div className="text-sm font-medium opacity-90">{title}</div>
        <div className="text-2xl opacity-80">{icon}</div>
      </div>
      <div className="text-3xl font-bold mb-2">{value}</div>
      <div className="flex items-center space-x-2 text-sm opacity-90">
        <span className={`flex items-center ${trend > 0 ? 'text-green-200' : 'text-red-200'}`}>
          <FaArrowUp className={`mr-1 ${trend > 0 ? '' : 'transform rotate-180'}`} />
          {Math.abs(percentage)}%
        </span>
        <span>vs last month</span>
      </div>
    </div>
    <div className="absolute -right-4 -top-4 w-24 h-24 bg-white opacity-10 rounded-full"></div>
  </div>
);

const DashboardMinistere = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchDashboardData = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:3001/api/dashboard/ministere", {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setDashboardData(response.data);
      setLoading(false);
    } catch (error) {
      console.error("Erreur lors du chargement du dashboard :", error.message);
      setError(error.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-xl font-semibold text-gray-600">Chargement en cours...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-xl font-semibold text-red-600">Erreur: {error}</div>
      </div>
    );
  }

  if (!dashboardData) return null;

  // Extract data from API response
  const { users, offers, candidatures, stats, chartData } = dashboardData;
  const { users: userStats, offers: offerStats, candidatures: candidatureStats } = stats;

  // Chart data for line chart
  const lineChartData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Utilisateurs',
        data: [12, 19, 15, 25, 22, 30],
        borderColor: 'rgb(168, 85, 247)',
        backgroundColor: 'rgba(168, 85, 247, 0.1)',
        tension: 0.4,
        fill: true,
      },
      {
        label: 'Offres',
        data: [8, 12, 18, 15, 20, 25],
        borderColor: 'rgb(59, 130, 246)',
        backgroundColor: 'rgba(59, 130, 246, 0.1)',
        tension: 0.4,
        fill: true,
      }
    ]
  };

  // Doughnut chart data
  const doughnutData = {
    labels: ['Utilisateurs Actifs', 'Offres Validées', 'Candidatures'],
    datasets: [{
      data: [totalUsers, activeOffers, totalCandidatures],
      backgroundColor: [
        'rgb(168, 85, 247)',
        'rgb(59, 130, 246)',
        'rgb(16, 185, 129)'
      ],
      borderWidth: 0,
    }]
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false,
      },
    },
    scales: {
      x: {
        display: false,
      },
      y: {
        display: false,
      },
    },
  };

  const doughnutOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'bottom',
        labels: {
          usePointStyle: true,
          padding: 20,
        }
      },
    },
    cutout: '70%',
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-xl font-semibold text-gray-600">Chargement en cours...</div>
      </div>
    );
  }

  return (
    <div className="flex bg-gray-50 min-h-screen">
      <Sidebar />
      
      <div className="flex-1 p-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Dashboard</h1>
          <p className="text-gray-600">Bienvenue dans votre tableau de bord administrateur</p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatCard
            title="Utilisateurs"
            value={totalUsers}
            icon={<FaUsers />}
            gradient="bg-gradient-to-br from-purple-500 to-purple-700"
            percentage={12}
            trend={1}
          />
          <StatCard
            title="Total Offres"
            value={totalOffers}
            icon={<FaBriefcase />}
            gradient="bg-gradient-to-br from-blue-500 to-blue-700"
            percentage={8}
            trend={1}
          />
          <StatCard
            title="Candidatures"
            value={totalCandidatures}
            icon={<FaFileAlt />}
            gradient="bg-gradient-to-br from-cyan-500 to-cyan-700"
            percentage={15}
            trend={1}
          />
          <StatCard
            title="Offres Validées"
            value={activeOffers}
            icon={<FaChartLine />}
            gradient="bg-gradient-to-br from-orange-500 to-orange-700"
            percentage={23}
            trend={1}
          />
        </div>

        {/* Charts Section */}
        <div className="grid grid-cols-1 lg:grid-cols-8 gap-6 mb-8">
          {/* Line Chart */}
          <div className="lg:col-span-5 bg-white rounded-2xl p-6 shadow-sm">
            <div className="flex items-center justify-between mb-6">
              <div>
                <h3 className="text-lg font-semibold text-gray-900">Analytics</h3>
                <p className="text-sm text-gray-500">Évolution mensuelle</p>
              </div>
              <div className="text-2xl font-bold text-purple-600">
                ${(totalUsers * 1.5 + totalOffers * 2.3).toFixed(0)}
              </div>
            </div>
            <div className="h-64">
              <Line data={lineChartData} options={chartOptions} />
            </div>
          </div>

          {/* Doughnut Chart */}
          <div className="lg:col-span-3 bg-white rounded-2xl p-6 shadow-sm">
            <div className="mb-6">
              <h3 className="text-lg font-semibold text-gray-900">Répartition</h3>
              <div className="text-3xl font-bold text-purple-600 mt-2">1.56k</div>
            </div>
            <div className="h-48">
              <Doughnut data={doughnutData} options={doughnutOptions} />
            </div>
          </div>
        </div>

        {/* Activity Tables */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Recent Activities */}
          <div className="bg-white rounded-2xl p-6 shadow-sm">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900">Activités Récentes</h3>
              <button className="text-sm text-purple-600 hover:text-purple-700">Voir tout</button>
            </div>
            <div className="space-y-4">
              {users.slice(0, 5).map((user, index) => (
                <div key={user.id} className="flex items-center space-x-4">
                  <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center">
                    <FaUserFriends className="text-white text-sm" />
                  </div>
                  <div className="flex-1">
                    <p className="text-sm font-medium text-gray-900">{user.nom}</p>
                    <p className="text-xs text-gray-500">Nouvel utilisateur - {user.role}</p>
                  </div>
                  <div className="text-xs text-gray-400">2 min</div>
                </div>
              ))}
            </div>
          </div>

          {/* Order Status */}
          <div className="bg-white rounded-2xl p-6 shadow-sm">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900">État des Offres</h3>
              <button className="text-sm text-purple-600 hover:text-purple-700">Gérer</button>
            </div>
            <div className="space-y-4">
              {offers.slice(0, 5).map((offer, index) => (
                <div key={offer.id} className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-gray-100 rounded-lg flex items-center justify-center">
                      <FaBriefcase className="text-gray-600 text-sm" />
                    </div>
                    <div>
                      <p className="text-sm font-medium text-gray-900">{offer.title}</p>
                      <p className="text-xs text-gray-500">{offer.company}</p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                      offer.status === 'valide' ? 'bg-green-100 text-green-800' :
                      offer.status === 'en_attente' ? 'bg-yellow-100 text-yellow-800' :
                      'bg-red-100 text-red-800'
                    }`}>
                      {offer.status}
                    </span>
                    <button className="text-gray-400 hover:text-gray-600">
                      <FaEye className="text-sm" />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardMinistere;
