import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaTrash, FaMoon, FaSun, FaUserTie, FaBriefcase, FaChartBar } from "react-icons/fa";
import { Bar, Pie } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

const DashboardMinistere = () => {
  const [users, setUsers] = useState([]);
  const [offers, setOffers] = useState([]);
  const [applications, setApplications] = useState([]);
  const [domaineStats, setDomaineStats] = useState([]);
  const [appEntrepriseStats, setAppEntrepriseStats] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem("token");
      const [usersRes, offersRes, applicationsRes, domaineRes, appEntrepriseRes] = await Promise.all([
        axios.get("http://localhost:3001/api/users", {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get("http://localhost:3001/api/offers", {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get("http://localhost:3001/api/applications", {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get("http://localhost:3001/api/offers/stats/domaines", {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get("http://localhost:3001/api/applications/stats/by-entreprise", {
          headers: { Authorization: `Bearer ${token}` }
        }),
      ]);

      setUsers(usersRes.data);
      setOffers(offersRes.data);
      setApplications(applicationsRes.data);
      setDomaineStats(domaineRes.data);
      setAppEntrepriseStats(appEntrepriseRes.data);
      setLoading(false);
    } catch (error) {
      console.error("Erreur lors du chargement des données :", error.message);
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:3001/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(users.filter(user => user.id !== userId));
    } catch (error) {
      console.error("Erreur lors de la suppression :", error.message);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const offerStatusData = {
    labels: ['Actives', 'Expirées', 'En attente'],
    datasets: [{
      label: 'Offres',
      data: [
        offers.filter(o => o.status === 'active').length,
        offers.filter(o => o.status === 'expired').length,
        offers.filter(o => o.status === 'pending').length
      ],
      backgroundColor: ['#4bc0c0', '#ff6384', '#ffce56'],
      borderColor: ['#4bc0c0', '#ff6384', '#ffce56'],
      borderWidth: 1
    }]
  };

  const applicationStatusData = {
    labels: ['En attente', 'Acceptées', 'Rejetées'],
    datasets: [{
      label: 'Candidatures',
      data: [
        applications.filter(a => a.status === 'pending').length,
        applications.filter(a => a.status === 'accepted').length,
        applications.filter(a => a.status === 'rejected').length
      ],
      backgroundColor: ['#36a2eb', '#4bc0c0', '#ff6384'],
      borderColor: ['#36a2eb', '#4bc0c0', '#ff6384'],
      borderWidth: 1
    }]
  };

  const userRoleData = {
    labels: ['Administrateurs', 'Entreprises', 'Candidats'],
    datasets: [{
      label: 'Utilisateurs',
      data: [
        users.filter(u => u.role === 'admin').length,
        users.filter(u => u.role === 'entreprise').length,
        users.filter(u => u.role === 'candidat').length
      ],
      backgroundColor: ['#9966ff', '#ff9f40', '#c9cbcf'],
      borderColor: ['#9966ff', '#ff9f40', '#c9cbcf'],
      borderWidth: 1
    }]
  };

  const domaineChartData = {
    labels: domaineStats.map(d => d.domaine),
    datasets: [{
      label: 'Offres par domaine',
      data: domaineStats.map(d => d.total),
      backgroundColor: '#ffcd56',
      borderColor: '#ffcd56',
      borderWidth: 1
    }]
  };

  const appEntrepriseChart = {
    labels: appEntrepriseStats.map(e => e.entreprise),
    datasets: [{
      label: 'Candidatures par entreprise',
      data: appEntrepriseStats.map(e => e.total),
      backgroundColor: '#36a2eb',
      borderColor: '#36a2eb',
      borderWidth: 1
    }]
  };

  if (loading) {
    return (
      <div className={`flex items-center justify-center min-h-screen ${darkMode ? 'dark:bg-gray-900' : 'bg-gray-50'}`}>
        <div className="text-xl font-semibold">Chargement en cours...</div>
      </div>
    );
  }

  return (
    <div className={`p-6 min-h-screen ${darkMode ? 'dark:bg-gray-900 dark:text-white' : 'bg-gray-50'}`}>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold">Tableau de bord Ministère</h2>
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="p-2 rounded-full bg-gray-200 dark:bg-gray-700"
        >
          {darkMode ? <FaSun className="text-yellow-400" /> : <FaMoon className="text-gray-600" />}
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <StatCard icon={<FaUserTie />} color="blue" label="Utilisateurs" value={users.length} darkMode={darkMode} />
        <StatCard icon={<FaBriefcase />} color="green" label="Offres d'emploi" value={offers.length} darkMode={darkMode} />
        <StatCard icon={<FaChartBar />} color="purple" label="Candidatures" value={applications.length} darkMode={darkMode} />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
        <ChartCard title="Statut des offres" data={offerStatusData} type="pie" darkMode={darkMode} />
        <ChartCard title="Statut des candidatures" data={applicationStatusData} type="pie" darkMode={darkMode} />
        <ChartCard title="Répartition des utilisateurs" data={userRoleData} type="bar" darkMode={darkMode} />
        <ChartCard title="Offres par domaine" data={domaineChartData} type="bar" darkMode={darkMode} />
        <ChartCard title="Candidatures par entreprise" data={appEntrepriseChart} type="bar" darkMode={darkMode} />
      </div>

      <div className={`p-6 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
        <h3 className="text-xl font-semibold mb-4">Liste des utilisateurs</h3>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
            <thead>
              <tr>
                <TableHeader title="Nom" />
                <TableHeader title="Email" />
                <TableHeader title="Rôle" />
                <TableHeader title="Actions" />
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200 dark:divide-gray-700">
              {users.map((user) => (
                <tr key={user.id}>
                  <td className="px-4 py-3 whitespace-nowrap">{user.username}</td>
                  <td className="px-4 py-3 whitespace-nowrap">{user.email}</td>
                  <td className="px-4 py-3 whitespace-nowrap">
                    <span className={`px-2 py-1 text-xs rounded-full ${
                      user.role === 'admin' ? 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200' :
                      user.role === 'entreprise' ? 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200' :
                      'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200'
                    }`}>
                      {user.role}
                    </span>
                  </td>
                  <td className="px-4 py-3 whitespace-nowrap">
                    <button 
                      onClick={() => handleDeleteUser(user.id)} 
                      className="text-red-600 hover:text-red-800 dark:hover:text-red-400"
                    >
                      <FaTrash />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

const StatCard = ({ icon, color, label, value, darkMode }) => (
  <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
    <div className="flex items-center">
      <div className={`p-3 rounded-full bg-${color}-100 dark:bg-${color}-900 mr-4`}>
        {React.cloneElement(icon, { className: `text-${color}-600 dark:text-${color}-300` })}
      </div>
      <div>
        <p className="text-sm text-gray-500 dark:text-gray-300">{label}</p>
        <p className="text-2xl font-bold">{value}</p>
      </div>
    </div>
  </div>
);

const ChartCard = ({ title, data, type = 'bar', darkMode }) => (
  <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
    <h3 className="text-lg font-semibold mb-4">{title}</h3>
    {type === 'pie' ? <Pie data={data} /> : <Bar data={data} />}
  </div>
);

const TableHeader = ({ title }) => (
  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
    {title}
  </th>
);

export default DashboardMinistere;
