import React, { createContext, useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const login = async ({ token, role, ...restUser }) => {
    // Stocker token en localStorage/sessionStorage ou state
    localStorage.setItem('token', token);
    // Stocker user
    setUser({ role, ...restUser });

    // Redirection selon rôle
    if (['ministere', 'admin', 'ministerepublique'].includes(role)) {
      navigate('/ministere/home', { replace: true });
    } else if (role === 'entreprise') {
      navigate('/entreprise/home', { replace: true });
    } else {
      navigate('/', { replace: true });
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    navigate('/login', { replace: true });
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
