import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  FaHome, FaUsers, FaFileAlt, FaSignOutAlt, 
  FaChartPie, FaPlus, FaEdit, FaTrash, FaDownload
} from 'react-icons/fa';
import { Line, Pie } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import { useAuth } from '../../context/AuthContext';
import LoadingSpinner from '../../components/LoadingSpinner';
import ConfirmationModal from '../../components/ConfirmationModal';
import OfferForm from '../../components/OfferForm';

Chart.register(...registerables);

const DashboardAdmin = () => {
  const [dashboard, setDashboard] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [modal, setModal] = useState({ show: false, action: null, data: null });
  const [selectedOffer, setSelectedOffer] = useState(null);

  const navigate = useNavigate();
  const { logout } = useAuth();

  const fetchDashboard = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/login');

    try {
      setLoading(true);
      const response = await fetch('http://localhost:3000/api/dashboard/admin', {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (!response.ok) throw new Error('Erreur lors du chargement des données');
      
      const data = await response.json();
      setDashboard(data);
    } catch (err) {
      setError(err.message);
      if (err.response?.status === 401) logout();
    } finally {
      setLoading(false);
    }
  }, [navigate, logout]);

  useEffect(() => {
    fetchDashboard();
  }, [fetchDashboard]);

  const handleDelete = async () => {
    if (!modal.data) return;
    
    try {
      const token = localStorage.getItem('token');
      const endpoint = modal.action === 'user' 
        ? `users/${modal.data.username}` 
        : `offers/${modal.data.id}`;

      const response = await fetch(`http://localhost:3000/api/admin/${endpoint}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });

      if (!response.ok) throw new Error('Erreur lors de la suppression');
      
      const data = await response.json();
      alert(data.message || "Opération réussie");
      fetchDashboard();
    } catch (err) {
      alert(err.message || "Erreur lors de la suppression");
    } finally {
      setModal({ show: false, action: null, data: null });
    }
  };

  const handleOfferSubmit = async (offerData) => {
    try {
      const token = localStorage.getItem('token');
      const method = selectedOffer ? 'PUT' : 'POST';
      const url = selectedOffer 
        ? `http://localhost:3000/api/admin/offers/${selectedOffer.id}`
        : 'http://localhost:3000/api/admin/offers';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(offerData)
      });

      if (!response.ok) throw new Error('Erreur lors de la sauvegarde');
      
      const data = await response.json();
      alert(data.message || "Opération réussie");
      setShowCreateForm(false);
      setSelectedOffer(null);
      fetchDashboard();
    } catch (err) {
      alert(err.message || "Erreur lors de la sauvegarde");
    }
  };

  if (loading) return <LoadingSpinner fullScreen />;
  if (error) return <ErrorPage message={error} />;
  if (!dashboard) return null;

  // Préparation des données pour les graphiques
  const chartData = {
    line: {
      labels: Object.keys(dashboard.statsByMonth || {}).sort(),
      datasets: [{
        label: 'Offres créées',
        data: Object.values(dashboard.statsByMonth || {}),
        borderColor: '#6366F1',
        backgroundColor: '#E0E7FF',
        borderWidth: 2,
        fill: true
      }]
    },
    pie: {
      labels: ['Entreprises', 'Ministères', 'Admins'],
      datasets: [{
        data: [
          dashboard.stats.entreprises, 
          dashboard.stats.ministeres, 
          dashboard.stats.admins
        ],
        backgroundColor: ['#3B82F6', '#10B981', '#8B5CF6']
      }]
    }
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: { 
      legend: { position: 'bottom' },
      tooltip: { enabled: true }
    },
    animation: { duration: 500 }
  };

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Sidebar */}
      <Sidebar 
        onNavigate={navigate}
        onShowCreate={() => {
          setSelectedOffer(null);
          setShowCreateForm(true);
        }}
        onLogout={logout}
      />

      {/* Main Content */}
      <div className="flex-1 ml-64 p-6">
        <h1 className="text-3xl font-bold mb-6 text-gray-800">Tableau de Bord Administrateur</h1>

        {/* Create/Edit Offer Form */}
        {showCreateForm && (
          <OfferForm 
            offer={selectedOffer}
            onSubmit={handleOfferSubmit}
            onCancel={() => {
              setShowCreateForm(false);
              setSelectedOffer(null);
            }}
          />
        )}

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <StatCard 
            label="Appels d'Offres" 
            value={dashboard.stats.offers} 
            icon={<FaFileAlt />} 
            color="indigo" 
          />
          <StatCard 
            label="Candidatures" 
            value={dashboard.stats.candidatures} 
            icon={<FaUsers />} 
            color="green" 
          />
          <StatCard 
            label="Utilisateurs" 
            value={dashboard.stats.users} 
            icon={<FaUsers />} 
            color="yellow" 
          />
        </div>

        {/* Charts Section */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <ChartContainer title="Statistiques par mois">
            <Line data={chartData.line} options={chartOptions} />
          </ChartContainer>
          <ChartContainer title="Répartition par rôle">
            <Pie data={chartData.pie} options={chartOptions} />
          </ChartContainer>
        </div>

        {/* Users Section */}
        <DataSection 
          id="users-section"
          title="Utilisateurs"
          data={dashboard.users}
          emptyMessage="Aucun utilisateur trouvé"
          columns={[
            { header: 'Nom', accessor: 'username' },
            { header: 'Email', accessor: 'email' },
            { 
              header: 'Rôle', 
              render: (user) => <span className="capitalize">{user.role}</span> 
            },
            {
              header: 'Actions',
              render: (user) => (
                <button 
                  onClick={() => setModal({
                    show: true,
                    action: 'user',
                    data: user,
                    message: `Supprimer l'utilisateur ${user.username} ?`
                  })}
                  className="btn-danger"
                >
                  <FaTrash />
                </button>
              )
            }
          ]}
        />

        {/* Offers Section */}
        <DataSection 
          id="offers-section"
          title="Gestion des Offres"
          data={dashboard.offers}
          emptyMessage="Aucune offre disponible"
          columns={[
            { header: 'Titre', accessor: 'title' },
            { 
              header: 'Date', 
              render: (offer) => new Date(offer.createdAt).toLocaleDateString() 
            },
            { 
              header: 'Statut', 
              render: (offer) => (
                <StatusBadge status={offer.status} />
              )
            },
            {
              header: 'Actions',
              render: (offer) => (
                <div className="flex gap-2">
                  <button 
                    onClick={() => {
                      setSelectedOffer(offer);
                      setShowCreateForm(true);
                    }}
                    className="btn-warning"
                  >
                    <FaEdit />
                  </button>
                  <button 
                    onClick={() => setModal({
                      show: true,
                      action: 'offer',
                      data: offer,
                      message: `Supprimer l'offre ${offer.title} ?`
                    })}
                    className="btn-danger"
                  >
                    <FaTrash />
                  </button>
                </div>
              )
            }
          ]}
        />

        {/* Applications Section */}
        <DataSection 
          id="applications-section"
          title="Candidatures reçues"
          data={dashboard.candidatures}
          emptyMessage="Aucune candidature reçue"
          columns={[
            { header: 'Entreprise', accessor: 'entreprise' },
            { header: 'Offre', accessor: 'offerId' },
            {
              header: 'Fichier',
              render: (c) => c.fileUrl ? (
                <a 
                  href={`http://localhost:3000${c.fileUrl}`} 
                  target="_blank" 
                  rel="noreferrer"
                  className="btn-primary"
                >
                  <FaDownload />
                </a>
              ) : 'Aucun fichier'
            }
          ]}
        />
      </div>

      {/* Confirmation Modal */}
      <ConfirmationModal 
        isOpen={modal.show}
        message={modal.message}
        onConfirm={handleDelete}
        onCancel={() => setModal({ show: false, action: null, data: null })}
      />
    </div>
  );
};

// Composants séparés pour une meilleure lisibilité
const Sidebar = ({ onNavigate, onShowCreate, onLogout }) => (
  <div className="w-64 bg-gray-800 text-white py-8 px-4 fixed h-full">
    <h2 className="text-2xl font-bold text-center mb-8">Admin Panel</h2>
    <nav className="space-y-2">
      <SidebarButton icon={<FaHome />} label="Accueil" onClick={() => onNavigate('/')} />
      <SidebarButton icon={<FaChartPie />} label="Statistiques" onClick={() => window.scrollTo(0, 0)} />
      <SidebarButton icon={<FaUsers />} label="Utilisateurs" onClick={() => document.getElementById('users-section')?.scrollIntoView()} />
      <SidebarButton icon={<FaFileAlt />} label="Offres" onClick={() => document.getElementById('offers-section')?.scrollIntoView()} />
      <SidebarButton icon={<FaPlus />} label="Créer Offre" onClick={onShowCreate} />
      <div className="pt-4 mt-4 border-t border-gray-700">
        <SidebarButton 
          icon={<FaSignOutAlt />} 
          label="Déconnexion" 
          onClick={onLogout} 
          isLogout 
        />
      </div>
    </nav>
  </div>
);

const SidebarButton = ({ icon, label, onClick, isLogout = false }) => (
  <button
    onClick={onClick}
    className={`flex items-center w-full px-4 py-3 rounded-md transition-colors ${
      isLogout 
        ? 'text-red-400 hover:bg-red-900/10' 
        : 'text-gray-300 hover:bg-gray-700 hover:text-white'
    }`}
  >
    <span className="mr-3">{icon}</span>
    <span>{label}</span>
  </button>
);

const StatCard = ({ label, value, icon, color }) => {
  const colorClasses = {
    indigo: 'bg-indigo-100 text-indigo-600',
    green: 'bg-green-100 text-green-600',
    yellow: 'bg-yellow-100 text-yellow-600',
    red: 'bg-red-100 text-red-600'
  };

  return (
    <div className={`${colorClasses[color]} p-6 rounded-lg shadow flex justify-between items-center transition-transform hover:scale-105`}>
      <div>
        <p className="text-sm font-medium">{label}</p>
        <p className="text-2xl font-bold">{value}</p>
      </div>
      <div className="text-3xl opacity-80">{icon}</div>
    </div>
  );
};

const ChartContainer = ({ title, children }) => (
  <div className="bg-white p-6 rounded shadow">
    <h2 className="text-xl font-bold mb-4">{title}</h2>
    <div className="h-80">{children}</div>
  </div>
);

const DataSection = ({ id, title, data, emptyMessage, columns }) => (
  <section id={id} className="bg-white rounded shadow mb-8">
    <div className="p-6 border-b">
      <h2 className="text-2xl font-bold">{title}</h2>
    </div>
    <div className="overflow-x-auto">
      {data.length === 0 ? (
        <p className="p-6 text-gray-500">{emptyMessage}</p>
      ) : (
        <table className="min-w-full divide-y">
          <thead className="bg-gray-50">
            <tr>
              {columns.map((col, index) => (
                <th key={index} className="px-6 py-3 text-left text-sm font-medium text-gray-500 uppercase">
                  {col.header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="divide-y">
            {data.map((item, index) => (
              <tr key={index} className="hover:bg-gray-50">
                {columns.map((col, colIndex) => (
                  <td key={colIndex} className="px-6 py-4 whitespace-nowrap">
                    {col.render ? col.render(item) : item[col.accessor]}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  </section>
);

const StatusBadge = ({ status }) => {
  const statusClasses = {
    approved: 'bg-green-100 text-green-800',
    pending: 'bg-yellow-100 text-yellow-800',
    rejected: 'bg-red-100 text-red-800'
  };

  const statusText = {
    approved: 'Approuvé',
    pending: 'En attente',
    rejected: 'Rejeté'
  };

  return (
    <span className={`px-2 py-1 rounded-full text-xs ${statusClasses[status]}`}>
      {statusText[status]}
    </span>
  );
};

export default DashboardAdmin;
