import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaFileAlt } from 'react-icons/fa';
import { motion } from 'framer-motion';

const rowVariants = {
  hidden: { opacity: 0, y: 10 },
  visible: i => ({
    opacity: 1,
    y: 0,
    transition: { delay: i * 0.1 }
  }),
};

const GestionCandidatures = () => {
  const [candidatures, setCandidatures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCandidatures = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/candidatures', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setCandidatures(res.data);
      } catch (err) {
        setError('Erreur lors du chargement des candidatures');
      } finally {
        setLoading(false);
      }
    };
    fetchCandidatures();
  }, []);

  if (loading) return <p className="text-center mt-10 text-xl animate-pulse">Chargement des candidatures...</p>;
  if (error) return <p className="text-center mt-10 text-red-600 font-semibold">{error}</p>;

  return (
    <div className="p-6 min-h-screen bg-gradient-to-r from-indigo-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800 text-gray-900 dark:text-gray-100">
      <h1 className="text-4xl font-extrabold mb-8 text-center text-indigo-700 dark:text-indigo-300 drop-shadow-lg">
        Gestion des Candidatures
      </h1>

      {candidatures.length === 0 ? (
        <p className="text-center text-gray-600 dark:text-gray-400 text-lg">Aucune candidature reçue pour le moment.</p>
      ) : (
        <div className="overflow-x-auto rounded-lg shadow-lg bg-white dark:bg-gray-900">
          <table className="min-w-full border-collapse border border-gray-200 dark:border-gray-700">
            <thead>
              <tr className="bg-indigo-100 dark:bg-indigo-900 text-indigo-800 dark:text-indigo-300 font-semibold">
                <th className="p-4 border border-indigo-200 dark:border-indigo-700">Entreprise</th>
                <th className="p-4 border border-indigo-200 dark:border-indigo-700">Offre</th>
                <th className="p-4 border border-indigo-200 dark:border-indigo-700">Candidat</th>
                <th className="p-4 border border-indigo-200 dark:border-indigo-700">Email</th>
                <th className="p-4 border border-indigo-200 dark:border-indigo-700">Date</th>
                <th className="p-4 border border-indigo-200 dark:border-indigo-700">Documents</th>
              </tr>
            </thead>
            <tbody>
              {candidatures.map((c, index) => (
                <motion.tr
                  key={c.id}
                  custom={index}
                  initial="hidden"
                  animate="visible"
                  variants={rowVariants}
                  className="border-t border-indigo-200 dark:border-indigo-700 hover:bg-indigo-50 dark:hover:bg-indigo-800 transition-colors cursor-pointer"
                >
                  <td className="p-4 border border-indigo-200 dark:border-indigo-700">{c.entrepriseName || c.companyName || '—'}</td>
                  <td className="p-4 border border-indigo-200 dark:border-indigo-700">{c.offreTitre || c.jobTitle || '—'}</td>
                  <td className="p-4 border border-indigo-200 dark:border-indigo-700">{c.candidatNom || c.candidateName || '—'}</td>
                  <td className="p-4 border border-indigo-200 dark:border-indigo-700 break-all">{c.email}</td>
                  <td className="p-4 border border-indigo-200 dark:border-indigo-700">{new Date(c.dateCandidature || c.createdAt).toLocaleDateString()}</td>
                  <td className="p-4 border border-indigo-200 dark:border-indigo-700 space-x-2">
                    {c.documents && c.documents.length > 0 ? (
                      c.documents.map((doc, i) => (
                        <a
                          key={i}
                          href={doc.url}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="inline-block px-2 py-1 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition"
                          title={`Document ${i + 1}`}
                        >
                          <FaFileAlt />
                        </a>
                      ))
                    ) : (
                      <span className="italic text-gray-400">Aucun document</span>
                    )}
                  </td>
                </motion.tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default GestionCandidatures;
