import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  FaCheckCircle,
  FaTimesCircle,
  FaChartBar,
  FaSpinner,
  FaSignOutAlt,
  FaSearch,
  FaFilter
} from 'react-icons/fa';

const DashboardMinistere = () => {
  const navigate = useNavigate();
  const [recentOffers, setRecentOffers] = useState([]); // Offres récentes (dashboard)
  const [offresEnAttente, setOffresEnAttente] = useState([]); // Offres en attente
  const [offresPubliees, setOffresPubliees] = useState([]); // Offres validées (publiées)
  const [stats, setStats] = useState({ total: 0, actifs: 0, rejetees: 0 });
  const [loading, setLoading] = useState({
    dashboard: false,
    pending: false,
    published: false,
    actions: {}
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    statut: 'tous'
  });

  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchDashboard();
    fetchOffresEnAttente();
    fetchOffresPubliees();
    // eslint-disable-next-line
  }, []);

  // Récupère stats et dernières offres (récentes)
  const fetchDashboard = async () => {
    setLoading(prev => ({ ...prev, dashboard: true }));
    try {
      const res = await axios.get('http://localhost:3000/api/dashboard/ministere', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRecentOffers(res.data.recentOffers || []);
      setStats(res.data.stats || { total: 0, actifs: 0, rejetees: 0 });
    } catch (err) {
      console.error('Erreur lors du chargement des données', err);
    } finally {
      setLoading(prev => ({ ...prev, dashboard: false }));
    }
  };

  // Offres en attente de validation
  const fetchOffresEnAttente = async () => {
    setLoading(prev => ({ ...prev, pending: true }));
    try {
      const res = await axios.get('http://localhost:3000/api/offres/en-attente', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOffresEnAttente(res.data || []);
    } catch (err) {
      console.error('Erreur lors du chargement des offres en attente', err);
    } finally {
      setLoading(prev => ({ ...prev, pending: false }));
    }
  };

  // Offres validées publiées
  const fetchOffresPubliees = async () => {
    setLoading(prev => ({ ...prev, published: true }));
    try {
      const res = await axios.get('http://localhost:3000/api/offres/publiees', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOffresPubliees(res.data || []);
    } catch (err) {
      console.error('Erreur lors du chargement des offres publiées', err);
    } finally {
      setLoading(prev => ({ ...prev, published: false }));
    }
  };

  // Valider une offre
  const validerOffre = async (id) => {
    setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: 'validation' } }));
    try {
      await axios.put(`http://localhost:3000/api/ministere/offres/${id}/valider`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      await Promise.all([fetchDashboard(), fetchOffresEnAttente(), fetchOffresPubliees()]);
    } catch (err) {
      console.error('Erreur lors de la validation', err);
    } finally {
      setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: false } }));
    }
  };

  // Rejeter une offre (penser à implémenter la route côté serveur si manquante)
  const rejeterOffre = async (id) => {
    setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: 'rejet' } }));
    try {
      await axios.post(`http://localhost:3000/api/ministere/rejeter/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      await Promise.all([fetchDashboard(), fetchOffresEnAttente(), fetchOffresPubliees()]);
    } catch (err) {
      console.error('Erreur lors du rejet', err);
    } finally {
      setLoading(prev => ({ ...prev, actions: { ...prev.actions, [id]: false } }));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };
  
  // Filtrer les offres selon recherche + filtre statut
  const filterByStatut = (offer) => {
    if (filters.statut === 'tous') return true;
    if (filters.statut === 'actives') return offer.status === 'valide';
    if (filters.statut === 'rejetees') return offer.status === 'rejected' || offer.status === 'rejetée'; // tolérance orthographe
    return true;
  };

  const filteredRecentOffers = recentOffers.filter(offer => {
    const searchMatch =
      offer.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      offer.description.toLowerCase().includes(searchTerm.toLowerCase());
    return searchMatch && filterByStatut(offer);
  });

  const filteredPendingOffers = offresEnAttente.filter(offer => {
    return (
      offer.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      offer.description.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  const filteredPublishedOffers = offresPubliees.filter(offer => {
    return (
      (offer.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      offer.description.toLowerCase().includes(searchTerm.toLowerCase()))
      && filterByStatut(offer)
    );
  });

  return (
    <div className="min-h-screen p-4 md:p-10 bg-gradient-to-r from-blue-100 via-blue-200 to-blue-300 text-blue-900">
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-2xl md:text-4xl font-bold">Espace Ministère</h1>
        <button
          onClick={handleLogout}
          className="flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
        >
          <FaSignOutAlt /> Déconnexion
        </button>
      </header>

      <div className="mb-8 bg-white bg-opacity-80 p-4 rounded-xl shadow">
        <div className="flex flex-col md:flex-row gap-4 mb-4">
          <div className="relative flex-grow">
            <FaSearch className="absolute left-3 top-3 text-blue-400" />
            <input
              type="text"
              placeholder="Rechercher une offre..."
              className="w-full pl-10 pr-4 py-2 bg-white rounded-lg text-blue-900 placeholder-blue-400 border border-blue-200"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <div className="flex gap-2">
            <select
              className="bg-white text-blue-900 px-3 py-2 rounded-lg border border-blue-200"
              value={filters.statut}
              onChange={(e) => setFilters({ ...filters, statut: e.target.value })}
            >
              <option value="tous">Tous les statuts</option>
              <option value="actives">Offres validées</option>
              <option value="rejetees">Offres rejetées</option>
            </select>
            <button className="flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-lg">
              <FaFilter /> Filtrer
            </button>
          </div>
        </div>
      </div>

      {loading.dashboard ? (
        <div className="flex justify-center items-center h-40">
          <FaSpinner className="animate-spin text-3xl text-blue-400" />
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
          <StatBox icon={<FaChartBar size={30} />} title="Offres totales" value={stats.total} />
          <StatBox icon={<FaCheckCircle size={30} />} title="Offres validées" value={stats.actifs} />
          <StatBox icon={<FaTimesCircle size={30} />} title="Offres rejetées" value={stats.rejetees} />
        </div>
      )}

      <section className="mb-10 bg-white bg-opacity-80 p-6 rounded-xl shadow">
        <h2 className="text-2xl font-semibold mb-4">Offres en Attente de Validation</h2>
        {loading.pending ? (
          <div className="flex justify-center items-center h-40">
            <FaSpinner className="animate-spin text-3xl text-blue-400" />
          </div>
        ) : filteredPendingOffers.length === 0 ? (
          <p className="text-blue-500 italic">Aucune offre en attente de validation</p>
        ) : (
          <div className="space-y-4">
            {filteredPendingOffers.map(offer => (
              <OfferCard
                key={offer.id}
                offer={offer}
                onValidate={() => validerOffre(offer.id)}
                onReject={() => rejeterOffre(offer.id)}
                loading={loading.actions[offer.id]}
                isPending={true}
              />
            ))}
          </div>
        )}
      </section>

      <section className="bg-white bg-opacity-80 p-6 rounded-xl shadow mb-10">
        <h2 className="text-2xl font-semibold mb-4">Offres Publiées</h2>
        {loading.published ? (
          <div className="flex justify-center items-center h-40">
            <FaSpinner className="animate-spin text-3xl text-blue-400" />
          </div>
        ) : filteredPublishedOffers.length === 0 ? (
          <p className="text-blue-500 italic">Aucune offre publiée pour le moment.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {filteredPublishedOffers.map(offer => (
              <OfferCard
                key={offer.id}
                offer={offer}
                isPublished={true}
              />
            ))}
          </div>
        )}
      </section>

      <section className="bg-white bg-opacity-80 p-6 rounded-xl shadow">
        <h2 className="text-2xl font-semibold mb-4">Dernières Offres à traiter</h2>
        {loading.dashboard ? (
          <div className="flex justify-center items-center h-40">
            <FaSpinner className="animate-spin text-3xl text-blue-400" />
          </div>
        ) : filteredRecentOffers.length === 0 ? (
          <p className="text-blue-500 italic">Aucune offre récente à afficher</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {filteredRecentOffers.map(offer => (
              <OfferCard
                key={offer.id}
                offer={offer}
                onValidate={() => validerOffre(offer.id)}
                onReject={() => rejeterOffre(offer.id)}
                loading={loading.actions[offer.id]}
              />
            ))}
          </div>
        )}
      </section>

      <footer className="mt-16 text-center text-sm text-blue-700 italic">
        Statistiques générées automatiquement – Ministère des Finances © {new Date().getFullYear()}
      </footer>
    </div>
  );
};

const StatBox = ({ icon, title, value }) => (
  <div className="bg-white bg-opacity-80 p-6 rounded-xl shadow-md text-center hover:bg-opacity-90 transition-colors">
    <div className="text-blue-400 mb-2">{icon}</div>
    <h3 className="text-lg font-semibold">{title}</h3>
    <p className="text-2xl font-bold">{value}</p>
  </div>
);

const OfferCard = ({ offer, onValidate, onReject, loading, isPending = false, isPublished = false }) => (
  <div className={`bg-white bg-opacity-80 p-4 rounded-xl shadow-md hover:bg-opacity-90 transition-colors ${isPending ? 'border-l-4 border-yellow-400' : ''}`}>
    <h3 className="text-lg font-bold text-blue-900">{offer.title}</h3>
    <p className="text-sm mb-2 text-blue-700">{offer.description}</p>
    <div className="flex flex-wrap justify-between items-center mt-4 gap-2">
      <div className="space-y-1">
        <p className="text-xs text-blue-500">Date limite : {new Date(offer.deadline).toLocaleDateString()}</p>
        {offer.status && (
          <span className={`text-xs px-2 py-1 rounded-full ${
            offer.status === 'valide' ? 'bg-green-100 text-green-700' :
            (offer.status === 'rejected' || offer.status === 'rejetée') ? 'bg-red-100 text-red-700' :
            'bg-yellow-100 text-yellow-700'
          }`}>
            {offer.status === 'valide' ? 'Validée' :
              (offer.status === 'rejected' || offer.status === 'rejetée') ? 'Rejetée' : 'En attente'}
          </span>
        )}
      </div>
      {!isPublished && (
        <div className="flex gap-2">
          {onValidate && (
            <button
              onClick={(e) => {
                e.stopPropagation();
                onValidate();
              }}
              className="flex items-center gap-1 bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm disabled:opacity-50"
              disabled={loading === 'validation'}
            >
              {loading === 'validation' ? <FaSpinner className="animate-spin" /> : <FaCheckCircle />}
              Valider
            </button>
          )}
          {onReject && (
            <button
              onClick={(e) => {
                e.stopPropagation();
                onReject();
              }}
              className="flex items-center gap-1 bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm disabled:opacity-50"
              disabled={loading === 'rejet'}
            >
              {loading === 'rejet' ? <FaSpinner className="animate-spin" /> : <FaTimesCircle />}
              Rejeter
            </button>
          )}
        </div>
      )}
    </div>
  </div>
);

export default DashboardMinistere;
