import React from 'react';
import { FaBullseye, FaFileAlt, FaUsers, FaMobileAlt } from 'react-icons/fa';

const Home = () => {
  return (
    <div className="min-h-screen bg-gradient-to-r from-indigo-900 via-purple-900 to-pink-900 text-white flex flex-col">
      {/* Header */}
      <header className="p-8 text-center">
        <h1 className="text-5xl font-extrabold mb-4 animate-fadeInDown">
          Plateforme de Transparence des Marchés Publics
        </h1>
        <p className="text-xl max-w-3xl mx-auto animate-fadeInUp">
          Garantir la transparence, la confiance et l'efficacité dans la gestion des marchés publics.
        </p>
      </header>

      {/* Section objectifs */}
      <section className="flex flex-col md:flex-row justify-around mt-16 px-6 md:px-20 gap-12">
        <Objective
          icon={<FaBullseye size={48} />}
          title="Objectif Clair"
          description="Promouvoir la transparence et la confiance entre les citoyens et les institutions publiques."
        />
        <Objective
          icon={<FaFileAlt size={48} />}
          title="Appels d'Offres"
          description="Accès facile et complet aux appels d’offres publiés par les ministères."
        />
        <Objective
          icon={<FaUsers size={48} />}
          title="Participation"
          description="Offrir aux entreprises une interface simple pour soumettre leurs candidatures."
        />
        <Objective
          icon={<FaMobileAlt size={48} />}
          title="Accessibilité"
          description="Interface responsive adaptée à tous les écrans, même les smartphones."
        />
      </section>

      {/* Footer */}
      <footer className="mt-auto p-6 text-center text-gray-300 text-sm">
        © 2025 Plateforme des Marchés Publics - Tous droits réservés
      </footer>

      {/* Animations CSS */}
      <style>{`
        @keyframes fadeInDown {
          0% {opacity: 0; transform: translateY(-30px);}
          100% {opacity: 1; transform: translateY(0);}
        }
        @keyframes fadeInUp {
          0% {opacity: 0; transform: translateY(30px);}
          100% {opacity: 1; transform: translateY(0);}
        }
        .animate-fadeInDown {
          animation: fadeInDown 1s ease forwards;
        }
        .animate-fadeInUp {
          animation: fadeInUp 1.2s ease forwards;
        }
      `}</style>
    </div>
  );
};

const Objective = ({ icon, title, description }) => (
  <div className="bg-white bg-opacity-10 rounded-2xl p-6 flex flex-col items-center text-center max-w-xs hover:scale-105 transition-transform duration-300 shadow-lg">
    <div className="mb-4 text-indigo-300">{icon}</div>
    <h3 className="text-xl font-semibold mb-2">{title}</h3>
    <p className="text-gray-300">{description}</p>
  </div>
);

export default Home;
