import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  FaHome, FaUsers, FaFileAlt, FaSignOutAlt, FaChartPie, FaPlus
} from 'react-icons/fa';
import { Line, Pie } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';

Chart.register(...registerables);

const DashboardAdmin = () => {
  const [dashboard, setDashboard] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newOffer, setNewOffer] = useState({ title: '', description: '', deadline: '' });
  const [createError, setCreateError] = useState('');

  const navigate = useNavigate();

  const fetchDashboard = () => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/');

    setLoading(true);
    fetch('http://localhost:3000/api/dashboard/admin', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        if (!res.ok) throw new Error('Erreur lors du chargement des données');
        return res.json();
      })
      .then(data => {
        setDashboard(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchDashboard();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const handleDeleteUser = (username) => {
    if (!window.confirm(`Supprimer ${username} ?`)) return;
    const token = localStorage.getItem('token');
    fetch(`http://localhost:3000/api/users/${username}`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        alert(data.message || "Utilisateur supprimé");
        fetchDashboard();
      });
  };

  const handleCreateOffer = () => {
  setCreateError('');
  if (!newOffer.title || !newOffer.description || !newOffer.deadline) {
    setCreateError("Tous les champs sont obligatoires");
    return;
  }
  //candidature list
  

  const token = localStorage.getItem('token');
  fetch('http://localhost:3000/api/admin/offres', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${token}`
    },
    body: JSON.stringify({
      ...newOffer,
      status: 'en_attente' 
    })
  })
    .then(async res => {
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Erreur lors de la création de l\'offre');
      alert(data.message || "Offre créée");
      setShowCreateForm(false);
      setNewOffer({ title: '', description: '', deadline: '' });
      fetchDashboard();
    })
    .catch(err => setCreateError(err.message));
};


  const handleDeleteOffer = (offerId) => {
    if (!window.confirm("Supprimer cette offre ?")) return;
    const token = localStorage.getItem('token');
    fetch(`http://localhost:3000/api/admin/offres/${offerId}`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        alert(data.message || "Offre supprimée");
        fetchDashboard();
      });
  };

  if (loading) return <div className="flex items-center justify-center h-screen"><div className="animate-spin h-12 w-12 border-t-2 border-b-2 border-indigo-500 rounded-full"></div></div>;
  if (error) return <div className="text-red-600 text-center mt-10">{error}</div>;
  if (!dashboard) return null;

  const monthsLabels = Object.keys(dashboard.statsByMonth || {}).sort();
  const offersCountByMonth = monthsLabels.map(m => dashboard.statsByMonth[m]);

  const lineChartData = {
    labels: monthsLabels,
    datasets: [{
      label: 'Offres créées',
      data: offersCountByMonth,
      borderColor: '#6366F1',
      backgroundColor: '#E0E7FF',
      borderWidth: 2,
      fill: true
    }]
  };

  const pieChartData = {
    labels: ['Entreprises', 'Ministères', 'Admins'],
    datasets: [{
      data: [dashboard.stats.entreprises, dashboard.stats.ministeres, 1],
      backgroundColor: ['#3B82F6', '#10B981', '#8B5CF6']
    }]
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: { legend: { position: 'bottom' } },
    animation: { duration: 0 }
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      <div className="w-64 bg-gray-800 text-white py-8 px-4 fixed h-full">
        <h2 className="text-2xl font-bold text-center mb-8">Admin Panel</h2>
        <nav className="space-y-2">
          <SidebarButton icon={<FaHome />} label="Accueil" onClick={() => navigate('/')} />
          <SidebarButton icon={<FaChartPie />} label="Statistiques" onClick={() => window.scrollTo(0, 0)} />
          <SidebarButton icon={<FaUsers />} label="Utilisateurs" onClick={() => document.getElementById('users-section')?.scrollIntoView()} />
          <SidebarButton icon={<FaFileAlt />} label="Offres" onClick={() => document.getElementById('offers-section')?.scrollIntoView()} />
          <SidebarButton icon={<FaPlus />} label="Créer Offre" onClick={() => setShowCreateForm(!showCreateForm)} />
          <div className="pt-4 mt-4 border-t border-gray-700">
            <SidebarButton icon={<FaSignOutAlt />} label="Déconnexion" onClick={handleLogout} isLogout />
          </div>
        </nav>
      </div>

      <div className="flex-1 ml-64 p-6">
        <h1 className="text-3xl font-bold mb-6 text-gray-800">Tableau de Bord Administrateur</h1>

        {showCreateForm && (
          <div className="bg-white p-6 rounded shadow mb-6">
            <h2 className="text-xl font-bold mb-4">Créer une Nouvelle Offre</h2>
            <input
              type="text"
              placeholder="Titre"
              value={newOffer.title}
              onChange={e => setNewOffer({ ...newOffer, title: e.target.value })}
              className="border p-2 w-full mb-2"
            />
            <textarea 
              placeholder="Description"
              value={newOffer.description}
              onChange={e => setNewOffer({ ...newOffer, description: e.target.value })}
              className="border p-2 w-full mb-2"
            />
            <input
              type="date"
              value={newOffer.deadline}
              onChange={e => setNewOffer({ ...newOffer, deadline: e.target.value })}
              className="border p-2 w-full mb-2"
            />
            {createError && <p className="text-red-600 mb-2">{createError}</p>}
            <button onClick={handleCreateOffer} className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700">Publier l'offre</button>
          </div>
        )}

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <StatCard label="Appels d'Offres" value={dashboard.stats.offers} icon={<FaFileAlt />} color="indigo" />
          <StatCard label="Candidatures" value={dashboard.stats.candidatures} icon={<FaUsers />} color="green" />
          <StatCard label="Utilisateurs" value={dashboard.stats.users} icon={<FaUsers />} color="yellow" />
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-xl font-bold mb-4">Statistiques par mois</h2>
            <div className="h-80"><Line data={lineChartData} options={chartOptions} /></div>
          </div>
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-xl font-bold mb-4">Répartition par rôle</h2>
            <div className="h-80"><Pie data={pieChartData} options={chartOptions} /></div>
          </div>
        </div>

        <section id="users-section" className="bg-white rounded shadow mb-8">
          <div className="p-6 border-b">
            <h2 className="text-2xl font-bold">Utilisateurs</h2>
          </div>
          <div className="overflow-x-auto">
            {dashboard.users.length === 0 ? (
              <p className="p-6 text-gray-500">Aucun utilisateur trouvé.</p>
            ) : (
              <table className="min-w-full divide-y">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3">Nom</th>
                    <th className="px-6 py-3">Email</th>
                    <th className="px-6 py-3">Rôle</th>
                    <th className="px-6 py-3">Action</th>
                  </tr>
                </thead>
                <tbody className="divide-y">
                  {dashboard.users.map((user, index) => (
                    <tr key={index}>
                      <td className="px-6 py-4">{user.username}</td>
                      <td className="px-6 py-4">{user.email}</td>
                      <td className="px-6 py-4 capitalize">{user.role}</td>
                      <td className="px-6 py-4">
                        <button onClick={() => handleDeleteUser(user.username)} className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700">Supprimer</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </section>

        <section id="offers-section" className="bg-white rounded shadow mb-8">
          <div className="p-6 border-b">
            <h2 className="text-2xl font-bold">Gestion des Offres</h2>
          </div>
          <div>
            {dashboard.offers.length === 0 ? (
              <p className="p-6 text-gray-500">Aucune offre disponible.</p>
            ) : (
              dashboard.offers.map((offer, index) => (
                <div key={index} className="p-6 border-b hover:bg-gray-50 transition">
                  <h3 className="text-lg font-semibold text-indigo-600">{offer.title}</h3>
                  <p className="text-sm text-gray-500">Par {offer.createdBy} • {new Date(offer.createdAt).toLocaleDateString()}</p>
                  <p className="text-gray-700 mt-2">{offer.description.substring(0, 100)}...</p>
                  <div className="mt-3 flex gap-3">
                    <button className="bg-yellow-400 text-white px-3 py-1 rounded cursor-not-allowed opacity-50" title="Fonction modification à implémenter">Modifier</button>
                    <button onClick={() => handleDeleteOffer(offer.id)} className="bg-red-500 text-white px-3 py-1 rounded">Supprimer</button>
                  </div>
                </div>
              ))
            )}
          </div>
        </section>

        <section className="bg-white rounded shadow">
          <div className="p-6 border-b">
            <h2 className="text-2xl font-bold">Candidatures reçues</h2>
          </div>
          <div className="overflow-x-auto">
            {dashboard.candidatures.length === 0 ? (
              <p className="p-6 text-gray-500">Aucune candidature reçue.</p>
            ) : (
              <table className="min-w-full divide-y">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3">Entreprise</th>
                    <th className="px-6 py-3">Offre</th>
                    <th className="px-6 py-3">Fichier</th>
                  </tr>
                </thead>
                <tbody className="divide-y">
                  {dashboard.candidatures.map((c, index) => (
                    <tr key={index}>
                      <td className="px-6 py-4">{c.entreprise}</td>
                      <td className="px-6 py-4">{c.offerId}</td>
                      <td className="px-6 py-4">
                        {c.fileUrl ? (
                          <a href={`http://localhost:3000${c.fileUrl}`} target="_blank" rel="noreferrer" className="text-blue-600 underline">Télécharger</a>
                        ) : (
                          <span>Aucun fichier</span>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </section>
      </div>
    </div>
  );
};

const SidebarButton = ({ icon, label, onClick, isLogout = false }) => (
  <button onClick={onClick} className={`flex items-center w-full px-4 py-3 rounded-md ${isLogout ? 'text-red-400 hover:bg-red-900/10' : 'text-gray-300 hover:bg-gray-700 hover:text-white'}`}>
    <span className="mr-3">{icon}</span>
    <span>{label}</span>
  </button>
);

const StatCard = ({ label, value, icon, color }) => {
  const colors = {
    indigo: 'bg-indigo-100 text-indigo-600',
    green: 'bg-green-100 text-green-600',
    yellow: 'bg-yellow-100 text-yellow-600'
  };
  return (
    <div className={`${colors[color]} p-6 rounded-lg shadow flex justify-between items-center`}>
      <div>
        <p className="text-sm font-medium">{label}</p>
        <p className="text-2xl font-bold">{value}</p>
      </div>
      <div className="text-3xl opacity-80">{icon}</div>
    </div>
  );
};

export default DashboardAdmin;
