// src/Pages/DashboardAdmin.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  FaHome, FaUsers, FaBuilding, FaLandmark,
  FaFileAlt, FaSignOutAlt, FaChartPie
} from 'react-icons/fa';
import { Line, Pie } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';

Chart.register(...registerables);

const DashboardAdmin = () => {
  const [dashboard, setDashboard] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/');

    fetch('http://localhost:3000/api/dashboard/admin', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.error) setError(data.error);
        else setDashboard(data);
      })
      .catch(() => setError("Erreur lors du chargement de l'admin."));
  }, [navigate]);

  if (error) return <div className="text-red-500 p-4">{error}</div>;
  if (!dashboard) return <div className="text-center mt-10 text-gray-500">Chargement...</div>;

  // Données pour les graphes
  const lineChartData = {
    labels: ['Jan', 'Fév', 'Mar', 'Avr', 'Mai', 'Juin'],
    datasets: [
      {
        label: 'Offres créées',
        data: [2, 4, 7, 3, 8, 6],
        borderColor: '#6366F1',
        backgroundColor: '#E0E7FF',
        tension: 0.4
      }
    ]
  };

  const pieChartData = {
    labels: ['Entreprises', 'Ministères', 'Admins'],
    datasets: [
      {
        label: 'Répartition des rôles',
        data: [
          dashboard.stats.entreprises,
          dashboard.stats.ministeres,
          1
        ],
        backgroundColor: ['#3B82F6', '#10B981', '#8B5CF6'],
        borderWidth: 0
      }
    ]
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-800 text-white py-8 px-4 space-y-6">
        <h2 className="text-2xl font-bold text-center">Admin Panel</h2>
        <nav className="flex flex-col space-y-4 mt-10">
          <button onClick={() => navigate('/home')} className="flex items-center gap-2 hover:text-indigo-400">
            <FaHome /> <span>Accueil</span>
          </button>
          <button className="flex items-center gap-2 hover:text-indigo-400">
            <FaChartPie /> <span>Statistiques</span>
          </button>
          <button className="flex items-center gap-2 hover:text-indigo-400">
            <FaUsers /> <span>Utilisateurs</span>
          </button>
          <button className="flex items-center gap-2 hover:text-indigo-400">
            <FaFileAlt /> <span>Offres</span>
          </button>
          <button
            onClick={() => navigate('/logout')}
            className="flex items-center gap-2 text-red-400 mt-6 hover:text-red-600"
          >
            <FaSignOutAlt /> <span>Déconnexion</span>
          </button>
        </nav>
      </aside>

      {/* Contenu principal */}
      <main className="flex-1 p-6 overflow-auto">
        <h1 className="text-4xl font-bold mb-6 text-gray-800 animate-fade-in">
          Tableau de Bord Administrateur
        </h1>

        {/* Objectifs */}
        <div className="bg-white p-6 rounded-xl shadow-md mb-8">
          <h2 className="text-2xl font-bold mb-2">Objectifs</h2>
          <p className="text-gray-600">
            Faciliter la transparence des marchés publics, permettre aux ministères de publier
            les appels d’offres et aux entreprises de candidater efficacement.
          </p>
        </div>

        {/* Statistiques */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <StatCard label="Utilisateurs" value={dashboard.stats.users} icon={<FaUsers size={30} />} color="text-indigo-500" />
          <StatCard label="Entreprises" value={dashboard.stats.entreprises} icon={<FaBuilding size={30} />} color="text-green-500" />
          <StatCard label="Ministères" value={dashboard.stats.ministeres} icon={<FaLandmark size={30} />} color="text-yellow-500" />
        </div>

        {/* Graphiques */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <div className="bg-white p-6 rounded-xl shadow-md">
            <h2 className="text-xl font-semibold mb-4">Activité des offres</h2>
            <Line data={lineChartData} options={{ responsive: true, plugins: { legend: { position: 'top' } } }} />
          </div>
          <div className="bg-white p-6 rounded-xl shadow-md">
            <h2 className="text-xl font-semibold mb-4">Répartition des rôles</h2>
            <Pie data={pieChartData} options={{ responsive: true, maintainAspectRatio: false }} />
          </div>
        </div>

        {/* Offres récentes */}
        <div className="bg-white p-6 rounded-xl shadow-md mb-8">
          <h2 className="text-2xl font-bold mb-4">5 Derniers Appels d’Offres</h2>
          <ul className="space-y-2">
            {dashboard.offers.map((offer, index) => (
              <li key={index} className="border-b border-gray-200 pb-2">
                <p className="text-lg font-semibold text-indigo-600">{offer.title}</p>
                <p className="text-sm text-gray-500">
                  Créé par : {offer.createdBy} — {new Date(offer.createdAt).toLocaleDateString()}
                </p>
              </li>
            ))}
          </ul>
        </div>

        {/* Liste des utilisateurs */}
        <div className="bg-white p-6 rounded-xl shadow-md">
          <h2 className="text-2xl font-bold mb-4">Utilisateurs</h2>
          <table className="w-full table-auto text-left">
            <thead className="text-gray-600">
              <tr>
                <th className="py-2">Nom</th>
                <th className="py-2">Email</th>
                <th className="py-2">Rôle</th>
              </tr>
            </thead>
            <tbody>
              {dashboard.users.map((user, index) => (
                <tr key={index} className="border-t border-gray-200">
                  <td className="py-2">{user.username}</td>
                  <td className="py-2">{user.email}</td>
                  <td className="py-2 capitalize">{user.role}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
};

