// src/Pages/Dashboard/DashboardMinistere.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import { FaCheckCircle, FaTimesCircle, FaFileAlt, FaChartBar } from 'react-icons/fa';
import ConfirmationModal from '../../omponents/ConfirmationModal';

const DashboardMinistere = () => {
  const [offres, setOffres] = useState([]);
  const [selectedOffer, setSelectedOffer] = useState(null);
  const [modalType, setModalType] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    fetchOffres();
  }, []);

  const fetchOffres = async () => {
    try {
      const res = await axios.get('/api/offres?status=pending');
      setOffres(res.data);
    } catch (err) {
      console.error('Erreur lors de la récupération des offres :', err);
    }
  };

  const handleActionClick = (offer, type) => {
    setSelectedOffer(offer);
    setModalType(type);
    setIsModalOpen(true);
  };

  const handleConfirm = async () => {
    try {
      if (modalType === 'approve') {
        await axios.patch(`/api/offres/${selectedOffer.id}/valider`);
      } else if (modalType === 'reject') {
        await axios.patch(`/api/offres/${selectedOffer.id}/rejeter`);
      }
      setIsModalOpen(false);
      fetchOffres();
    } catch (err) {
      console.error("Erreur lors de l'action sur l'offre :", err);
    }
  };

  const handleCancel = () => {
    setIsModalOpen(false);
    setSelectedOffer(null);
    setModalType(null);
  };

  return (
    <div className="min-h-screen p-6 bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
      <h1 className="text-3xl font-bold mb-6">Dashboard Ministère</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {offres.map((offre) => (
          <motion.div
            key={offre.id}
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4 }}
            className="bg-white dark:bg-gray-800 shadow-md rounded-lg p-4"
          >
            <h2 className="text-xl font-semibold mb-2">{offre.titre}</h2>
            <p className="text-sm mb-4">{offre.description}</p>
            <div className="flex justify-between items-center">
              <button
                className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
                onClick={() => handleActionClick(offre, 'approve')}
              >
                <FaCheckCircle className="inline mr-2" /> Valider
              </button>
              <button
                className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                onClick={() => handleActionClick(offre, 'reject')}
              >
                <FaTimesCircle className="inline mr-2" /> Rejeter
              </button>
            </div>
          </motion.div>
        ))}
      </div>

      <ConfirmationModal
        isOpen={isModalOpen}
        message={`Voulez-vous vraiment ${modalType === 'approve' ? 'valider' : 'rejeter'} cette offre ?`}
        onConfirm={handleConfirm}
        onCancel={handleCancel}
      />
    </div>
  );
};

export default DashboardMinistere;
