import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaUser, FaGavel, FaUniversity, FaTrash } from 'react-icons/fa';

const DashboardAdmin = () => {
  const [stats, setStats] = useState({});
  const [users, setUsers] = useState([]);
  const [offers, setOffers] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchDashboard();
  }, []);

  const fetchDashboard = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/dashboard/admin', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setStats(res.data.stats);
      setUsers(res.data.users);
      setOffers(res.data.offers);
    } catch (err) {
      console.error('Erreur chargement dashboard admin:', err);
    }
  };

  const deleteUser = async (username) => {
    if (!window.confirm(`Supprimer ${username} ?`)) return;
    try {
      await axios.delete(`http://localhost:3000/api/users/${username}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter((u) => u.username !== username));
    } catch (err) {
      alert("Erreur suppression: " + err.response?.data?.error || err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold mb-6 text-indigo-800">Tableau de Bord - Administrateur</h1>

      {/* Statistiques */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <StatCard icon={<FaUser size={30} />} title="Utilisateurs" value={stats.users} />
        <StatCard icon={<FaGavel size={30} />} title="Offres totales" value={stats.offers} />
        <StatCard icon={<FaUniversity size={30} />} title="Ministères" value={stats.ministeres} />
      </div>

      {/* Liste des utilisateurs */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Liste des Utilisateurs</h2>
        <div className="overflow-auto bg-white rounded-lg shadow">
          <table className="min-w-full">
            <thead>
              <tr className="bg-indigo-100 text-gray-700">
                <th className="px-4 py-2">Nom d'utilisateur</th>
                <th className="px-4 py-2">Email</th>
                <th className="px-4 py-2">Rôle</th>
                <th className="px-4 py-2">Action</th>
              </tr>
            </thead>
            <tbody>
              {users.map((u, index) => (
                <tr key={index} className="text-center border-b">
                  <td className="px-4 py-2">{u.username}</td>
                  <td className="px-4 py-2">{u.email}</td>
                  <td className="px-4 py-2">{u.role}</td>
                  <td className="px-4 py-2">
                    {u.role !== 'admin' && (
                      <button
                        onClick={() => deleteUser(u.username)}
                        className="text-red-600 hover:text-red-800"
                      >
                        <FaTrash />
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* Dernières offres */}
      <section>
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Dernières Offres</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {offers.map((offer, index) => (
            <div key={index} className="bg-white shadow-md p-4 rounded-lg">
              <h3 className="text-lg font-bold text-indigo-700">{offer.title}</h3>
              <p className="text-gray-600 text-sm">{offer.description}</p>
              <p className="text-sm text-gray-400 mt-2">Créé le : {new Date(offer.createdAt).toLocaleDateString()}</p>
              <p className={`mt-1 text-sm font-medium ${
                offer.status === 'en_attente' ? 'text-yellow-500' :
                offer.status === 'active' ? 'text-green-600' : 'text-red-500'
              }`}>
                Statut : {offer.status}
              </p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

const StatCard = ({ icon, title, value }) => (
  <div className="bg-white p-6 rounded-lg shadow flex items-center space-x-4">
    <div className="text-indigo-600">{icon}</div>
    <div>
      <p className="text-gray-600">{title}</p>
      <h3 className="text-2xl font-bold">{value}</h3>
    </div>
  </div>
);

export default DashboardAdmin;
