// sr
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DashboardMinistere = () => {
  const [offres, setOffres] = useState([]);
  const token = localStorage.getItem('token');

  const fetchOffres = async () => {
    try {
      const res = await axios.get('http://localhost:3000/api/dashboard/ministere', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setOffres(res.data.recentOffers.filter(o => o.status === 'en_attente'));
    } catch (err) {
      console.error(err);
    }
  };

  const validerOffre = async (id) => {
    try {
      await axios.post(`http://localhost:3000/api/ministere/valider/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchOffres();
    } catch (err) {
      console.error(err);
    }
  };

  const rejeterOffre = async (id) => {
    try {
      await axios.post(`http://localhost:3000/api/ministere/rejeter/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchOffres();
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchOffres();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h2 className="text-2xl font-bold mb-6 text-center">Tableau de Bord Minist√®re</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {offres.map((offre) => (
          <div key={offre.id} className="bg-white rounded-xl p-4 shadow hover:shadow-lg">
            <h3 className="text-lg font-semibold">{offre.title}</h3>
            <p className="text-sm text-gray-600">{offre.description}</p>
            <p className="text-sm text-gray-500 mt-2">Date limite : {offre.deadline}</p>
            <div className="flex gap-4 mt-4">
              <button onClick={() => validerOffre(offre.id)} className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700">
                Valider
              </button>
              <button onClick={() => rejeterOffre(offre.id)} className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700">
                Rejeter
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardMinistere;
