import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const GestionOffres = () => {
  const navigate = useNavigate();
  const [offres, setOffres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchOffres = async () => {
    try {
      const token = localStorage.getItem('token');
      const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';
      
      const response = await axios.get(`${API_BASE_URL}/api/offres`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      setOffres(response.data.offres || response.data);
      setLoading(false);
    } catch (error) {
      console.error('Erreur r√©cup√©ration offres:', error);
      setError('Erreur lors du chargement des offres');
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOffres();
  }, []);

  const handleValidateOffer = async (offerId) => {
    try {
      const token = localStorage.getItem('token');
      const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';
      
      await axios.put(`${API_BASE_URL}/api/offres/${offerId}/validate`, {}, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Rafra√Æchir la liste
      fetchOffres();
      alert('Offre valid√©e avec succ√®s !');
    } catch (error) {
      console.error('Erreur validation:', error);
      alert('Erreur lors de la validation de l\'offre');
    }
  };

  const handleRejectOffer = async (offerId) => {
    try {
      const token = localStorage.getItem('token');
      const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';
      
      await axios.put(`${API_BASE_URL}/api/offres/${offerId}/reject`, {}, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Rafra√Æchir la liste
      fetchOffres();
      alert('Offre rejet√©e !');
    } catch (error) {
      console.error('Erreur rejet:', error);
      alert('Erreur lors du rejet de l\'offre');
    }
  };

  const handleDeleteOffer = async (offerId) => {
    if (window.confirm('√ätes-vous s√ªr de vouloir supprimer cette offre ?')) {
      try {
        const token = localStorage.getItem('token');
        const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';
        
        await axios.delete(`${API_BASE_URL}/api/offres/${offerId}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        // Rafra√Æchir la liste
        fetchOffres();
        alert('Offre supprim√©e !');
      } catch (error) {
        console.error('Erreur suppression:', error);
        alert('Erreur lors de la suppression de l\'offre');
      }
    }
  };

  const getStatusBadge = (status) => {
    const badges = {
      'en_attente': { bg: 'bg-yellow-100', text: 'text-yellow-800', label: '‚è≥ En attente' },
      'valide': { bg: 'bg-green-100', text: 'text-green-800', label: '‚úÖ Valid√©e' },
      'rejet√©e': { bg: 'bg-red-100', text: 'text-red-800', label: '‚ùå Rejet√©e' }
    };
    
    const badge = badges[status] || { bg: 'bg-gray-100', text: 'text-gray-800', label: status };
    
    return (
      <span className={`px-2 py-1 rounded-full text-xs font-medium ${badge.bg} ${badge.text}`}>
        {badge.label}
      </span>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        
        {/* Header */}
        <div className="mb-8">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">
                üìã Gestion des Offres
              </h1>
              <p className="text-gray-600 mt-2">
                G√©rez toutes les offres de march√©s publics
              </p>
            </div>
            <button
              onClick={() => navigate('/ministere/home')}
              className="px-4 py-2 bg-gray-500 hover:bg-gray-600 text-white rounded-lg"
            >
              ‚Üê Retour
            </button>
          </div>
        </div>

        {/* Statistiques */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white p-6 rounded-lg shadow">
            <div className="text-2xl font-bold text-blue-600">{offres.length}</div>
            <div className="text-sm text-gray-600">Total des offres</div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow">
            <div className="text-2xl font-bold text-yellow-600">
              {offres.filter(o => o.status === 'en_attente').length}
            </div>
            <div className="text-sm text-gray-600">En attente</div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow">
            <div className="text-2xl font-bold text-green-600">
              {offres.filter(o => o.status === 'valide').length}
            </div>
            <div className="text-sm text-gray-600">Valid√©es</div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow">
            <div className="text-2xl font-bold text-red-600">
              {offres.filter(o => o.status === 'rejet√©e').length}
            </div>
            <div className="text-sm text-gray-600">Rejet√©es</div>
          </div>
        </div>

        {/* Liste des offres */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
            <div className="text-red-800">{error}</div>
          </div>
        )}

        {offres.length === 0 ? (
          <div className="bg-white rounded-lg shadow p-8 text-center">
            <div className="text-gray-500 text-lg">Aucune offre trouv√©e</div>
          </div>
        ) : (
          <div className="bg-white rounded-lg shadow overflow-hidden">
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Offre
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Domaine
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Date Limite
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Statut
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {offres.map((offre) => (
                    <tr key={offre.id} className="hover:bg-gray-50">
                      <td className="px-6 py-4">
                        <div>
                          <div className="text-sm font-medium text-gray-900">
                            {offre.title}
                          </div>
                          <div className="text-sm text-gray-500 truncate max-w-xs">
                            {offre.description}
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                          {offre.domaine}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {new Date(offre.dateLimite).toLocaleDateString('fr-FR')}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {getStatusBadge(offre.status)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                        {offre.status === 'en_attente' && (
                          <>
                            <button
                              onClick={() => handleValidateOffer(offre.id)}
                              className="text-green-600 hover:text-green-900 px-3 py-1 bg-green-100 rounded"
                            >
                              ‚úÖ Valider
                            </button>
                            <button
                              onClick={() => handleRejectOffer(offre.id)}
                              className="text-red-600 hover:text-red-900 px-3 py-1 bg-red-100 rounded"
                            >
                              ‚ùå Rejeter
                            </button>
                          </>
                        )}
                        <button
                          onClick={() => handleDeleteOffer(offre.id)}
                          className="text-red-600 hover:text-red-900 px-3 py-1 bg-gray-100 rounded"
                        >
                          üóëÔ∏è Supprimer
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default GestionOffres;
