// src/Pages/DashboardAdmin.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const DashboardAdmin = () => {
  const [dashboard, setDashboard] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/');

    fetch('http://localhost:3000/api/dashboard/admin', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.error) setError(data.error);
        else setDashboard(data);
      })
      .catch(() => setError("Erreur lors du chargement de l'admin."));
  }, [navigate]);

  if (error) return <div className="text-red-500 p-4">{error}</div>;
  if (!dashboard) return <div className="text-white text-center mt-10">Chargement...</div>;

  return (
    <div className="min-h-screen bg-gray-900 text-white p-8">
      <h1 className="text-3xl font-bold mb-4">Dashboard Admin</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-gray-800 p-4 rounded-xl shadow-lg">
          <h2 className="text-lg font-semibold">Utilisateurs</h2>
          <p className="text-2xl">{dashboard.stats.users}</p>
        </div>
        <div className="bg-gray-800 p-4 rounded-xl shadow-lg">
          <h2 className="text-lg font-semibold">Entreprises</h2>
          <p className="text-2xl">{dashboard.stats.entreprises}</p>
        </div>
        <div className="bg-gray-800 p-4 rounded-xl shadow-lg">
          <h2 className="text-lg font-semibold">Ministères</h2>
          <p className="text-2xl">{dashboard.stats.ministeres}</p>
        </div>
      </div>

      <div className="bg-gray-800 p-6 rounded-xl shadow-lg">
        <h2 className="text-2xl font-bold mb-4">Utilisateurs</h2>
        <table className="w-full table-auto text-left">
          <thead className="text-gray-400">
            <tr>
              <th className="py-2">Nom</th>
              <th className="py-2">Email</th>
              <th className="py-2">Rôle</th>
            </tr>
          </thead>
          <tbody>
            {dashboard.users.map((user, index) => (
              <tr key={index} className="border-t border-gray-700">
                <td className="py-2">{user.username}</td>
                <td className="py-2">{user.email}</td>
                <td className="py-2 capitalize">{user.role}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default DashboardAdmin;
