import React, { useEffect, useState } from 'react';
import { FaBullseye, FaFileAlt, FaUsers, FaSearch, FaHandshake, FaChartLine, FaGlobe } from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion';
import { ChevronLeftIcon, ChevronRightIcon } from "@heroicons/react/24/solid";

// Animations
const container = {
  hidden: { opacity: 0 },
  show: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1
    }
  }
};

const item = {
  hidden: { opacity: 0, y: 20 },
  show: { opacity: 1, y: 0, transition: { duration: 0.5 } }
};

const fadeIn = {
  hidden: { opacity: 0 },
  show: { opacity: 1, transition: { duration: 0.8 } }
};

const slideUp = {
  hidden: { opacity: 0, y: 30 },
  show: { opacity: 1, y: 0, transition: { duration: 0.6 } }
};

const floatAnimation = {
  y: [0, -15, 0],
  transition: {
    duration: 4,
    repeat: Infinity,
    repeatType: "loop",
    ease: "easeInOut"
  }
};

const Home = () => {
  const navigate = useNavigate();
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchPublishedOffers = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/offres/publiees');
        setOffers(response.data);
      } catch (error) {
        console.error("Erreur lors du chargement des offres", error);
      } finally {
        setLoading(false);
      }
    };
    fetchPublishedOffers();
  }, []);

  const filteredOffers = offers.filter(offer =>
    offer.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    offer.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalOffers = offers.length;
  const urgentOffers = offers.filter(offer => offer.statut === 'urgent').length;
  const uniqueCategories = [...new Set(offers.map(offer => offer.category || 'Autre'))];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 flex flex-col">
      {/* Header */}
      <motion.header 
        initial="hidden"
        animate="show"
        variants={fadeIn}
        className="p-8 text-center mt-6"
      >
        <motion.h1 
          variants={slideUp}
          className="text-4xl font-bold mb-4 text-blue-800"
        >
          Plateforme des Marchés Publics
        </motion.h1>
        <motion.p 
          variants={slideUp}
          className="text-lg max-w-3xl mx-auto text-blue-600"
        >
          Transparence et efficacité dans la gestion des marchés publics
        </motion.p>
      </motion.header>

     {/* Barre de recherche (inchangée) */}
<motion.div 
  initial="hidden"
  animate="show"
  variants={slideUp}
  transition={{ delay: 0.2 }}
  className="px-6 md:px-20 mt-8 mb-6"
>
  <div className="relative max-w-2xl mx-auto">
    <FaSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-blue-400" />
    <input
      type="text"
      placeholder="Rechercher une offre..."
      className="w-full pl-12 pr-4 py-3 bg-white border border-blue-200 rounded-full text-blue-800 placeholder-blue-400 focus:outline-none focus:ring-2 focus:ring-blue-300 focus:border-transparent"
      value={searchTerm}
      onChange={(e) => setSearchTerm(e.target.value)}
    />
  </div>
</motion.div>

{/* Carrousel amélioré */}
{!loading && (
  <motion.div 
    initial="hidden"
    animate="show"
    variants={slideUp}
    transition={{ delay: 0.3 }}
    className="mb-8 max-w-4xl mx-auto"
  >
    <div className="relative group h-96 md:h-[28rem]"> {/* Ajuste la hauteur ici */}
      {/* Slides */}
      <div className="swiper-wrapper h-full flex">
        {/* Slide 1 - Statistiques */}
        <div 
          className="swiper-slide flex-shrink-0 w-full relative bg-blue-900 rounded-xl overflow-hidden"
          style={{
            backgroundImage: "linear-gradient(rgba(3, 83, 164, 0.8), rgba(3, 83, 164, 0.9))"
          }}
        >
          <div className="absolute inset-0 flex items-center justify-center p-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 w-full max-w-3xl">
              <motion.div 
                variants={item} 
                className="bg-white/10 backdrop-blur-sm p-6 rounded-xl border border-white/20"
              >
                <span className="block text-3xl font-bold text-white">{totalOffers}</span>
                <span className="text-white/90">Offres disponibles</span>
              </motion.div>
              <motion.div 
                variants={item} 
                className="bg-white/10 backdrop-blur-sm p-6 rounded-xl border border-white/20"
              >
                <span className="block text-3xl font-bold text-red-300">{urgentOffers}</span>
                <span className="text-white/90">Urgences</span>
              </motion.div>
              <motion.div 
                variants={item} 
                className="bg-white/10 backdrop-blur-sm p-6 rounded-xl border border-white/20"
              >
                <span className="block text-3xl font-bold text-green-300">{uniqueCategories.length}</span>
                <span className="text-white/90">Spécialités</span>
              </motion.div>
            </div>
          </div>
        </div>

        {/* Slides d'images avec textes */}
        {[
          {
            img: "./Public/TP.JPEG",
            title: "Infrastructures Durables",
            text: "Routes, ponts et réseaux hydrauliques conçus pour résister aux décennies",
            cta: "Voir les appels d'offres"
          },
          {
            img: u,
            title: "Aménagement Urbain",
            text: "Transformons les villes avec des solutions innovantes et écologiques",
            cta: "Projets en cours"
          },
          {
            img: "./Public/AA.JPEG",
            title: "Bâtiments Publics",
            text: "Écoles, hôpitaux et administrations construits aux normes internationales",
            cta: "Soumissionner"
          }
        ].map((item, index) => (
          <div 
            key={index} 
            className="swiper-slide flex-shrink-0 w-full relative rounded-xl overflow-hidden"
          >
            {/* Image de fond */}
            <img 
              src={item.img} 
              alt={item.title}
              className="absolute inset-0 w-full h-full object-cover brightness-75"
            />
            {/* Texte superposé */}
            <div className="absolute inset-0 flex items-center justify-center p-8 text-center">
              <div className="max-w-2xl bg-black/40 backdrop-blur-sm p-8 rounded-xl border border-white/10">
                <h3 className="text-2xl md:text-3xl font-bold text-white mb-3">{item.title}</h3>
                <p className="text-white/90 mb-6 text-lg">{item.text}</p>
                <button className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-full font-medium transition-all">
                  {item.cta}
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Contrôles */}
      <button className="carousel-prev absolute left-4 top-1/2 -translate-y-1/2 bg-white/20 hover:bg-white/30 text-white w-12 h-12 rounded-full flex items-center justify-center shadow-lg z-10 transition-all">
        <ChevronLeftIcon className="w-6 h-6" />
      </button>
      <button className="carousel-next absolute right-4 top-1/2 -translate-y-1/2 bg-white/20 hover:bg-white/30 text-white w-12 h-12 rounded-full flex items-center justify-center shadow-lg z-10 transition-all">
        <ChevronRightIcon className="w-6 h-6" />
      </button>

      {/* Pagination */}
      <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 flex space-x-2 z-10">
        {[0, 1, 2, 3].map((i) => (
          <button 
            key={i}
            className={`w-3 h-3 rounded-full transition-all ${i === 0 ? 'bg-white w-6' : 'bg-white/40'}`}
          ></button>
        ))}
      </div>
    </div>
  </motion.div>
)}
      {/* Section À propos */}
    <motion.section 
  initial="hidden"
  animate="show"
  variants={container}
  className="py-12 px-6 md:px-20 bg-white bg-opacity-50 backdrop-blur-sm my-8 rounded-xl max-w-6xl mx-auto"
>
  <motion.h2 
    variants={item}
    className="text-3xl font-bold text-center mb-8 text-blue-800"
  >
    À Propos de Notre Plateforme
  </motion.h2>
  
  <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
    <motion.div 
      variants={item}
      className="bg-white p-6 rounded-lg shadow-md border border-blue-100"
    >
      <motion.div
        animate={{
          y: [0, -10, 0],
          transition: {
            duration: 4,
            repeat: Infinity,
            repeatType: "loop",
            ease: "easeInOut"
          }
        }}
        className="flex justify-center mb-4"
      >
        <FaHandshake className="text-4xl text-blue-600" />
      </motion.div>
      <h3 className="text-xl font-semibold text-center mb-3 text-blue-700">Transparence</h3>
      <p className="text-gray-600 text-center">
        Nous garantissons une gestion transparente et équitable de tous les marchés publics.
      </p>
    </motion.div>
    
    <motion.div 
      variants={item}
      className="bg-white p-6 rounded-lg shadow-md border border-blue-100"
    >
      <motion.div
        animate={{
          y: [0, -10, 0],
          transition: {
            duration: 4,
            repeat: Infinity,
            repeatType: "loop",
            ease: "easeInOut",
            delay: 0.5
          }
        }}
        className="flex justify-center mb-4"
      >
        <FaChartLine className="text-4xl text-blue-600" />
      </motion.div>
      <h3 className="text-xl font-semibold text-center mb-3 text-blue-700">Efficacité</h3>
      <p className="text-gray-600 text-center">
        Optimisation des processus pour une gestion plus rapide et plus efficace des appels d'offres.
      </p>
    </motion.div>
    
    <motion.div 
      variants={item}
      className="bg-white p-6 rounded-lg shadow-md border border-blue-100"
    >
      <motion.div
        animate={{
          y: [0, -10, 0],
          transition: {
            duration: 4,
            repeat: Infinity,
            repeatType: "loop",
            ease: "easeInOut",
            delay: 1
          }
        }}
        className="flex justify-center mb-4"
      >
        <FaGlobe className="text-4xl text-blue-600" />
      </motion.div>
      <h3 className="text-xl font-semibold text-center mb-3 text-blue-700">Accessibilité</h3>
      <p className="text-gray-600 text-center">
        Plateforme accessible à toutes les entreprises, favorisant une concurrence saine et ouverte.
      </p>
    </motion.div>
  </div>
  
  <motion.div 
    variants={item}
    className="mt-10 text-center max-w-3xl mx-auto"
  >
    <p className="text-blue-700 mb-6">
      Notre plateforme a été créée pour moderniser la gestion des marchés publics, en mettant à disposition des outils numériques performants qui simplifient les processus tout en garantissant l'intégrité et la transparence des transactions.
    </p>
  </motion.div>
</motion.section>

      {/* Liste des offres */}
      <section className="mt-4 px-6 md:px-20 mb-12">
        <motion.h2 
          initial="hidden"
          animate="show"
          variants={slideUp}
          className="text-2xl font-bold mb-4 text-center text-blue-800"
        >
          Derniers Appels d'Offres
        </motion.h2>
        {loading ? (
          <motion.div 
            animate={{ 
              rotate: 360,
              transition: { repeat: Infinity, duration: 1, ease: "linear" } 
            }}
            className="flex justify-center items-center h-40"
          >
            <div className="rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
          </motion.div>
        ) : filteredOffers.length === 0 ? (
          <motion.div 
            initial="hidden"
            animate="show"
            variants={fadeIn}
            className="text-center py-12"
          >
            <p className="text-lg text-blue-600">
              {searchTerm ? "Aucune offre ne correspond à votre recherche" : "Aucune offre disponible pour le moment"}
            </p>
          </motion.div>
        ) : (
          <motion.div 
            initial="hidden"
            animate="show"
            variants={container}
            className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
          >
            {filteredOffers.map((offer, index) => (
              <motion.div 
                key={offer.id || index}
                variants={item}
                whileHover={{ scale: 1.03 }}
                whileTap={{ scale: 0.98 }}
              >
                <OfferCard 
                  offer={offer}
                  onClick={() => navigate(`/offre/${offer.id}`)}
                />
              </motion.div>
            ))}
          </motion.div>
        )}
      </section>

      {/* Services avec animations flottantes */}
      <motion.section 
        initial="hidden"
        animate="show"
        variants={container}
        className="flex flex-col md:flex-row justify-center px-6 md:px-20 gap-6 mb-16 relative"
      >
        {/* Éléments décoratifs animés */}
        <motion.div
          animate={{
            ...floatAnimation,
            x: [0, 20, 0],
            transition: { ...floatAnimation.transition, delay: 0.5 }
          }}
          className="absolute top-0 left-10 w-8 h-8 bg-blue-200 rounded-full opacity-30"
        />
        <motion.div
          animate={{
            ...floatAnimation,
            y: [0, -20, 0],
            x: [0, -15, 0],
            transition: { ...floatAnimation.transition, delay: 1 }
          }}
          className="absolute bottom-10 right-20 w-6 h-6 bg-blue-300 rounded-full opacity-40"
        />
        
        <motion.div 
          variants={item}
          animate={floatAnimation}
        >
          <ServiceCard
            icon={<FaBullseye className="text-blue-600 text-3xl" />}
            title="Notre Mission"
            description="Découvrez nos engagements pour la transparence"
            link="/mission"
          />
        </motion.div>
        <motion.div 
          variants={item}
          animate={{
            ...floatAnimation,
            transition: { ...floatAnimation.transition, delay: 0.3 }
          }}
        >
          <ServiceCard
            icon={<FaFileAlt className="text-blue-600 text-3xl" />}
            title="Toutes les Offres"
            description="Consultez l'ensemble des marchés publics"
            link="/offres"
          />
        </motion.div>
        <motion.div 
          variants={item}
          animate={{
            ...floatAnimation,
            transition: { ...floatAnimation.transition, delay: 0.6 }
          }}
        >
          <ServiceCard
            icon={<FaUsers className="text-blue-600 text-3xl" />}
            title="Espace Entreprise"
            description="Accédez à votre espace personnalisé"
            link="/login"
          />
        </motion.div>
      </motion.section>

      {/* Footer */}
      <motion.footer 
        initial="hidden"
        animate="show"
        variants={fadeIn}
        className="bg-blue-800 text-white py-10 mt-auto"
      >
        <div className="max-w-5xl mx-auto px-4 text-center">
          <h2 className="text-xl font-semibold mb-4">Plateforme des Marchés Publics</h2>
          <p className="text-sm text-blue-200 mb-4">
            Un outil moderne pour une gestion transparente des marchés publics
          </p>
          <div className="flex justify-center gap-6 text-blue-300 text-xl mb-6">
            <motion.a 
              whileHover={{ scale: 1.2, color: "#ffffff" }}
              href="#" 
              aria-label="Facebook" 
              className="hover:text-white"
            >
              <i className="fab fa-facebook-f"></i>
            </motion.a>
            <motion.a 
              whileHover={{ scale: 1.2, color: "#ffffff" }}
              href="#" 
              aria-label="Twitter" 
              className="hover:text-white"
            >
              <i className="fab fa-twitter"></i>
            </motion.a>
            <motion.a 
              whileHover={{ scale: 1.2, color: "#ffffff" }}
              href="#" 
              aria-label="LinkedIn" 
              className="hover:text-white"
            >
              <i className="fab fa-linkedin-in"></i>
            </motion.a>
          </div>
          <p className="text-xs text-blue-300">© {new Date().getFullYear()} Ministère des Finances – Tous droits réservés</p>
        </div>
      </motion.footer>
    </div>
  );
};

// Carte Offre
const OfferCard = ({ offer, onClick }) => (
  <motion.div 
    whileHover={{ 
      scale: 1.02,
      boxShadow: "0 10px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)"
    }}
    onClick={onClick}
    className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow cursor-pointer border border-blue-100"
  >
    <div className="flex justify-between items-start mb-3">
      <h3 className="text-lg font-semibold text-blue-800 line-clamp-2">{offer.title}</h3>
      {offer.statut === 'urgent' && (
        <motion.span 
          animate={{ 
            scale: [1, 1.1, 1],
            transition: { repeat: Infinity, duration: 1.5 }
          }}
          className="bg-red-100 text-red-800 text-xs px-2 py-1 rounded-full"
        >
          Urgent
        </motion.span>
      )}
    </div>
    <p className="text-gray-600 text-sm mb-4 line-clamp-3">{offer.description}</p>
    <div className="flex justify-between items-center text-xs">
      <span className="text-blue-600">Date limite : {new Date(offer.deadline).toLocaleDateString()}</span>
      <span className="text-gray-500">Ref : {offer.reference || "N/A"}</span>
    </div>
  </motion.div>
);

// Carte Service
const ServiceCard = ({ icon, title, description, link }) => (
  <Link to={link}>
    <motion.div
      whileHover={{ 
        y: -5,
        boxShadow: "0 10px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)"
      }}
      className="bg-white rounded-xl shadow-md p-6 flex flex-col items-center text-center hover:shadow-lg transition-transform max-w-xs h-full"
    >
      <motion.div 
        whileHover={{ scale: 1.1 }}
        className="mb-4 p-3 bg-blue-50 rounded-full"
      >
        {icon}
      </motion.div>
      <h3 className="text-xl font-semibold mb-2 text-blue-700">{title}</h3>
      <p className="text-gray-600 text-sm">{description}</p>
    </motion.div>
  </Link>
);

export default Home;
