// DashboardMinistere.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  FaCheckCircle,
  FaTimesCircle,
  FaChartBar,
  FaSpinner,
  FaSignOutAlt,
  FaSearch,
  FaFilter,
  FaTrash
} from 'react-icons/fa';
import DashboardChart from './DashboardChart';

// Composants internes (à créer dans le même dossier ou importer depuis un fichier commun)
const StatBox = ({ icon, title, value }) => (
  <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-md text-center hover:bg-opacity-90 transition-colors">
    <div className="text-blue-400 mb-2">{icon}</div>
    <h3 className="text-lg font-semibold">{title}</h3>
    <p className="text-2xl font-bold">{value}</p>
  </div>
);

const OfferCard = ({ offer, onValidate, onReject, onDelete, loading, isPublished = false }) => (
  <div className="bg-white dark:bg-gray-800 p-4 rounded-xl shadow-md hover:bg-opacity-90 transition-colors border-l-4 border-blue-200">
    <h3 className="text-lg font-bold text-blue-900 dark:text-white">{offer.title}</h3>
    <p className="text-sm mb-2 text-blue-700 dark:text-blue-300">{offer.description}</p>
    <div className="flex flex-wrap justify-between items-center mt-4 gap-2">
      <div className="space-y-1">
        <p className="text-xs text-blue-500">Date limite : {new Date(offer.deadline).toLocaleDateString()}</p>
        {offer.status && (
          <span className={`text-xs px-2 py-1 rounded-full ${
            offer.status === 'valide' ? 'bg-green-100 text-green-700' :
            (offer.status === 'rejected' || offer.status === 'rejetée') ? 'bg-red-100 text-red-700' :
            'bg-yellow-100 text-yellow-700'
          }`}>
            {offer.status === 'valide' ? 'Validée' :
              (offer.status === 'rejected' || offer.status === 'rejetée') ? 'Rejetée' : 'En attente'}
          </span>
        )}
      </div>
      {!isPublished && (
        <div className="flex gap-2">
          {onValidate && (
            <button
              onClick={(e) => {
                e.stopPropagation();
                onValidate();
              }}
              className="flex items-center gap-1 bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm disabled:opacity-50"
              disabled={loading === 'validation'}
            >
              {loading === 'validation' ? <FaSpinner className="animate-spin" /> : <FaCheckCircle />}
              Valider
            </button>
          )}
          {onReject && (
            <button
              onClick={(e) => {
                e.stopPropagation();
                onReject();
              }}
              className="flex items-center gap-1 bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm disabled:opacity-50"
              disabled={loading === 'rejet'}
            >
              {loading === 'rejet' ? <FaSpinner className="animate-spin" /> : <FaTimesCircle />}
              Rejeter
            </button>
          )}
          {onDelete && (
            <button
              onClick={(e) => {
                e.stopPropagation();
                onDelete();
              }}
              className="flex items-center gap-1 bg-gray-600 hover:bg-gray-700 text-white px-3 py-1 rounded text-sm"
            >
              <FaTrash /> Supprimer
            </button>
          )}
        </div>
      )}
    </div>
  </div>
);

const DashboardMinistere = ({ toggleDarkMode, darkMode }) => {
  // ... (le reste du code reste inchangé)
};

export default DashboardMinistere;
