import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { useTheme } from '../../context/ThemeContext';
import { useAuth } from '../../context/AuthContext'; // Import corrigé
import axios from 'axios';
import {
  FaSave,
  FaTimes,
  FaCalendarAlt,
  FaMoneyBillWave,
  FaMapMarkerAlt,
  FaBuilding,
  FaFileContract
} from 'react-icons/fa';

const CreerOffre = () => {
  const { darkMode } = useTheme();
  const { user } = useAuth(); // Utilisation du contexte d'authentification
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    titre: '',
    description: '',
    dateLimite: '',
    budget: '',
    lieu: '',
    typeMarche: 'Services',
    reference: '',
    documents: null
  });

  const [entreprises, setEntreprises] = useState([]);
  const [selectedEntreprise, setSelectedEntreprise] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    const fetchEntreprises = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/entreprises', {
          headers: {
            Authorization: `Bearer ${user.token}` // Utilisation du token
          }
        });
        setEntreprises(response.data);
      } catch (error) {
        console.error("Erreur chargement entreprises:", error);
      }
    };
    
    if (user?.token) fetchEntreprises();
  }, [user]);

  // ... (le reste du code reste identique)
};

export default CreerOffre;
