import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaSearch, FaPaperPlane } from 'react-icons/fa';

const PostulerOffre = () => {
  const [offers, setOffers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedOffer, setSelectedOffer] = useState(null);
  const [message, setMessage] = useState('');
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchOffers = async () => {
      try {
        const res = await axios.get('http://localhost:3000/api/offres');
        setOffers(res.data);
      } catch (err) {
        console.error('Erreur chargement offres:', err);
      }
    };
    fetchOffers();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedOffer || !message) return;

    setSubmitting(true);
    try {
      await axios.post('http://localhost:3000/api/candidatures', {
        offerId: selectedOffer.id,
        message
      });
      alert('Candidature envoyée avec succès!');
      setMessage('');
      setSelectedOffer(null);
    } catch (err) {
      console.error('Erreur envoi candidature:', err);
    } finally {
      setSubmitting(false);
    }
  };

  const filteredOffers = offers.filter(offer =>
    offer.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen p-6 bg-gradient-to-br from-purple-900 to-indigo-900 text-white">
      <h1 className="text-3xl font-bold mb-8 text-center">Postuler à une offre</h1>

      <div className="max-w-4xl mx-auto">
        <div className="relative mb-8">
          <FaSearch className="absolute left-3 top-3 text-gray-400" />
          <input
            type="text"
            placeholder="Rechercher une offre..."
            className="w-full pl-10 pr-4 py-2 rounded-lg bg-white bg-opacity-20 text-white"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {filteredOffers.map(offer => (
            <div 
              key={offer.id}
              className={`p-4 rounded-lg cursor-pointer transition-all ${
                selectedOffer?.id === offer.id 
                  ? 'bg-indigo-600' 
                  : 'bg-white bg-opacity-10 hover:bg-opacity-20'
              }`}
              onClick={() => setSelectedOffer(offer)}
            >
              <h3 className="font-bold">{offer.title}</h3>
              <p className="text-sm text-gray-300">{offer.description}</p>
              <p className="text-xs text-indigo-300 mt-2">Date limite: {offer.deadline}</p>
            </div>
          ))}
        </div>

        {selectedOffer && (
          <form onSubmit={handleSubmit} className="bg-white bg-opacity-10 p-6 rounded-xl">
            <h3 className="text-xl font-semibold mb-4">Postuler à: {selectedOffer.title}</h3>
            <textarea
              className="w-full p-4 rounded-lg bg-white bg-opacity-20 text-white mb-4"
              rows="6"
              placeholder="Votre message de motivation..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
            <button
              type="submit"
              disabled={submitting}
              className="flex items-center gap-2 bg-green-600 hover:bg-green-700 px-6 py-2 rounded-lg"
            >
              {submitting ? 'Envoi en cours...' : (
                <>
                  <FaPaperPlane /> Envoyer la candidature
                </>
              )}
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default PostulerOffre;
