import React, { useEffect, useState, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  FaClipboardList,
  FaCheckCircle,
  FaChartBar,
  FaChartPie,
  FaArrowRight,
  FaSpinner,
  FaArrowLeft,
  FaClock,
  FaTimesCircle,
  FaCalendarAlt
} from 'react-icons/fa';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, registerables } from 'chart.js';
import { DarkModeContext } from '../../Components/DarkModeContext';

ChartJS.register(...registerables);

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

const DashboardEntreprise = () => {
  const { darkMode } = useContext(DarkModeContext);
  const navigate = useNavigate();
  
  const [stats, setStats] = useState({
    totalOffres: 0,
    totalCandidatures: 0,
    candidaturesEnAttente: 0,
    candidaturesValides: 0,
    candidaturesRejetees: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [chartData, setChartData] = useState(null);
  const [latestOffers, setLatestOffers] = useState([]);
  const [recentCandidatures, setRecentCandidatures] = useState([]);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('Token manquant');
          return;
        }

        const response = await axios.get(`${API_BASE_URL}/api/dashboard/entreprise`, {
          headers: { Authorization: `Bearer ${token}` }
        });

        const { offers, candidatures, stats } = response.data;
        
        setStats(stats);
        setLatestOffers(offers);
        setRecentCandidatures(candidatures.slice(0, 5)); // 5 dernières candidatures

        prepareChartData(stats);
      } catch (err) {
        console.error('Erreur chargement dashboard:', err);
        setError(err.response?.data?.message || 'Erreur de chargement');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const prepareChartData = (data) => {
    const colors = darkMode ? {
      success: 'rgba(34, 197, 94, 0.8)',
      warning: 'rgba(251, 191, 36, 0.8)',
      danger: 'rgba(239, 68, 68, 0.8)',
      primary: 'rgba(59, 130, 246, 0.8)',
      secondary: 'rgba(168, 85, 247, 0.8)'
    } : {
      success: 'rgba(16, 185, 129, 0.7)',
      warning: 'rgba(234, 179, 8, 0.7)',
      danger: 'rgba(220, 38, 38, 0.7)',
      primary: 'rgba(59, 130, 246, 0.7)',
      secondary: 'rgba(168, 85, 247, 0.7)'
    };

    setChartData({
      bar: {
        labels: ['Acceptées', 'En attente', 'Rejetées'],
        datasets: [{
          label: 'Statut des Candidatures',
          data: [data.candidaturesValides, data.candidaturesEnAttente, data.candidaturesRejetees],
          backgroundColor: [colors.success, colors.warning, colors.danger],
          borderColor: [colors.success, colors.warning, colors.danger],
          borderWidth: 1
        }]
      },
      pie: {
        labels: ['Offres disponibles', 'Candidatures envoyées'],
        datasets: [{
          data: [data.totalOffres, data.totalCandidatures],
          backgroundColor: [colors.primary, colors.secondary],
          borderColor: [colors.primary, colors.secondary],
          borderWidth: 1
        }]
      }
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <FaSpinner className="animate-spin text-4xl text-blue-600 dark:text-blue-400 mx-auto mb-4" />
          <p className="text-gray-600 dark:text-gray-300">Chargement du dashboard...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="bg-red-100 dark:bg-red-900 border border-red-400 dark:border-red-600 text-red-700 dark:text-red-200 px-4 py-3 rounded">
            <p className="font-bold">Erreur</p>
            <p>{error}</p>
          </div>
          <button
            onClick={() => window.location.reload()}
            className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Réessayer
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Header */}
      <div className="bg-white dark:bg-gray-800 shadow-sm border-b dark:border-gray-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <button
                onClick={() => navigate('/entreprise/home')}
                className="mr-4 p-2 text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-full"
              >
                <FaArrowLeft className="h-5 w-5" />
              </button>
              <h1 className="text-2xl font-bold text-gray-900 dark:text-white">Dashboard Entreprise</h1>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Cartes de Statistiques */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatCard
            icon={<FaClipboardList className="text-blue-600 dark:text-blue-400" size={24} />}
            title="Offres Disponibles"
            value={stats.totalOffres}
            link="/entreprise/offres-disponibles"
            linkText="Voir les offres"
            darkMode={darkMode}
          />
          <StatCard
            icon={<FaCheckCircle className="text-green-600 dark:text-green-400" size={24} />}
            title="Candidatures Acceptées"
            value={stats.candidaturesValides}
            link="/entreprise/mescandidatures"
            linkText="Voir détails"
            darkMode={darkMode}
          />
          <StatCard
            icon={<FaClock className="text-yellow-500 dark:text-yellow-400" size={24} />}
            title="En Attente"
            value={stats.candidaturesEnAttente}
            link="/entreprise/mescandidatures"
            linkText="Voir le suivi"
            darkMode={darkMode}
          />
          <StatCard
            icon={<FaTimesCircle className="text-red-600 dark:text-red-400" size={24} />}
            title="Candidatures Rejetées"
            value={stats.candidaturesRejetees}
            link="/entreprise/mescandidatures"
            linkText="Voir détails"
            darkMode={darkMode}
          />
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Dernières offres disponibles */}
          <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg">
            <h3 className="text-xl font-semibold mb-4 text-gray-900 dark:text-white">Dernières Offres Disponibles</h3>
            {latestOffers.length > 0 ? (
              <div className="space-y-4">
                {latestOffers.slice(0, 4).map(offer => (
                  <OfferCard key={offer.id} offer={offer} darkMode={darkMode} />
                ))}
              </div>
            ) : (
              <p className="text-gray-500 dark:text-gray-400 italic">Aucune offre disponible actuellement</p>
            )}
            <Link
              to="/entreprise/offres-disponibles"
              className="mt-4 inline-flex items-center text-blue-600 dark:text-blue-400 hover:text-blue-800 dark:hover:text-blue-300"
            >
              Voir toutes les offres <FaArrowRight className="ml-1" />
            </Link>
          </div>

          {/* Candidatures récentes */}
          <div className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg">
            <h3 className="text-xl font-semibold mb-4 text-gray-900 dark:text-white">Candidatures Récentes</h3>
            {recentCandidatures.length > 0 ? (
              <div className="space-y-4">
                {recentCandidatures.map(candidature => (
                  <CandidatureCard key={candidature.id} candidature={candidature} darkMode={darkMode} />
                ))}
              </div>
            ) : (
              <p className="text-gray-500 dark:text-gray-400 italic">Aucune candidature envoyée</p>
            )}
            <Link
              to="/entreprise/mescandidatures"
              className="mt-4 inline-flex items-center text-blue-600 dark:text-blue-400 hover:text-blue-800 dark:hover:text-blue-300"
            >
              Voir toutes les candidatures <FaArrowRight className="ml-1" />
            </Link>
          </div>
        </div>

        {/* Visualisations Graphiques */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <ChartContainer
            icon={<FaChartBar className="text-blue-600 dark:text-blue-400" />}
            title="Répartition des Candidatures"
            darkMode={darkMode}
          >
            {chartData && <Bar data={chartData.bar} options={getChartOptions('bar', darkMode)} />}
          </ChartContainer>
          <ChartContainer
            icon={<FaChartPie className="text-purple-600 dark:text-purple-400" />}
            title="Ratio Offres/Candidatures"
            darkMode={darkMode}
          >
            {chartData && <Pie data={chartData.pie} options={getChartOptions('pie', darkMode)} />}
          </ChartContainer>
        </div>
      </div>
    </div>
  );
};

// Composants adaptés au mode sombre
const OfferCard = ({ offer, darkMode }) => {
  const isExpired = new Date(offer.dateLimite) < new Date();
  
  return (
    <div className={`p-4 rounded-lg border transition-colors ${
      darkMode
        ? 'bg-gray-700 border-gray-600 hover:bg-gray-600'
        : 'bg-gray-50 border-gray-200 hover:bg-gray-100'
    }`}>
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <h4 className={`font-semibold text-sm ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            {offer.title}
          </h4>
          <p className={`text-xs mt-1 ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
            {offer.domaine}
          </p>
          <div className="flex items-center mt-2 text-xs">
            <FaCalendarAlt className={`mr-1 ${darkMode ? 'text-gray-400' : 'text-gray-500'}`} />
            <span className={`${isExpired ? 'text-red-500' : darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
              {new Date(offer.dateLimite).toLocaleDateString('fr-FR')}
            </span>
          </div>
        </div>
        {!isExpired && (
          <Link
            to={`/entreprise/postuler/${offer.id}`}
            className="text-xs bg-blue-600 hover:bg-blue-700 text-white px-2 py-1 rounded"
          >
            Postuler
          </Link>
        )}
      </div>
    </div>
  );
};

const CandidatureCard = ({ candidature, darkMode }) => {
  const getStatusColor = (status) => {
    switch (status) {
      case 'valide':
        return 'text-green-600 dark:text-green-400';
      case 'rejetee':
        return 'text-red-600 dark:text-red-400';
      default:
        return 'text-yellow-600 dark:text-yellow-400';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'valide':
        return 'Acceptée';
      case 'rejetee':
        return 'Rejetée';
      default:
        return 'En attente';
    }
  };

  return (
    <div className={`p-4 rounded-lg border transition-colors ${
      darkMode
        ? 'bg-gray-700 border-gray-600'
        : 'bg-gray-50 border-gray-200'
    }`}>
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <h4 className={`font-semibold text-sm ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            {candidature.offer?.title || `Offre #${candidature.offerId}`}
          </h4>
          <p className={`text-xs mt-1 ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
            {new Date(candidature.createdAt).toLocaleDateString('fr-FR')}
          </p>
        </div>
        <span className={`text-xs font-medium ${getStatusColor(candidature.status)}`}>
          {getStatusText(candidature.status)}
        </span>
      </div>
    </div>
  );
};

const ChartContainer = ({ icon, title, children, darkMode }) => (
  <div className={`p-6 rounded-xl shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
    <div className={`flex items-center gap-2 mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
      {icon}
      <h3 className="text-xl font-semibold">{title}</h3>
    </div>
    {children}
  </div>
);

const StatCard = ({ icon, title, value, link, linkText, darkMode }) => (
  <div className={`p-6 rounded-xl shadow-lg transition-colors ${
    darkMode
      ? 'bg-gray-800 hover:bg-gray-700'
      : 'bg-white hover:bg-gray-50'
  }`}>
    <div className={`flex items-center gap-3 mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
      {icon}
      <h3 className="text-lg font-semibold">{title}</h3>
    </div>
    <p className={`text-3xl font-bold mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
      {value}
    </p>
    <Link
      to={link}
      className={`inline-flex items-center text-sm transition-colors ${
        darkMode
          ? 'text-blue-400 hover:text-blue-300'
          : 'text-blue-600 hover:text-blue-800'
      }`}
    >
      {linkText} <FaArrowRight className="ml-1" />
    </Link>
  </div>
);

// Options des graphiques adaptées au mode sombre
const getChartOptions = (type, darkMode) => {
  const textColor = darkMode ? '#e5e7eb' : '#374151';
  const gridColor = darkMode ? 'rgba(75, 85, 99, 0.3)' : 'rgba(156, 163, 175, 0.3)';

  const baseOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          color: textColor,
          font: {
            size: 12
          }
        }
      }
    }
  };

  if (type === 'bar') {
    return {
      ...baseOptions,
      scales: {
        y: {
          beginAtZero: true,
          ticks: { color: textColor },
          grid: { color: gridColor }
        },
        x: {
          ticks: { color: textColor },
          grid: { color: gridColor }
        }
      }
    };
  }

  return baseOptions;
};

export default DashboardEntreprise;
