import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaPaperPlane } from 'react-icons/fa';

const Postuler = () => {
  const [offers, setOffers] = useState([]);
  const [offerId, setOfferId] = useState('');
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    axios.get('/api/entreprise/offres', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setOffers(res.data))
    .catch(err => console.error(err));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!offerId || !file) {
      setMessage("Veuillez sélectionner une offre et un fichier.");
      return;
    }

    const formData = new FormData();
    formData.append('offerId', offerId);
    formData.append('file', file);

    try {
      const response = await axios.post('/api/entreprise/candidatures', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      setMessage("Candidature soumise avec succès !");
      setOfferId('');
      setFile(null);
    } catch (error) {
      setMessage("Erreur lors de la soumission de la candidature.");
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto bg-white shadow-lg rounded-xl mt-10">
      <h2 className="text-2xl font-bold mb-4 text-blue-700">Soumettre une candidature</h2>
      
      {message && <div className="mb-4 text-sm text-green-600">{message}</div>}

      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <select
          value={offerId}
          onChange={(e) => setOfferId(e.target.value)}
          className="border p-2 rounded"
        >
          <option value="">-- Sélectionner une offre --</option>
          {offers.map((offer) => (
            <option key={offer.id} value={offer.id}>
              {offer.title}
            </option>
          ))}
        </select>

        <input
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
          className="border p-2 rounded"
        />

        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded flex items-center justify-center gap-2"
        >
          <FaPaperPlane /> Soumettre
        </button>
      </form>
    </div>
  );
};

export default Postuler;
