import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { FaChartLine, FaUsers, FaPlusCircle } from 'react-icons/fa';
import { motion } from 'framer-motion';
import axios from 'axios';

const MinistereHome = () => {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const verifyAuth = async () => {
      setIsLoading(true);
      try {
        const token = localStorage.getItem('token');
        if (!token || user?.role !== 'ministere') {
          throw new Error('Accès non autorisé');
        }

        await axios.get('/api/auth/verify', {
          headers: { Authorization: `Bearer ${token}` },
        });
      } catch (error) {
        console.error('Erreur de vérification:', error);
        logout();
        navigate('/login');
      } finally {
        setIsLoading(false);
      }
    };

    verifyAuth();
  }, [user, logout, navigate]);

  useEffect(() => {
    const handleBeforeUnload = (e) => {
      if (user) {
        e.preventDefault();
        e.returnValue = 'Êtes-vous sûr de vouloir quitter?';
      }
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => window.removeEventListener('beforeunload', handleBeforeUnload);
  }, [user]);

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-white">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-purple-200 to-blue-300 dark:from-gray-900 dark:via-gray-800 dark:to-black text-gray-800 dark:text-white transition-colors duration-300 p-6">
      <h1 className="text-4xl font-bold text-center mb-10 text-indigo-900 dark:text-white">
        Espace Ministère
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-5xl mx-auto">
        {/* Dashboard */}
        <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
          <Link
            to="/Dashboard/DashbordMinistere"
            className="block bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8 hover:shadow-xl transition-all"
          >
            <div className="flex flex-col items-center">
              <FaChartLine className="text-indigo-600 dark:text-indigo-300 text-3xl mb-2" />
              <span className="text-2xl font-semibold mb-2">Dashboard</span>
              <span className="text-gray-500 dark:text-gray-300 text-center">
                Statistiques et analyses des marchés
              </span>
            </div>
          </Link>
        </motion.div>

        {/* Créer Offre */}
        <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
          <Link
            to="/Ministere/CreerOffre"
            className="block bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8 hover:shadow-xl transition-all"
          >
            <div className="flex flex-col items-center">
              <FaPlusCircle className="text-green-600 dark:text-green-400 text-3xl mb-2" />
              <span className="text-2xl font-semibold mb-2">Créer Offre</span>
              <span className="text-gray-500 dark:text-gray-300 text-center">
                Publier un nouveau marché public
              </span>
            </div>
          </Link>
        </motion.div>

        {/* Candidatures */}
        <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
          <Link
            to="/Ministere/GestionCandidatures"
            className="block bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8 hover:shadow-xl transition-all"
          >
            <div className="flex flex-col items-center">
              <FaUsers className="text-purple-600 dark:text-purple-300 text-3xl mb-2" />
              <span className="text-2xl font-semibold mb-2">Candidatures</span>
              <span className="text-gray-500 dark:text-gray-300 text-center">
                Gérer les soumissions reçues
              </span>
            </div>
          </Link>
        </motion.div>
      </div>

      <div className="mt-12 text-center text-sm text-gray-500 dark:text-gray-400">
        Session sécurisée • Dernière connexion: {new Date().toLocaleString()}
      </div>
    </div>
  );
};

export default MinistereHome;
