import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaSearch, FaBuilding, FaEnvelope, FaPhone, FaFileAlt, FaCheck, FaTimes, FaClock } from 'react-icons/fa';

const GestionCandidatures = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('tous');
  const [candidatures, setCandidatures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Récupération des candidatures depuis l'API
  useEffect(() => {
    const fetchCandidatures = async () => {
      try {
        setLoading(true);
        // Remplacez par votre appel API réel
        const response = await fetch('/api/candidatures');
        const data = await response.json();
        
        if (response.ok) {
          setCandidatures(data);
        } else {
          throw new Error(data.message || 'Erreur lors du chargement');
        }
      } catch (err) {
        setError(err.message);
        console.error('Erreur:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchCandidatures();

    // Optionnel: Mettre en place un système de mise à jour en temps réel
    // const eventSource = new EventSource('/api/candidatures/updates');
    // eventSource.onmessage = (e) => {
    //   setCandidatures(JSON.parse(e.data));
    // };
    // return () => eventSource.close();
  }, []);

  // Mise à jour du statut d'une candidature
  const updateCandidatureStatus = async (id, newStatus) => {
    try {
      // Optimistic UI update
      setCandidatures(prev => 
        prev.map(c => 
          c.id === id ? { ...c, status: newStatus } : c
        )
      );

      // Appel API pour sauvegarder
      const response = await fetch(`/api/candidatures/${id}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status: newStatus }),
      });

      if (!response.ok) {
        throw new Error('Échec de la mise à jour');
      }
    } catch (err) {
      console.error('Erreur:', err);
      // Revert if error
      setCandidatures(prev => 
        prev.map(c => 
          c.id === id ? { ...c, status: c.status } : c
        )
      );
    }
  };

  // Filtrage des candidatures
  const filteredCandidatures = candidatures.filter(candidature => {
    const matchesSearch = 
      candidature.nomEntreprise.toLowerCase().includes(searchTerm.toLowerCase()) || 
      candidature.titreOffre.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = selectedStatus === 'tous' || candidature.status === selectedStatus;
    return matchesSearch && matchesStatus;
  });

  // Styles dynamiques
  const getStatusStyle = (status) => {
    const base = "px-3 py-1 rounded-full flex items-center text-sm";
    return darkMode
      ? {
          'en_attente': `${base} bg-yellow-900 text-yellow-200`,
          'accepte': `${base} bg-green-900 text-green-200`,
          'rejete': `${base} bg-red-900 text-red-200`
        }[status]
      : {
          'en_attente': `${base} bg-yellow-100 text-yellow-800`,
          'accepte': `${base} bg-green-100 text-green-800`,
          'rejete': `${base} bg-red-100 text-red-800`
        }[status];
  };

  const getStatusIcon = (status) => {
    return {
      'en_attente': <FaClock className="mr-1" />,
      'accepte': <FaCheck className="mr-1" />,
      'rejete': <FaTimes className="mr-1" />
    }[status];
  };

  if (loading) {
    return (
      <div className={`min-h-screen flex items-center justify-center ${darkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ repeat: Infinity, duration: 1, ease: "linear" }}
          className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full"
        />
      </div>
    );
  }

  if (error) {
    return (
      <div className={`min-h-screen flex items-center justify-center ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
        <div className={`p-8 rounded-xl ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg text-center`}>
          <h2 className="text-2xl font-bold mb-4">Erreur</h2>
          <p className="mb-4">{error}</p>
          <button
            onClick={() => window.location.reload()}
            className={`px-4 py-2 rounded-lg ${darkMode ? 'bg-blue-600' : 'bg-blue-500'} text-white`}
          >
            Réessayer
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className={`min-h-screen transition-colors duration-300 ${darkMode ? 'bg-gray-900 text-gray-100' : 'bg-gray-50 text-gray-900'}`}>
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <motion.h1 
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            className="text-3xl font-bold"
          >
            Gestion des Candidatures
          </motion.h1>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className={`px-4 py-2 rounded-lg ${darkMode ? 'bg-yellow-400 text-gray-900' : 'bg-gray-800 text-white'}`}
          >
            {darkMode ? '☀️ Mode Clair' : '🌙 Mode Sombre'}
          </button>
        </div>

        {/* Barre de filtres */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2, duration: 0.5 }}
          className={`p-6 rounded-xl shadow-lg mb-8 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}
        >
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaSearch className="text-gray-400" />
              </div>
              <input
                type="text"
                placeholder="Rechercher entreprise ou offre..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className={`pl-10 w-full p-3 rounded-lg border ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
              />
            </div>

            <select
              value={selectedStatus}
              onChange={(e) => setSelectedStatus(e.target.value)}
              className={`p-3 rounded-lg border ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
            >
              <option value="tous">Tous les statuts</option>
              <option value="en_attente">En attente</option>
              <option value="accepte">Accepté</option>
              <option value="rejete">Rejeté</option>
            </select>

            <div className="flex items-center justify-end">
              <span className={`px-3 py-1 rounded-full ${darkMode ? 'bg-blue-900 text-blue-100' : 'bg-blue-100 text-blue-800'}`}>
                {filteredCandidatures.length} candidature(s)
              </span>
            </div>
          </div>
        </motion.div>

        {/* Liste des candidatures */}
        <AnimatePresence>
          {filteredCandidatures.length > 0 ? (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
              className="space-y-4"
            >
              {filteredCandidatures.map((candidature) => (
                <motion.div
                  key={candidature.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                  className={`p-6 rounded-xl shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'}`}
                >
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    {/* Informations entreprise */}
                    <div className="space-y-2">
                      <div className="flex items-center">
                        <FaBuilding className="mr-2 text-blue-500" />
                        <h3 className="text-xl font-semibold">{candidature.nomEntreprise}</h3>
                      </div>
                      {candidature.email && (
                        <div className="flex items-center">
                          <FaEnvelope className="mr-2 text-blue-500" />
                          <span>{candidature.email}</span>
                        </div>
                      )}
                      {candidature.telephone && (
                        <div className="flex items-center">
                          <FaPhone className="mr-2 text-blue-500" />
                          <span>{candidature.telephone}</span>
                        </div>
                      )}
                    </div>

                    {/* Détails offre */}
                    <div className="space-y-2">
                      <h4 className="font-medium">Offre postulée:</h4>
                      <p className="text-lg font-semibold">{candidature.titreOffre}</p>
                      <div className="flex items-center">
                        <FaFileAlt className="mr-2 text-blue-500" />
                        <a 
                          href={`/api/candidatures/${candidature.id}/dossier`} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className={`${darkMode ? 'text-blue-300 hover:text-blue-200' : 'text-blue-600 hover:text-blue-800'} underline`}
                        >
                          Voir le dossier complet
                        </a>
                      </div>
                    </div>

                    {/* Actions */}
                    <div className="flex flex-col justify-between">
                      <div className="flex justify-between items-center mb-4">
                        <span className={getStatusStyle(candidature.status)}>
                          {getStatusIcon(candidature.status)}
                          {candidature.status === 'en_attente' ? 'En attente' : 
                           candidature.status === 'accepte' ? 'Accepté' : 'Rejeté'}
                        </span>
                        <span className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                          {new Date(candidature.date).toLocaleDateString()}
                        </span>
                      </div>

                      <div className="flex space-x-2">
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => updateCandidatureStatus(candidature.id, 'accepte')}
                          className={`px-4 py-2 rounded-lg flex-1 ${candidature.status === 'accepte' 
                            ? 'bg-gray-400 cursor-not-allowed' 
                            : 'bg-green-600 hover:bg-green-700'} text-white`}
                          disabled={candidature.status === 'accepte'}
                        >
                          Valider
                        </motion.button>
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => updateCandidatureStatus(candidature.id, 'rejete')}
                          className={`px-4 py-2 rounded-lg flex-1 ${candidature.status === 'rejete' 
                            ? 'bg-gray-400 cursor-not-allowed' 
                            : 'bg-red-600 hover:bg-red-700'} text-white`}
                          disabled={candidature.status === 'rejete'}
                        >
                          Rejeter
                        </motion.button>
                      </div>
                    </div>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          ) : (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
              className={`p-8 text-center rounded-xl ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg`}
            >
              <p className="text-xl font-semibold mb-2">Aucune candidature trouvée</p>
              <p className={`${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                {searchTerm || selectedStatus !== 'tous' 
                  ? "Aucun résultat pour vos critères de recherche" 
                  : "Aucune candidature n'a été soumise pour le moment"}
              </p>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default GestionCandidatures;
