const GestionCandidatures = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('tous');
  const [candidatures, setCandidatures] = useState([]);

  // Simuler la récupération des candidatures depuis une API
  useEffect(() => {
    // En pratique, vous feriez un appel API ici
    const fetchCandidatures = async () => {
      // Exemple de données simulées - à remplacer par un vrai appel API
      const mockData = [
        // Les données viendront de votre backend quand une entreprise postule
      ];
      setCandidatures(mockData);
    };

    fetchCandidatures();
  }, []);

  // Fonction pour simuler la réception d'une nouvelle candidature
  const handleNewCandidature = (newCandidature) => {
    setCandidatures(prev => [...prev, {
      ...newCandidature,
      id: Date.now(), // Génère un ID unique
      status: 'en_attente',
      date: new Date().toISOString().split('T')[0]
    }]);
  };

  // Fonction pour valider/rejeter une candidature
  const updateCandidatureStatus = (id, newStatus) => {
    setCandidatures(prev => 
      prev.map(c => 
        c.id === id ? { ...c, status: newStatus } : c
      )
    );
  };

  // Filtrer les candidatures selon la recherche et le statut
  const filteredCandidatures = candidatures.filter(candidature => {
    const matchesSearch = candidature.nomEntreprise.toLowerCase().includes(searchTerm.toLowerCase()) || 
                         candidature.titreOffre.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = selectedStatus === 'tous' || candidature.status === selectedStatus;
    return matchesSearch && matchesStatus;
  });

  // Couleurs et icônes selon le statut
  const getStatusStyle = (status) => {
    const baseClasses = "px-3 py-1 rounded-full flex items-center text-sm";
    
    if (darkMode) {
      return {
        'en_attente': `${baseClasses} bg-yellow-900 text-yellow-200`,
        'accepte': `${baseClasses} bg-green-900 text-green-200`,
        'rejete': `${baseClasses} bg-red-900 text-red-200`
      }[status];
    } else {
      return {
        'en_attente': `${baseClasses} bg-yellow-100 text-yellow-800`,
        'accepte': `${baseClasses} bg-green-100 text-green-800`,
        'rejete': `${baseClasses} bg-red-100 text-red-800`
      }[status];
    }
  };

  const getStatusIcon = (status) => {
    return {
      'en_attente': <FaClock className="mr-1" />,
      'accepte': <FaCheck className="mr-1" />,
      'rejete': <FaTimes className="mr-1" />
    }[status];
  };

  // Composant pour simuler une nouvelle candidature (pour démo)
  const DemoCandidatureForm = () => {
    const [formData, setFormData] = useState({
      nomEntreprise: '',
      email: '',
      telephone: '',
      titreOffre: ''
    });

    const handleSubmit = (e) => {
      e.preventDefault();
      if (formData.nomEntreprise && formData.titreOffre) {
        handleNewCandidature(formData);
        setFormData({
          nomEntreprise: '',
          email: '',
          telephone: '',
          titreOffre: ''
        });
      }
    };

    return (
      <motion.div 
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className={`p-6 rounded-xl shadow-lg mb-8 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}
      >
        <h3 className="text-xl font-semibold mb-4">Simuler une candidature d'entreprise</h3>
        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block mb-1">Nom de l'entreprise</label>
            <input
              type="text"
              value={formData.nomEntreprise}
              onChange={(e) => setFormData({...formData, nomEntreprise: e.target.value})}
              className={`w-full p-2 rounded border ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
              required
            />
          </div>
          <div>
            <label className="block mb-1">Titre de l'offre</label>
            <input
              type="text"
              value={formData.titreOffre}
              onChange={(e) => setFormData({...formData, titreOffre: e.target.value})}
              className={`w-full p-2 rounded border ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
              required
            />
          </div>
          <div>
            <label className="block mb-1">Email</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              className={`w-full p-2 rounded border ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
            />
          </div>
          <div>
            <label className="block mb-1">Téléphone</label>
            <input
              type="tel"
              value={formData.telephone}
              onChange={(e) => setFormData({...formData, telephone: e.target.value})}
              className={`w-full p-2 rounded border ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
            />
          </div>
          <div className="md:col-span-2">
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded"
            >
              Ajouter une candidature de test
            </motion.button>
          </div>
        </form>
      </motion.div>
    );
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 ${darkMode ? 'bg-gray-900 text-gray-100' : 'bg-gray-50 text-gray-900'}`}>
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <motion.h1 
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            className="text-3xl font-bold"
          >
            Candidatures des entreprises
          </motion.h1>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className={`px-4 py-2 rounded-lg ${darkMode ? 'bg-yellow-400 text-gray-900' : 'bg-gray-800 text-white'}`}
          >
            {darkMode ? '☀️ Light Mode' : '🌙 Dark Mode'}
          </button>
        </div>

        {/* Formulaire de démonstration - À enlever en production */}
        <DemoCandidatureForm />

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2, duration: 0.5 }}
          className={`p-6 rounded-xl shadow-lg mb-8 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}
        >
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaSearch className="text-gray-400" />
              </div>
              <input
                type="text"
                placeholder="Rechercher entreprise ou offre..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className={`pl-10 w-full p-3 rounded-lg border ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
              />
            </div>

            <select
              value={selectedStatus}
              onChange={(e) => setSelectedStatus(e.target.value)}
              className={`p-3 rounded-lg border ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'}`}
            >
              <option value="tous">Tous les statuts</option>
              <option value="en_attente">En attente</option>
              <option value="accepte">Accepté</option>
              <option value="rejete">Rejeté</option>
            </select>

            <div className="flex items-center justify-end">
              <span className={`px-3 py-1 rounded-full ${darkMode ? 'bg-blue-900 text-blue-100' : 'bg-blue-100 text-blue-800'}`}>
                {filteredCandidatures.length} candidature(s)
              </span>
            </div>
          </div>
        </motion.div>

        <AnimatePresence>
          {filteredCandidatures.length > 0 ? (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
              className="space-y-4"
            >
              {filteredCandidatures.map((candidature) => (
                <motion.div
                  key={candidature.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                  className={`p-6 rounded-xl shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'}`}
                >
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    <div className="space-y-2">
                      <div className="flex items-center">
                        <FaBuilding className="mr-2 text-blue-500" />
                        <h3 className="text-xl font-semibold">{candidature.nomEntreprise}</h3>
                      </div>
                      {candidature.email && (
                        <div className="flex items-center">
                          <FaEnvelope className="mr-2 text-blue-500" />
                          <span>{candidature.email}</span>
                        </div>
                      )}
                      {candidature.telephone && (
                        <div className="flex items-center">
                          <FaPhone className="mr-2 text-blue-500" />
                          <span>{candidature.telephone}</span>
                        </div>
                      )}
                    </div>

                    <div className="space-y-2">
                      <h4 className="font-medium">Offre postulée:</h4>
                      <p className="text-lg font-semibold">{candidature.titreOffre}</p>
                      <div className="flex items-center">
                        <FaFileAlt className="mr-2 text-blue-500" />
                        <span className={`${darkMode ? 'text-blue-300' : 'text-blue-600'}`}>
                          Dossier complet disponible
                        </span>
                      </div>
                    </div>

                    <div className="flex flex-col justify-between">
                      <div className="flex justify-between items-center mb-4">
                        <span className={getStatusStyle(candidature.status)}>
                          {getStatusIcon(candidature.status)}
                          {candidature.status === 'en_attente' ? 'En attente' : 
                          candidature.status === 'accepte' ? 'Accepté' : 'Rejeté'}
                        </span>
                        <span className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                          {candidature.date}
                        </span>
                      </div>

                      <div className="flex space-x-2">
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => updateCandidatureStatus(candidature.id, 'accepte')}
                          className="px-4 py-2 bg-green-600 text-white rounded-lg flex-1"
                          disabled={candidature.status === 'accepte'}
                        >
                          Valider
                        </motion.button>
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => updateCandidatureStatus(candidature.id, 'rejete')}
                          className="px-4 py-2 bg-red-600 text-white rounded-lg flex-1"
                          disabled={candidature.status === 'rejete'}
                        >
                          Rejeter
                        </motion.button>
                      </div>
                    </div>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          ) : (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
              className={`p-8 text-center rounded-xl ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg`}
            >
              <p className="text-xl">Aucune candidature d'entreprise trouvée</p>
              <p className={`mt-2 ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                Les candidatures apparaîtront ici quand les entreprises postuleront à vos offres
              </p>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default GestionCandidatures;
