import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { FaSpinner, FaPaperPlane } from 'react-icons/fa';

const Postuler = () => {
  const { offerId } = useParams();
  const navigate = useNavigate();
  const [offer, setOffer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchOffer = async () => {
      try {
        const res = await axios.get(`/api/offres/${offerId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setOffer(res.data.offre || res.data);
        setLoading(false);
      } catch (err) {
        setError("Impossible de charger l'offre.");
        setLoading(false);
      }
    };

    fetchOffer();
  }, [offerId, token]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError('Veuillez sélectionner un fichier.');
      return;
    }

    const formData = new FormData();
    formData.append('offerId', offerId);
    formData.append('file', file);

    try {
      const res = await axios.post('/api/candidatures', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage(res.data.message);
      setError('');
      navigate('/dashboard-entreprise');
    } catch (err) {
      setError(err.response?.data?.error || 'Erreur lors de la soumission.');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <FaSpinner className="animate-spin text-4xl text-blue-600" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center mt-10 text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white shadow-md rounded-lg">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Postuler à l'offre</h2>
      <div className="mb-6">
        <h3 className="text-xl font-semibold">{offer.title}</h3>
        <p className="text-gray-600">{offer.description}</p>
        <p className="text-sm text-gray-400 mt-2">Publié le : {new Date(offer.createdAt).toLocaleDateString()}</p>
      </div>

      <form onSubmit={handleSubmit}>
        <label className="block text-gray-700 mb-2 font-medium">
          Joindre un document (PDF, doc...) :
        </label>
        <input
          type="file"
          accept=".pdf,.doc,.docx"
          onChange={handleFileChange}
          className="mb-4 border border-gray-300 p-2 w-full rounded"
        />
        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded flex items-center gap-2"
        >
          <FaPaperPlane /> Envoyer
        </button>
      </form>

      {message && (
        <p className="mt-4 text-green-600 font-medium">{message}</p>
      )}
      {error && (
        <p className="mt-4 text-red-600 font-medium">{error}</p>
      )}
    </div>
  );
};

export default Postuler;
