import React, { useEffect, useState } from 'react';
import { FaBullseye, FaFileAlt, FaUsers, FaSearch, FaHandshake, FaChartLine, FaGlobe } from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion';
import { ChevronLeftIcon, ChevronRightIcon } from "@heroicons/react/24/solid";

// Animations
const container = {
  hidden: { opacity: 0 },
  show: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1
    }
  }
};

const item = {
  hidden: { opacity: 0, y: 20 },
  show: { opacity: 1, y: 0, transition: { duration: 0.5 } }
};

const fadeIn = {
  hidden: { opacity: 0 },
  show: { opacity: 1, transition: { duration: 0.8 } }
};

const slideUp = {
  hidden: { opacity: 0, y: 30 },
  show: { opacity: 1, y: 0, transition: { duration: 0.6 } }
};

const floatAnimation = {
  y: [0, -15, 0],
  transition: {
    duration: 4,
    repeat: Infinity,
    repeatType: "loop",
    ease: "easeInOut"
  }
};

const Home = () => {
  const navigate = useNavigate();
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchPublishedOffers = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/offres/publiees');
        setOffers(response.data);
      } catch (error) {
        console.error("Erreur lors du chargement des offres", error);
      } finally {
        setLoading(false);
      }
    };
    fetchPublishedOffers();
  }, []);

  const filteredOffers = offers.filter(offer =>
    offer.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    offer.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalOffers = offers.length;
  const urgentOffers = offers.filter(offer => offer.statut === 'urgent').length;
  const uniqueCategories = [...new Set(offers.map(offer => offer.category || 'Autre'))];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 flex flex-col">
      {/* Header */}
      <motion.header 
        initial="hidden"
        animate="show"
        variants={fadeIn}
        className="p-8 text-center mt-6"
      >
        <motion.h1 
          variants={slideUp}
          className="text-4xl font-bold mb-4 text-blue-800"
        >
          Plateforme des Marchés Publics
        </motion.h1>
        <motion.p 
          variants={slideUp}
          className="text-lg max-w-3xl mx-auto text-blue-600"
        >
          Transparence et efficacité dans la gestion des marchés publics
        </motion.p>
      </motion.header>

   {/* Section Missions Ministérielles - Version Animée */}
<motion.section 
  initial="hidden"
  animate="show"
  variants={container}
  className="py-16 px-6 md:px-20 bg-blue-50"
>
  <motion.h2 
    variants={item}
    className="text-3xl font-bold text-center mb-12 text-blue-800 border-b-2 border-blue-200 pb-4"
  >
    Nos Missions Prioritaires
  </motion.h2>
  
  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 max-w-6xl mx-auto">
    {/* Case 1 - Régulation */}
    <motion.div 
      variants={item}
      className="bg-white p-8 rounded-lg shadow-sm border-l-4 border-blue-600 hover:shadow-md transition-all h-full flex flex-col"
      whileHover={{ y: -5 }}
    >
      <motion.div 
        className="bg-blue-100 w-14 h-14 rounded-lg flex items-center justify-center mb-6 text-blue-600"
        animate={{
          rotate: [0, 5, -5, 0],
          transition: { 
            duration: 3,
            repeat: Infinity,
            repeatType: "mirror"
          }
        }}
      >
        <motion.svg 
          className="w-8 h-8" 
          fill="none" 
          stroke="currentColor" 
          viewBox="0 0 24 24"
          animate={{
            scale: [1, 1.1, 1],
            transition: { 
              duration: 4,
              repeat: Infinity 
            }
          }}
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
        </motion.svg>
      </motion.div>
      <h3 className="text-xl font-semibold mb-3 text-gray-800">Régulation des Marchés</h3>
      <p className="text-gray-600 flex-grow">
        Supervision et contrôle des procédures d'appels d'offres publics pour garantir l'équité
      </p>
      <motion.a 
        href="/regulation" 
        className="mt-4 text-blue-600 hover:text-blue-800 font-medium inline-flex items-center"
        whileHover={{ x: 3 }}
      >
        En savoir plus <ChevronRightIcon className="w-4 h-4 ml-1" />
      </motion.a>
    </motion.div>

    {/* Case 2 - Transparence */}
    <motion.div 
      variants={item}
      className="bg-white p-8 rounded-lg shadow-sm border-l-4 border-green-600 hover:shadow-md transition-all h-full flex flex-col"
      whileHover={{ y: -5 }}
    >
      <motion.div 
        className="bg-green-100 w-14 h-14 rounded-lg flex items-center justify-center mb-6 text-green-600"
        animate={{
          y: [0, -3, 0],
          transition: { 
            duration: 3,
            repeat: Infinity
          }
        }}
      >
        <motion.svg 
          className="w-8 h-8" 
          fill="none" 
          stroke="currentColor" 
          viewBox="0 0 24 24"
          animate={{
            opacity: [1, 0.8, 1],
            transition: { 
              duration: 3,
              repeat: Infinity
            }
          }}
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
        </motion.svg>
      </motion.div>
      <h3 className="text-xl font-semibold mb-3 text-gray-800">Transparence</h3>
      <p className="text-gray-600 flex-grow">
        Publication systématique des données essentielles pour une gouvernance ouverte
      </p>
      <motion.a 
        href="/transparence" 
        className="mt-4 text-green-600 hover:text-green-800 font-medium inline-flex items-center"
        whileHover={{ x: 3 }}
      >
        En savoir plus <ChevronRightIcon className="w-4 h-4 ml-1" />
      </motion.a>
    </motion.div>

    {/* Case 3 - Contrôle */}
    <motion.div 
      variants={item}
      className="bg-white p-8 rounded-lg shadow-sm border-l-4 border-red-600 hover:shadow-md transition-all h-full flex flex-col"
      whileHover={{ y: -5 }}
    >
      <motion.div 
        className="bg-red-100 w-14 h-14 rounded-lg flex items-center justify-center mb-6 text-red-600"
        animate={{
          rotate: [0, 360],
          transition: { 
            duration: 8,
            repeat: Infinity,
            ease: "linear"
          }
        }}
      >
        <motion.svg 
          className="w-8 h-8" 
          fill="none" 
          stroke="currentColor" 
          viewBox="0 0 24 24"
          animate={{
            rotate: [0, -360],
            transition: { 
              duration: 8,
              repeat: Infinity,
              ease: "linear"
            }
          }}
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
        </motion.svg>
      </motion.div>
      <h3 className="text-xl font-semibold mb-3 text-gray-800">Contrôle des Procédures</h3>
      <p className="text-gray-600 flex-grow">
        Vérification systématique du respect des normes et réglementations en vigueur
      </p>
      <motion.a 
        href="/controle" 
        className="mt-4 text-red-600 hover:text-red-800 font-medium inline-flex items-center"
        whileHover={{ x: 3 }}
      >
        En savoir plus <ChevronRightIcon className="w-4 h-4 ml-1" />
      </motion.a>
    </motion.div>

    {/* Case 4 - Formation */}
    <motion.div 
      variants={item}
      className="bg-white p-8 rounded-lg shadow-sm border-l-4 border-purple-600 hover:shadow-md transition-all h-full flex flex-col"
      whileHover={{ y: -5 }}
    >
      <motion.div 
        className="bg-purple-100 w-14 h-14 rounded-lg flex items-center justify-center mb-6 text-purple-600"
        animate={{
          scale: [1, 1.05, 1],
          transition: { 
            duration: 3,
            repeat: Infinity
          }
        }}
      >
        <motion.svg 
          className="w-8 h-8" 
          fill="none" 
          stroke="currentColor" 
          viewBox="0 0 24 24"
          animate={{
            y: [0, -2, 0],
            transition: { 
              duration: 3,
              repeat: Infinity
            }
          }}
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.247 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
        </motion.svg>
      </motion.div>
      <h3 className="text-xl font-semibold mb-3 text-gray-800">Formation des Acteurs</h3>
      <p className="text-gray-600 flex-grow">
        Programme national de formation pour les acheteurs publics et soumissionnaires
      </p>
      <motion.a 
        href="/formation" 
        className="mt-4 text-purple-600 hover:text-purple-800 font-medium inline-flex items-center"
        whileHover={{ x: 3 }}
      >
        En savoir plus <ChevronRightIcon className="w-4 h-4 ml-1" />
      </motion.a>
    </motion.div>
  </div>
</motion.section>
      {/* Section À propos */}
    <motion.section 
  initial="hidden"
  animate="show"
  variants={container}
  className="py-12 px-6 md:px-20 bg-white bg-opacity-50 backdrop-blur-sm my-8 rounded-xl max-w-6xl mx-auto"
>
  <motion.h2 
    variants={item}
    className="text-3xl font-bold text-center mb-8 text-blue-800"
  >
    À Propos de Notre Plateforme
  </motion.h2>
  
  <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
    <motion.div 
      variants={item}
      className="bg-white p-6 rounded-lg shadow-md border border-blue-100"
    >
      <motion.div
        animate={{
          y: [0, -10, 0],
          transition: {
            duration: 4,
            repeat: Infinity,
            repeatType: "loop",
            ease: "easeInOut"
          }
        }}
        className="flex justify-center mb-4"
      >
        <FaHandshake className="text-4xl text-blue-600" />
      </motion.div>
      <h3 className="text-xl font-semibold text-center mb-3 text-blue-700">Transparence</h3>
      <p className="text-gray-600 text-center">
        Nous garantissons une gestion transparente et équitable de tous les marchés publics.
      </p>
    </motion.div>
    
    <motion.div 
      variants={item}
      className="bg-white p-6 rounded-lg shadow-md border border-blue-100"
    >
      <motion.div
        animate={{
          y: [0, -10, 0],
          transition: {
            duration: 4,
            repeat: Infinity,
            repeatType: "loop",
            ease: "easeInOut",
            delay: 0.5
          }
        }}
        className="flex justify-center mb-4"
      >
        <FaChartLine className="text-4xl text-blue-600" />
      </motion.div>
      <h3 className="text-xl font-semibold text-center mb-3 text-blue-700">Efficacité</h3>
      <p className="text-gray-600 text-center">
        Optimisation des processus pour une gestion plus rapide et plus efficace des appels d'offres.
      </p>
    </motion.div>
    
    <motion.div 
      variants={item}
      className="bg-white p-6 rounded-lg shadow-md border border-blue-100"
    >
      <motion.div
        animate={{
          y: [0, -10, 0],
          transition: {
            duration: 4,
            repeat: Infinity,
            repeatType: "loop",
            ease: "easeInOut",
            delay: 1
          }
        }}
        className="flex justify-center mb-4"
      >
        <FaGlobe className="text-4xl text-blue-600" />
      </motion.div>
      <h3 className="text-xl font-semibold text-center mb-3 text-blue-700">Accessibilité</h3>
      <p className="text-gray-600 text-center">
        Plateforme accessible à toutes les entreprises, favorisant une concurrence saine et ouverte.
      </p>
    </motion.div>
  </div>
  
  <motion.div 
    variants={item}
    className="mt-10 text-center max-w-3xl mx-auto"
  >
    <p className="text-blue-700 mb-6">
      Notre plateforme a été créée pour moderniser la gestion des marchés publics, en mettant à disposition des outils numériques performants qui simplifient les processus tout en garantissant l'intégrité et la transparence des transactions.
    </p>
  </motion.div>
</motion.section>

    {/* Section Offres et Services - Version Ministérielle */}
<div className="bg-white">
  {/* Section des Dernières Offres */}
  <section className="py-12 px-6 md:px-20 bg-blue-50">
    <motion.div
      initial="hidden"
      animate="show"
      variants={slideUp}
      className="max-w-7xl mx-auto"
    >
      </motion.div>
      {/* Section des Procédures en Cours */}
<section className="py-12 px-6 md:px-20 bg-white">
  <div className="max-w-7xl mx-auto">
    <motion.div
      initial="hidden"
      animate="show"
      variants={slideUp}
      className="text-center mb-12"
    >
      <h2 className="text-3xl font-bold text-blue-800 mb-3">
        Procédures Contractuelles en Cours
      </h2>
      <p className="text-lg text-gray-600 max-w-3xl mx-auto">
        Liste des marchés publics actuellement en phase de consultation
      </p>
    </motion.div>

    {loading ? (
      <div className="flex justify-center items-center h-40">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ repeat: Infinity, duration: 1, ease: "linear" }}
          className="w-12 h-12 border-4 border-blue-500 border-t-transparent rounded-full"
        />
      </div>
    ) : filteredOffers.length === 0 ? (
      <motion.div
        initial="hidden"
        animate="show"
        variants={fadeIn}
        className="text-center py-12 bg-gray-50 rounded-lg"
      >
        <p className="text-lg text-gray-600">
          {searchTerm 
            ? "Aucune procédure ne correspond à vos critères" 
            : "Aucune consultation en cours actuellement"}
        </p>
      </motion.div>
    ) : (
      <motion.div
        initial="hidden"
        animate="show"
        variants={container}
        className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
      >
        {filteredOffers.map((offer) => (
          <motion.div
            key={offer.id}
            variants={item}
            whileHover={{ y: -5 }}
          >
            <div 
              className="bg-white border border-gray-200 rounded-lg shadow-sm hover:shadow-md transition-all h-full flex flex-col overflow-hidden"
              onClick={() => navigate(`/marches/${offer.id}`)}
            >
              <div className="p-6 flex-grow">
                <div className="flex justify-between items-start mb-3">
                  <h3 className="text-xl font-semibold text-gray-800">
                    {offer.title}
                  </h3>
                  {offer.statut === 'urgent' && (
                    <span className="bg-red-100 text-red-800 text-xs px-2 py-1 rounded-full">
                      Prioritaire
                    </span>
                  )}
                </div>

                <div className="flex flex-wrap gap-2 mb-4">
                  <span className="bg-blue-50 text-blue-700 px-3 py-1 rounded-full text-xs font-medium">
                    {offer.category || 'Tous secteurs'}
                  </span>
                  <span className="bg-gray-100 text-gray-700 px-3 py-1 rounded-full text-xs font-medium">
                    Réf: {offer.reference}
                  </span>
                </div>

                <p className="text-gray-600 text-sm mb-4 line-clamp-3">
                  {offer.description}
                </p>
              </div>

              <div className="px-6 py-4 bg-gray-50 border-t border-gray-100">
                <div className="flex justify-between items-center">
                  <div className="text-sm text-gray-600">
                    <span className="font-medium">Clôture:</span> {new Date(offer.deadline).toLocaleDateString()}
                  </div>
                  <button className="text-blue-600 hover:text-blue-800 text-sm font-medium flex items-center">
                    Détails <ChevronRightIcon className="w-4 h-4 ml-1" />
                  </button>
                </div>
              </div>
            </div>
          </motion.div>
        ))}
      </motion.div>
    )}
  </div>
</section>
  </section>

  {/* Section Services Ministériels */}
  <section className="py-16 px-6 md:px-20 bg-white border-t border-gray-100">
    <motion.div
      initial="hidden"
      animate="show"
      variants={container}
      className="max-w-7xl mx-auto"
    >
      <motion.h2 
        variants={item}
        className="text-3xl font-bold text-center mb-12 text-blue-800"
      >
        Nos Services en Ligne
      </motion.h2>
      
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {/* Service 1 */}
        <motion.div
          variants={item}
          whileHover={{ y: -5 }}
          className="bg-white p-8 rounded-xl shadow-sm border border-gray-200 hover:shadow-md transition-all text-center"
        >
          <div className="bg-blue-50 w-20 h-20 mx-auto rounded-full flex items-center justify-center mb-6">
            <motion.div
              animate={{
                scale: [1, 1.1, 1],
                transition: { duration: 3, repeat: Infinity }
              }}
            >
              <FaFileAlt className="text-blue-600 text-3xl" />
            </motion.div>
          </div>
          <h3 className="text-xl font-semibold mb-3 text-gray-800">Dépôt Electronique</h3>
          <p className="text-gray-600 mb-6">
            Soumettez vos offres en ligne de manière sécurisée et simplifiée
          </p>
          <Link 
            to="/depot" 
            className="inline-flex items-center text-blue-600 hover:text-blue-800 font-medium"
          >
            Accéder au service <ChevronRightIcon className="w-4 h-4 ml-2" />
          </Link>
        </motion.div>

        {/* Service 2 */}
        <motion.div
          variants={item}
          whileHover={{ y: -5 }}
          className="bg-white p-8 rounded-xl shadow-sm border border-gray-200 hover:shadow-md transition-all text-center"
        >
          <div className="bg-blue-50 w-20 h-20 mx-auto rounded-full flex items-center justify-center mb-6">
            <motion.div
              animate={{
                rotate: [0, 10, -10, 0],
                transition: { duration: 4, repeat: Infinity }
              }}
            >
              <FaChartLine className="text-blue-600 text-3xl" />
            </motion.div>
          </div>
          <h3 className="text-xl font-semibold mb-3 text-gray-800">Suivi des Marchés</h3>
          <p className="text-gray-600 mb-6">
            Visualisez l'avancement des procédures en temps réel
          </p>
          <Link 
            to="/suivi" 
            className="inline-flex items-center text-blue-600 hover:text-blue-800 font-medium"
          >
            Accéder au service <ChevronRightIcon className="w-4 h-4 ml-2" />
          </Link>
        </motion.div>

        {/* Service 3 */}
        <motion.div
          variants={item}
          whileHover={{ y: -5 }}
          className="bg-white p-8 rounded-xl shadow-sm border border-gray-200 hover:shadow-md transition-all text-center"
        >
          <div className="bg-blue-50 w-20 h-20 mx-auto rounded-full flex items-center justify-center mb-6">
            <motion.div
              animate={{
                y: [0, -5, 0],
                transition: { duration: 3, repeat: Infinity }
              }}
            >
              <FaUsers className="text-blue-600 text-3xl" />
            </motion.div>
          </div>
          <h3 className="text-xl font-semibold mb-3 text-gray-800">Espace Professionnel</h3>
          <p className="text-gray-600 mb-6">
            Gestion centralisée de vos dossiers et documents
          </p>
          <Link 
            to="/espace-pro" 
            className="inline-flex items-center text-blue-600 hover:text-blue-800 font-medium"
          >
            Accéder au service <ChevronRightIcon className="w-4 h-4 ml-2" />
          </Link>
        </motion.div>
      </div>
    </motion.div>
  </section>
</div>
      {/* Footer */}
      <motion.footer 
        initial="hidden"
        animate="show"
        variants={fadeIn}
        className="bg-blue-800 text-white py-10 mt-auto"
      >
        <div className="max-w-5xl mx-auto px-4 text-center">
          <h2 className="text-xl font-semibold mb-4">Plateforme des Marchés Publics</h2>
          <p className="text-sm text-blue-200 mb-4">
            Un outil moderne pour une gestion transparente des marchés publics
          </p>
          <div className="flex justify-center gap-6 text-blue-300 text-xl mb-6">
            <motion.a 
              whileHover={{ scale: 1.2, color: "#ffffff" }}
              href="#" 
              aria-label="Facebook" 
              className="hover:text-white"
            >
              <i className="fab fa-facebook-f"></i>
            </motion.a>
            <motion.a 
              whileHover={{ scale: 1.2, color: "#ffffff" }}
              href="#" 
              aria-label="Twitter" 
              className="hover:text-white"
            >
              <i className="fab fa-twitter"></i>
            </motion.a>
            <motion.a 
              whileHover={{ scale: 1.2, color: "#ffffff" }}
              href="#" 
              aria-label="LinkedIn" 
              className="hover:text-white"
            >
              <i className="fab fa-linkedin-in"></i>
            </motion.a>
          </div>
          <p className="text-xs text-blue-300">© {new Date().getFullYear()} Ministère des Finances – Tous droits réservés</p>
        </div>
      </motion.footer>
    </div>
  );
};

// Carte Offre
const OfferCard = ({ offer, onClick }) => (
  <motion.div 
    whileHover={{ 
      scale: 1.02,
      boxShadow: "0 10px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)"
    }}
    onClick={onClick}
    className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow cursor-pointer border border-blue-100"
  >
    <div className="flex justify-between items-start mb-3">
      <h3 className="text-lg font-semibold text-blue-800 line-clamp-2">{offer.title}</h3>
      {offer.statut === 'urgent' && (
        <motion.span 
          animate={{ 
            scale: [1, 1.1, 1],
            transition: { repeat: Infinity, duration: 1.5 }
          }}
          className="bg-red-100 text-red-800 text-xs px-2 py-1 rounded-full"
        >
          Urgent
        </motion.span>
      )}
    </div>
    <p className="text-gray-600 text-sm mb-4 line-clamp-3">{offer.description}</p>
    <div className="flex justify-between items-center text-xs">
      <span className="text-blue-600">Date limite : {new Date(offer.deadline).toLocaleDateString()}</span>
      <span className="text-gray-500">Ref : {offer.reference || "N/A"}</span>
    </div>
  </motion.div>
);

// Carte Service
const ServiceCard = ({ icon, title, description, link }) => (
  <Link to={link}>
    <motion.div
      whileHover={{ 
        y: -5,
        boxShadow: "0 10px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)"
      }}
      className="bg-white rounded-xl shadow-md p-6 flex flex-col items-center text-center hover:shadow-lg transition-transform max-w-xs h-full"
    >
      <motion.div 
        whileHover={{ scale: 1.1 }}
        className="mb-4 p-3 bg-blue-50 rounded-full"
      >
        {icon}
      </motion.div>
      <h3 className="text-xl font-semibold mb-2 text-blue-700">{title}</h3>
      <p className="text-gray-600 text-sm">{description}</p>
    </motion.div>
  </Link>
);

export default Home;
