import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { Bar, Doughnut, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
  ArcElement,
  LineElement,
  PointElement,
} from "chart.js";
import { useTheme } from "../../context/ThemeContext";

ChartJS.register(
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
  ArcElement,
  LineElement,
  PointElement
);

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState({
    stats: {
      totalOffres: 0,
      totalCandidatures: 0,
      candidaturesEnAttente: 0,
      candidaturesValides: 0,
      candidaturesRejetees: 0
    },
    offers: [],
    candidatures: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { darkMode } = useTheme();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      
      const response = await axios.get("http://localhost:3000/api/dashboard/entreprise", {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      setDashboardData(response.data);
      setError(null);
    } catch (error) {
      console.error("Erreur lors du chargement du dashboard :", error);
      setError("Impossible de charger les données du dashboard");
    } finally {
      setLoading(false);
    }
  };

  const doughnutData = {
    labels: ["Offres", "Candidatures"],
    datasets: [
      {
        label: "Répartition",
        data: [offresCount, candidaturesCount],
        backgroundColor: ["#3b82f6", "#facc15"],
      },
    ],
  };

  const barData = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul"],
    datasets: [
      {
        label: "Offres",
        backgroundColor: "#3b82f6",
        data: [5, 10, 15, 10, 25, 30, offresCount],
      },
      {
        label: "Candidatures",
        backgroundColor: "#facc15",
        data: [2, 8, 10, 15, 20, 25, candidaturesCount],
      },
    ],
  };

  return (
    <div className="p-6 bg-gray-100 dark:bg-gray-900 min-h-screen">
      <h1 className="text-3xl font-bold text-gray-800 dark:text-white mb-6">Tableau de bord</h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 mb-6">
        <div className="bg-blue-500 text-white p-5 rounded-xl shadow-md">
          <h2 className="text-xl">Offres</h2>
          <p className="text-3xl font-bold">{offresCount}</p>
        </div>

        <div className="bg-yellow-400 text-white p-5 rounded-xl shadow-md">
          <h2 className="text-xl">Candidatures</h2>
          <p className="text-3xl font-bold">{candidaturesCount}</p>
        </div>

        <div className="bg-green-500 text-white p-5 rounded-xl shadow-md">
          <h2 className="text-xl">Taux de Réussite</h2>
          <p className="text-3xl font-bold">{offresCount > 0 ? Math.round((candidaturesCount / offresCount) * 100) : 0}%</p>
        </div>

        <div className="bg-red-500 text-white p-5 rounded-xl shadow-md">
          <h2 className="text-xl">Visiteurs uniques</h2>
          <p className="text-3xl font-bold">65</p>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white dark:bg-gray-800 p-4 rounded-xl shadow">
          <h3 className="text-lg font-semibold text-gray-800 dark:text-white mb-2">Répartition</h3>
          <Doughnut data={doughnutData} />
        </div>

        <div className="bg-white dark:bg-gray-800 p-4 rounded-xl shadow">
          <h3 className="text-lg font-semibold text-gray-800 dark:text-white mb-2">Évolution</h3>
          <Bar data={barData} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
