import React from 'react';
import { Link } from 'react-router-dom';
import {
  FaChartLine,
  FaPlusCircle,
  FaUsers,
  FaArrowRight,
} from 'react-icons/fa';
import { motion } from 'framer-motion';

const cards = [
  {
    path: "/ministere/statistiques",
    icon: <FaChartLine className="text-indigo-600 dark:text-indigo-300 text-4xl mb-2" />,
    title: "Statistiques",
    description: "Visualiser les données des marchés publics"
  },
  {
    path: "/ministere/creeroffre",
    icon: <FaPlusCircle className="text-green-600 dark:text-green-400 text-4xl mb-2" />,
    title: "Créer Offre",
    description: "Publier un nouveau marché public"
  },
  {
    path: "/ministere/gestioncandidatures",
    icon: <FaUsers className="text-purple-600 dark:text-purple-300 text-4xl mb-2" />,
    title: "Candidatures",
    description: "Gérer les soumissions reçues"
  }
];

const DashboardMinistere = () => {
  return (
    <div className="min-h-screen px-6 py-10 bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
      <h1 className="text-3xl font-bold text-center text-gray-800 dark:text-white mb-12">
        Tableau de bord Ministère
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {cards.map(({ path, icon, title, description }) => (
          <motion.div
            key={path}
            whileHover={{ scale: 1.05 }}
            className="bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6 flex flex-col items-center text-center hover:shadow-xl transition"
          >
            {icon}
            <h2 className="text-xl font-semibold text-gray-800 dark:text-white">{title}</h2>
            <p className="text-gray-600 dark:text-gray-300 text-sm mt-2">{description}</p>
            <Link
              to={path}
              className="mt-4 inline-flex items-center text-blue-600 dark:text-blue-400 font-medium hover:underline"
            >
              Accéder <FaArrowRight className="ml-1" />
            </Link>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default DashboardMinistere;
