import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

const DashboardEntreprise = ({ token }) => {
  const [offres, setOffres] = useState([]);
  const [message, setMessage] = useState('');
  const [selectedId, setSelectedId] = useState(null);

  useEffect(() => {
    fetch('http://localhost:3000/api/dashboard/entreprise', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => res.json())
      .then(data => setOffres(data.offres || []));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:3000/api/candidatures', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ offerId: selectedId, message })
    })
      .then(res => res.json())
      .then(() => {
        setMessage('');
        setSelectedId(null);
        alert('Candidature envoyée !');
      });
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Offres disponibles</h1>
      <div className="grid md:grid-cols-2 gap-4">
        {offres.map(offre => (
          <motion.div
            key={offre.id}
            whileHover={{ scale: 1.03 }}
            className="bg-white p-4 rounded shadow border"
          >
            <h2 className="font-semibold text-indigo-700">{offre.title}</h2>
            <p className="text-sm text-gray-600">{offre.description}</p>
            <form onSubmit={handleSubmit} className="mt-2 space-y-2">
              <textarea
                value={selectedId === offre.id ? message : ''}
                onChange={(e) => {
                  setSelectedId(offre.id);
                  setMessage(e.target.value);
                }}
                rows="2"
                className="w-full border rounded p-2"
                placeholder="Votre message..."
              ></textarea>
              <button type="submit" className="bg-blue-600 text-white px-3 py-1 rounded">Postuler</button>
            </form>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default DashboardEntreprise;
