import React, { useEffect, useState } from 'react';
import { FaBullseye, FaFileAlt, FaUsers, FaSearch, FaHandshake, FaChartLine, FaGlobe } from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion';
import { ChevronLeftIcon, ChevronRightIcon, SunIcon, MoonIcon } from "@heroicons/react/24/solid";

// Animations
const container = {
  hidden: { opacity: 0 },
  show: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1
    }
  }
};

const item = {
  hidden: { opacity: 0, y: 20 },
  show: { opacity: 1, y: 0, transition: { duration: 0.5 } }
};

const fadeIn = {
  hidden: { opacity: 0 },
  show: { opacity: 1, transition: { duration: 0.8 } }
};

const slideUp = {
  hidden: { opacity: 0, y: 30 },
  show: { opacity: 1, y: 0, transition: { duration: 0.6 } }
};

const floatAnimation = {
  y: [0, -15, 0],
  transition: {
    duration: 4,
    repeat: Infinity,
    repeatType: "loop",
    ease: "easeInOut"
  }
};

const Home = () => {
  const navigate = useNavigate();
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [darkMode, setDarkMode] = useState(false);

  // Dark mode toggle
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    if (!darkMode) {
      document.documentElement.classList.add('dark');
      localStorage.theme = 'dark';
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.theme = 'light';
    }
  };

  useEffect(() => {
    // Check for dark mode preference
    if (localStorage.theme === 'dark' || (!('theme' in localStorage) && window.matchMedia('(prefers-color-scheme: dark)').matches)) {
      setDarkMode(true);
      document.documentElement.classList.add('dark');
    }

    const fetchPublishedOffers = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/offres/publiees');
        setOffers(response.data);
      } catch (error) {
        console.error("Erreur lors du chargement des offres", error);
      } finally {
        setLoading(false);
      }
    };
    fetchPublishedOffers();
  }, []);

  const filteredOffers = offers.filter(offer =>
    offer.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    offer.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalOffers = offers.length;
  const urgentOffers = offers.filter(offer => offer.statut === 'urgent').length;
  const uniqueCategories = [...new Set(offers.map(offer => offer.category || 'Autre'))];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 dark:from-gray-900 dark:to-gray-800 flex flex-col transition-colors duration-300">
      {/* Header avec bouton dark mode */}
      <motion.header 
        initial="hidden"
        animate="show"
        variants={fadeIn}
        className="p-8 text-center mt-6 relative"
      >
        <button
          onClick={toggleDarkMode}
          className="absolute top-6 right-6 p-2 rounded-full focus:outline-none"
          aria-label={darkMode ? 'DÃ©sactiver le mode sombre' : 'Activer le mode sombre'}
        >
          {darkMode ? (
            <SunIcon className="w-6 h-6 text-yellow-300" />
          ) : (
            <MoonIcon className="w-6 h-6 text-gray-700" />
          )}
        </button>

        <motion.h1 
          variants={slideUp}
          className="text-4xl font-bold mb-4 text-blue-800 dark:text-blue-400"
        >
          Plateforme des MarchÃ©s Publics
        </motion.h1>
        <motion.p 
          variants={slideUp}
          className="text-lg max-w-3xl mx-auto text-blue-600 dark:text-blue-200"
        >
          Transparence et efficacitÃ© dans la gestion des marchÃ©s publics
        </motion.p>
      </motion.header>

      {/* Barre de recherche */}
      <motion.div 
        initial="hidden"
        animate="show"
        variants={slideUp}
        transition={{ delay: 0.2 }}
        className="px-6 md:px-20 mt-8 mb-6"
      >
        <div className="relative max-w-2xl mx-auto">
          <FaSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-blue-400 dark:text-blue-300" />
          <input
            type="text"
            placeholder="Rechercher une offre..."
            className="w-full pl-12 pr-4 py-3 bg-white dark:bg-gray-800 border border-blue-200 dark:border-gray-700 rounded-full text-blue-800 dark:text-gray-200 placeholder-blue-400 dark:placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-300 dark:focus:ring-blue-500 focus:border-transparent transition-colors"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </motion.div>

      {/* Section Missions MinistÃ©rielles */}
      <motion.section 
        initial="hidden"
        animate="show"
        variants={container}
        className="py-16 px-6 md:px-20 bg-blue-50 dark:bg-gray-800/50"
      >
        <motion.h2 
          variants={item}
          className="text-3xl font-bold text-center mb-12 text-blue-800 dark:text-blue-400 border-b-2 border-blue-200 dark:border-gray-700 pb-4"
        >
          Nos Missions Prioritaires
        </motion.h2>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 max-w-6xl mx-auto">
          {/* Case 1 - RÃ©gulation */}
          <motion.div 
            variants={item}
            className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-sm border-l-4 border-blue-600 dark:border-blue-500 hover:shadow-md transition-all h-full flex flex-col"
            whileHover={{ y: -5 }}
          >
            <motion.div 
              className="bg-blue-100 dark:bg-blue-900/50 w-14 h-14 rounded-lg flex items-center justify-center mb-6 text-blue-600 dark:text-blue-400"
              animate={{
                rotate: [0, 5, -5, 0],
                transition: { 
                  duration: 3,
                  repeat: Infinity,
                  repeatType: "mirror"
                }
              }}
            >
              {/* IcÃ´ne SVG */}
            </motion.div>
            <h3 className="text-xl font-semibold mb-3 text-gray-800 dark:text-gray-200">RÃ©gulation des MarchÃ©s</h3>
            <p className="text-gray-600 dark:text-gray-300 flex-grow">
              Supervision et contrÃ´le des procÃ©dures d'appels d'offres publics pour garantir l'Ã©quitÃ©
            </p>
            <motion.a 
              href="/regulation" 
              className="mt-4 text-blue-600 dark:text-blue-400 hover:text-blue-800 dark:hover:text-blue-300 font-medium inline-flex items-center"
              whileHover={{ x: 3 }}
            >
              En savoir plus <ChevronRightIcon className="w-4 h-4 ml-1" />
            </motion.a>
          </motion.div>

          {/* ... Autres cases avec les mÃªmes adaptations ... */}
        </div>
      </motion.section>

      {/* Section Ã propos */}
      <motion.section 
        initial="hidden"
        animate="show"
        variants={container}
        className="py-12 px-6 md:px-20 bg-white dark:bg-gray-800 bg-opacity-50 dark:bg-opacity-50 backdrop-blur-sm my-8 rounded-xl max-w-6xl mx-auto"
      >
        {/* ... Contenu adaptÃ© pour le dark mode ... */}
      </motion.section>

      {/* Section ProcÃ©dures en Cours */}
      <section className="py-12 px-6 md:px-20 bg-white dark:bg-gray-900">
        <div className="max-w-7xl mx-auto">
          <motion.div
            initial="hidden"
            animate="show"
            variants={slideUp}
