import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthC';
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000/api';

const DashboardMinistere = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState({
    stats: null,
    recentOffers: [],
    candidatures: [],
    isLoading: true,
    error: null
  });

  // Composant StatsCard int√©gr√©
  const StatsCard = ({ title, value, icon }) => (
    <div className="bg-white p-6 rounded-lg shadow">
      <div className="flex items-center">
        <span className="text-2xl mr-3">{icon}</span>
        <div>
          <p className="text-gray-500 text-sm">{title}</p>
          <p className="text-2xl font-bold">{value || 0}</p>
        </div>
      </div>
    </div>
  );

  // Composant BarChart int√©gr√©
  const BarChart = ({ data }) => {
    if (!data || data.length === 0) {
      return <div className="text-gray-500">Aucune donn√©e disponible</div>;
    }

    const maxValue = Math.max(...data.map(item => item.count));

    return (
      <div className="space-y-2">
        {data.map((item, index) => (
          <div key={index} className="flex items-center">
            <div className="w-24 text-sm text-gray-600 truncate">{item.sector}</div>
            <div className="flex-1 ml-2">
              <div
                className="bg-blue-500 h-6 rounded"
                style={{ width: `${(item.count / maxValue) * 100}%` }}
              ></div>
            </div>
            <div className="ml-2 text-sm font-medium">{item.count}</div>
          </div>
        ))}
      </div>
    );
  };

  // Composant PieChart int√©gr√©
  const PieChart = ({ data }) => {
    if (!data || data.length === 0) {
      return <div className="text-gray-500">Aucune donn√©e disponible</div>;
    }

    const colors = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444'];
    const total = data.reduce((sum, item) => sum + item.count, 0);

    return (
      <div className="flex flex-wrap justify-center">
        <div className="relative w-40 h-40">
          {data.map((item, index) => {
            const percent = (item.count / total) * 100;
            const cumulativePercent = data
              .slice(0, index)
              .reduce((sum, item) => sum + (item.count / total) * 100, 0);

            return (
              <div
                key={index}
                className="absolute inset-0"
                style={{
                  background: `conic-gradient(${colors[index]} ${cumulativePercent}% ${cumulativePercent + percent}%, transparent ${cumulativePercent + percent}% 100%)`,
                  borderRadius: '50%'
                }}
              ></div>
            );
          })}
        </div>
        <div className="ml-6 space-y-2">
          {data.map((item, index) => (
            <div key={index} className="flex items-center">
              <div
                className="w-4 h-4 mr-2 rounded-full"
                style={{ backgroundColor: colors[index] }}
              ></div>
              <span className="text-sm">
                {item.status}: {item.count} ({(item.count / total * 100).toFixed(1)}%)
              </span>
            </div>
          ))}
        </div>
      </div>
    );
  };

  // Composant LoadingSpinner int√©gr√©
  const LoadingSpinner = ({ fullScreen = false }) => (
    <div className={`flex justify-center items-center ${fullScreen ? 'h-screen' : 'h-full'}`}>
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
    </div>
  );

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem('token');
        
        const [statsRes, offersRes, candidaturesRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/admin/stats`, {
            headers: { Authorization: `Bearer ${token}` }
          }),
          axios.get(`${API_BASE_URL}/offres/recent`, {
            headers: { Authorization: `Bearer ${token}` }
          }),
          axios.get(`${API_BASE_URL}/admin/candidatures`, {
            headers: { Authorization: `Bearer ${token}` }
          })
        ]);

        setDashboardData({
          stats: statsRes.data,
          recentOffers: offersRes.data,
          candidatures: candidaturesRes.data,
          isLoading: false,
          error: null
        });
      } catch (error) {
        setDashboardData(prev => ({
          ...prev,
          isLoading: false,
          error: error.response?.data?.message || 'Erreur de chargement'
        }));
      }
    };

    fetchDashboardData();
  }, []);

  const handleApproveOffer = async (offerId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `${API_BASE_URL}/admin/offres/${offerId}/approve`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      setDashboardData(prev => ({
        ...prev,
        recentOffers: prev.recentOffers.map(offer => 
          offer._id === offerId ? { ...offer, status: 'approved' } : offer
        )
      }));
    } catch (error) {
      console.error('Approval error:', error);
    }
  };

  if (dashboardData.isLoading) {
    return <LoadingSpinner fullScreen />;
  }

  if (dashboardData.error) {
    return (
      <div className="p-4 text-red-500 bg-red-50 rounded-lg">
        {dashboardData.error}
      </div>
    );
  }

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <header className="mb-8">
        <h1 className="text-3xl font-bold text-gray-800">
          Tableau de Bord Administratif
        </h1>
        <p className="text-gray-600">
          Bonjour, {user?.name || 'Administrateur'}
        </p>
      </header>

      {/* Statistiques */}
      <section className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <StatsCard 
          title="Offres Actives" 
          value={dashboardData.stats?.activeOffers} 
          icon="üìã"
        />
        <StatsCard 
          title="Candidatures" 
          value={dashboardData.stats?.totalApplications} 
          icon="üì®"
        />
        <StatsCard 
          title="Entreprises" 
          value={dashboardData.stats?.totalCompanies} 
          icon="üè¢"
        />
        <StatsCard 
          title="En Attente" 
          value={dashboardData.stats?.pendingOffers} 
          icon="‚è≥"
        />
      </section>

      {/* Graphiques */}
      <section className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4">R√©partition des Offres</h2>
          <BarChart data={dashboardData.stats?.offersBySector} />
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4">Statut des Candidatures</h2>
          <PieChart data={dashboardData.stats?.applicationsByStatus} />
        </div>
      </section>

      {/* Derni√®res Offres */}
      <section className="bg-white p-6 rounded-lg shadow mb-8">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Derni√®res Offres</h2>
          <button className="text-blue-600 hover:text-blue-800">
            Voir tout
          </button>
        </div>
        
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Titre</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Secteur</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Date</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Statut</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {dashboardData.recentOffers.map(offer => (
                <tr key={offer._id}>
                  <td className="px-6 py-4 whitespace-nowrap">{offer.title}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{offer.sector}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    {new Date(offer.createdAt).toLocaleDateString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 py-1 rounded-full text-xs ${
                      offer.status === 'approved' 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {offer.status === 'approved' ? 'Approuv√©' : 'En attente'}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    {offer.status !== 'approved' && (
                      <button
                        onClick={() => handleApproveOffer(offer._id)}
                        className="text-blue-600 hover:text-blue-800 mr-3"
                      >
                        Approuver
                      </button>
                    )}
                    <button className="text-gray-600 hover:text-gray-800">
                      D√©tails
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {/* Candidatures R√©centes */}
      <section className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-semibold mb-4">Candidatures R√©centes</h2>
        <div className="space-y-4">
          {dashboardData.candidatures.slice(0, 5).map(candidature => (
            <div key={candidature._id} className="border-b pb-4 last:border-0">
              <div className="flex justify-between">
                <div>
                  <h3 className="font-medium">{candidature.offerTitle}</h3>
                  <p className="text-sm text-gray-600">
                    {candidature.companyName} ‚Ä¢ {candidature.status}
                  </p>
                </div>
                <span className="text-sm text-gray-500">
                  {new Date(candidature.createdAt).toLocaleDateString()}
                </span>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default DashboardMinistere;
