import { BrowserRouter, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import Accueil from './Pages/Home/Accueil';
import Home from './Pages/Home/Home';
import Login from './Pages/Auth/Login';
import Register from './Pages/Auth/Register';
import MesCandidatures from './Pages/Offre/MesCandidatures';
import PostulerOffre from './Pages/Entreprise/Postuler';
import OffreDetails from './Pages/Offre/OffreDetails';
import DashboardAdmin from './Pages/Dashbord/DashboardAdmin';
import DashboardEntreprise from './Pages/Dashbord/DashboardEntreprise';
import DashboardMinistere from './Pages/Dashbord/DashboardMinistere';
import EntrepriseHome from './Pages/Entreprise/EntrepriseHome';
import Navbar from './Components/Navbar';
import CreerOffre from './Pages/Offre/CreerOffre';
import PrivateRoute from './Components/PrivateRoute';

function App() {
  const location = useLocation();

  return (
    <>
      {/* Afficher le Navbar uniquement sur la page d'accueil, home, login et register exclus, et pas sur les dashboards */}
      {['/', '/home'].includes(location.pathname) && <Navbar />}

      <Routes>
        {/* Routes publiques */}
        <Route path="/" element={<Accueil />} />
        <Route path="/home" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Tableaux de bord */}
        <Route path="/dashboard/admin" element={
          <PrivateRoute roles={['admin']}>
            <DashboardAdmin />
          </PrivateRoute>
        } />

        <Route path="/dashboard/ministere" element={
          <PrivateRoute roles={['ministere']}>
            <DashboardMinistere />
          </PrivateRoute>
        } />

        <Route path="/dashboard/entreprise" element={
          <PrivateRoute roles={['entreprise']}>
            <DashboardEntreprise />
          </PrivateRoute>
        } />

        {/* Espace Entreprise */}
        <Route path="/entreprise" element={
          <PrivateRoute roles={['entreprise']}>
            <EntrepriseHome />
          </PrivateRoute>
        } />

        <Route path="/mes-candidatures" element={
          <PrivateRoute roles={['entreprise']}>
            <MesCandidatures />
          </PrivateRoute>
        } />

        {/* Correction ici: param√®tre en camelCase "offerId" */}
        <Route path="/entreprise/postuler/:offerId" element={
          <PrivateRoute roles={['entreprise']}>
            <PostulerOffre />
          </PrivateRoute>
        } />

        {/* Administration */}
        <Route path="/offre/:id" element={<OffreDetails />} />
        <Route path="/admin/creerOffre" element={
          <PrivateRoute roles={['admin']}>
            <CreerOffre />
          </PrivateRoute>
        } />

        {/* Redirection pour les routes inconnues */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </>
  );
}

function AppWithRouter() {
  return (
    <BrowserRouter>
      <App />
    </BrowserRouter>
  );
}

export default AppWithRouter;
