import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaChartLine, FaFileAlt, FaUsers, FaSignOutAlt, FaPlusCircle } from 'react-icons/fa';

const MinistereHome = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-indigo-100 via-purple-200 to-blue-300 flex flex-col items-center p-6 relative">
      {/* Bouton Déconnexion en haut à droite */}
      <button
        onClick={handleLogout}
        className="absolute top-6 right-6 flex items-center gap-2 bg-indigo-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
      >
        <FaSignOutAlt /> Déconnexion
      </button>

      <h1 className="text-4xl font-bold mb-8 text-indigo-900">Espace Ministère</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 w-full max-w-3xl">
        
        {/* Lien vers le Dashboard Ministère */}
        <Link 
          to="/dashboard/DashbordMinistere" 
          className="bg-white rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-indigo-50 transition transform hover:scale-105"
        >
          <FaChartLine className="text-indigo-600 text-3xl mb-2" />
          <span className="text-2xl font-semibold mb-2 text-indigo-700">Dashboard</span>
          <span className="text-gray-500 text-center">Tableau de bord complet avec statistiques</span>
        </Link>
        
        {/* Lien vers la création d'offres */}
        <Link 
          to="/dashboard/ministere/creer-offre" 
          className="bg-white rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-indigo-50 transition transform hover:scale-105"
        >
          <FaPlusCircle className="text-green-600 text-3xl mb-2" />
          <span className="text-2xl font-semibold mb-2 text-indigo-700">Créer Offre</span>
          <span className="text-gray-500 text-center">Publier de nouvelles offres d'emploi</span>
        </Link>
        
        {/* Lien vers la gestion des candidatures */}
        <Link 
          to="/dashboard/ministere/candidatures" 
          className="bg-white rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-indigo-50 transition transform hover:scale-105"
        >
          <FaUsers className="text-purple-600 text-3xl mb-2" />
          <span className="text-2xl font-semibold mb-2 text-indigo-700">Candidatures</span>
          <span className="text-gray-500 text-center">Gérer les candidatures reçues</span>
        </Link>

      </div>
    </div>
  );
};

export default MinistereHome;
