// Extrait dans OffresEntreprise.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OffresEntreprise = () => {
  const [offres, setOffres] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchOffres = async () => {
      try {
        const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';
        const res = await axios.get(`${API_BASE_URL}/api/offres/published`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setOffres(res.data.offres || res.data);
      } catch (error) {
        console.error('Erreur récupération offres:', error);
      }
    };
    fetchOffres();
  }, [token]);

  return (
    <div>
      <h2>Offres disponibles</h2>
      {offres.length === 0 && <p>Aucune offre disponible pour le moment.</p>}
      <ul>
        {offres.map((offre) => (
          <li key={offre.id}>
            <h3>{offre.title}</h3>
            <p>{offre.description}</p>
            <button>Postuler</button> {/* Tu peux gérer la navigation vers la page postuler */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OffresEntreprise;
