import React, { createContext, useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(() => {
    const stored = localStorage.getItem('user');
    return stored ? JSON.parse(stored) : null;
  });

  const [isAuthenticated, setIsAuthenticated] = useState(() => {
    return !!localStorage.getItem('token');
  });

  // VÃ©rifier l'authentification au chargement
  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');
    
    if (token && storedUser) {
      setUser(JSON.parse(storedUser));
      setIsAuthenticated(true);
    } else {
      setIsAuthenticated(false);
    }
  }, []);

  const login = ({ token, role, ...userData }) => {
    console.log('ðŸ” AuthContext: DÃ©but de la connexion avec role:', role);
    console.log('ðŸ“‹ AuthContext: DonnÃ©es reÃ§ues:', { token: token ? 'prÃ©sent' : 'absent', role, userData });
    
    try {
      // Sauvegarder les donnÃ©es
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify({ role, ...userData }));
      
      // Mettre Ã  jour l'Ã©tat
      const newUser = { token, role, ...userData };
      setUser(newUser);
      setIsAuthenticated(true);

      console.log('âœ… AuthContext: DonnÃ©es sauvegardÃ©es');
      console.log('ðŸ‘¤ AuthContext: Nouvel utilisateur:', newUser);
      console.log('ðŸ” AuthContext: localStorage user:', localStorage.getItem('user'));
      console.log('ðŸ” AuthContext: localStorage token:', localStorage.getItem('token') ? 'prÃ©sent' : 'absent');

      // Redirection selon rÃ´le avec un petit dÃ©lai pour s'assurer que l'Ã©tat est mis Ã  jour
      setTimeout(() => {
        if (['ministere', 'admin', 'ministerepublique'].includes(role)) {
          console.log('âž¡ï¸ Redirection vers /ministere/home');
          navigate('/ministere/home', { replace: true });
        } else if (role === 'entreprise') {
          console.log('âž¡ï¸ Redirection vers /entreprise/home');
          navigate('/entreprise/home', { replace: true });
        } else {
          console.log('âž¡ï¸ Redirection vers /');
          navigate('/', { replace: true });
        }
      }, 200); // AugmentÃ© Ã  200ms pour plus de sÃ©curitÃ©

    } catch (error) {
      console.error('âŒ Erreur dans AuthContext login:', error);
    }
  };

  const logout = () => {
    console.log('ðŸšª DÃ©connexion en cours...');
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
    navigate('/login', { replace: true });
  };

  return (
    <AuthContext.Provider value={{ 
      user, 
      isAuthenticated, 
      login, 
      logout 
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
