import React from 'react';
import { Link } from 'react-router-dom';

const EntrepriseHome = () => {
  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-100 via-blue-200 to-blue-300 flex flex-col items-center justify-center p-6">
      <h1 className="text-4xl font-bold mb-8 text-blue-900">Espace Entreprise</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 w-full max-w-3xl">
        {/* Dashboard Entreprise */}
        <Link to="/dashboard/entreprise" className="bg-white rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-blue-50 transition">
          <span className="text-2xl font-semibold mb-2 text-blue-700">Dashboard</span>
          <span className="text-gray-500">Gérer vos offres et candidatures</span>
        </Link>
        {/* Postuler */}
        <Link to="/postuler/1" className="bg-white rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-blue-50 transition">
          <span className="text-2xl font-semibold mb-2 text-blue-700">Postuler</span>
          <span className="text-gray-500">Soumettre une nouvelle candidature</span>
        </Link>
        {/* Suivi des candidatures */}
        <Link to="/Mescandidatures" className="bg-white rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-blue-50 transition">
          <span className="text-2xl font-semibold mb-2 text-blue-700">Suivi Candidature</span>
          <span className="text-gray-500">Voir l'état de vos candidatures</span>
        </Link>
      </div>
    </div>
  );
};

export default EntrepriseHome;
