// src/pages/OffreDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaArrowLeft } from 'react-icons/fa';

const OffreDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [offer, setOffer] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOffer = async () => {
      try {
        const res = await axios.get(`http://localhost:3000/api/offres/${id}`);
        setOffer(res.data);
      } catch (err) {
        console.error('Erreur lors de la récupération de l’offre', err);
      } finally {
        setLoading(false);
      }
    };

    fetchOffer();
  }, [id]);

  if (loading) return <div className="p-10 text-center text-blue-600">Chargement...</div>;
  if (!offer) return <div className="p-10 text-center text-red-600">Offre introuvable</div>;

  return (
    <div className="min-h-screen bg-blue-50 p-6 md:p-12">
      <div className="max-w-3xl mx-auto bg-white p-6 rounded-lg shadow">
        <button onClick={() => navigate(-1)} className="text-blue-600 hover:underline mb-4 flex items-center">
          <FaArrowLeft className="mr-2" /> Retour
        </button>

        <h1 className="text-2xl font-bold text-blue-800 mb-4">{offer.title}</h1>
        <p className="text-gray-700 mb-4">{offer.description}</p>

        <div className="text-sm text-blue-700 space-y-2">
          <p><strong>Statut :</strong> {offer.status}</p>
          <p><strong>Créée par :</strong> {offer.createdBy || 'Inconnu'}</p>
          <p><strong>Date limite :</strong> {new Date(offer.deadline).toLocaleDateString()}</p>
          <p><strong>Date de création :</strong> {new Date(offer.createdAt).toLocaleDateString()}</p>
          {offer.validatedAt && <p><strong>Validée le :</strong> {new Date(offer.validatedAt).toLocaleDateString()}</p>}
        </div>

        <div className="mt-6 text-right">
          <Link
            to="/login"
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Postuler
          </Link>
        </div>
      </div>
    </div>
  );
};

export default OffreDetails;
