import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const res = await fetch('http://localhost:3000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      const data = await res.json();

      if (!res.ok) return setError(data.error || 'Échec de connexion');

      localStorage.setItem('token', data.token);

      setSuccess(`Connecté avec succès en tant que ${data.user.role}`);

      setTimeout(() => {
        if (data.user.role === 'admin') {
          navigate('/admin');
        } else if (data.user.role === 'ministere') {
          navigate('/ministere');
        } else {
          navigate('/entreprise');
        }
      }, 2000); // délai pour montrer le message de succès
    } catch (err) {
      setError('Erreur serveur. Réessayez.');
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-gray-900">
      <form
        onSubmit={handleLogin}
        className="bg-gray-800 p-8 rounded-xl shadow-lg w-full max-w-md text-white"
      >
        <h2 className="text-3xl font-semibold mb-6 text-center text-indigo-400">Connexion</h2>

        {error && <p className="text-red-400 text-center mb-4">{error}</p>}
        {success && (
          <p className="text-green-400 text-center mb-4">
            {success}... Redirection vers le tableau de bord.
          </p>
        )}

        <input
          type="text"
          name="username"
          onChange={handleChange}
          placeholder="Nom d'utilisateur"
          required
          className="w-full mb-4 p-2 rounded bg-gray-700 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-indigo-400"
        />

        <input
          type="password"
          name="password"
          onChange={handleChange}
          placeholder="Mot de passe"
          required
          className="w-full mb-6 p-2 rounded bg-gray-700 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-indigo-400"
        />

        <button
          type="submit"
          className="w-full bg-indigo-500 hover:bg-indigo-600 text-white font-medium py-2 rounded transition duration-300"
        >
          Se connecter
        </button>

        <div className="text-sm text-center mt-4">
          <span>Pas encore de compte ? </span>
          <a href="/register" className="text-indigo-400 hover:underline">S'inscrire</a>
        </div>
      </form>
    </div>
  );
};

export default Login;
