import React, { useEffect, useState } from 'react';

const MesCandidatures = () => {
  const [candidatures, setCandidatures] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;

    fetch('http://localhost:3000/api/mes-candidatures', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        setCandidatures(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  if (loading) return <div className="p-4">Chargement...</div>;

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-4">Mes Candidatures</h2>
      {candidatures.length === 0 ? (
        <p className="text-gray-500">Aucune candidature envoyée.</p>
      ) : (
        <ul className="space-y-4">
          {candidatures.map(c => (
            <li key={c.id} className="border p-4 rounded shadow">
              <p className="font-semibold text-indigo-600">Offre ID : {c.offerId}</p>
              <p className="text-gray-700 mt-1">Message : {c.message}</p>
              <p className="text-sm text-gray-500 mt-2">Envoyée le {new Date(c.date).toLocaleString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MesCandidatures;
