import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaBriefcase, FaCalendarAlt, FaTag, FaSyncAlt, FaPaperPlane, FaTimes } from "react-icons/fa";
import { motion, AnimatePresence } from "framer-motion";

const OffresEntreprise = () => {
  const [offres, setOffres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("");
  const [selectedOffre, setSelectedOffre] = useState(null);
  const [candidatureMessage, setCandidatureMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  const fetchOffres = async () => {
    try {
      setLoading(true);
      const res = await axios.get("/api/offres");
      setOffres(res.data);
    } catch (error) {
      console.error("Erreur chargement offres", error);
      alert("Erreur lors du chargement des offres.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOffres();
  }, []);

  const filteredOffres = filter
    ? offres.filter((offre) =>
        offre.domaine.toLowerCase().includes(filter.toLowerCase())
      )
    : offres;

  const openCandidatureModal = (offre) => {
    setSelectedOffre(offre);
    setCandidatureMessage("");
  };

  const closeCandidatureModal = () => {
    setSelectedOffre(null);
    setCandidatureMessage("");
    setIsSubmitting(false);
  };

  const handleCandidatureSubmit = async () => {
    if (!candidatureMessage.trim()) {
      alert("Merci de remplir votre message de candidature.");
      return;
    }
    setIsSubmitting(true);
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "/api/candidatures",
        {
          offreId: selectedOffre.id,
          message: candidatureMessage,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert("Candidature envoyée avec succès !");
      closeCandidatureModal();
    } catch (error) {
      console.error("Erreur lors de l'envoi de la candidature", error);
      alert("Erreur lors de l'envoi de la candidature.");
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen p-6 md:p-12 bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Marchés Publics Disponibles</h1>
        <button
          onClick={fetchOffres}
          title="Rafraîchir"
          className="text-indigo-600 hover:text-indigo-800 dark:hover:text-indigo-400 text-2xl"
        >
          <FaSyncAlt />
        </button>
      </header>

      <div className="mb-6 max-w-sm">
        <input
          type="text"
          placeholder="Filtrer par domaine (ex: informatique, BTP...)"
          className="w-full p-3 rounded-lg border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
      </div>

      {loading ? (
        <p className="text-center text-lg">Chargement des offres...</p>
      ) : filteredOffres.length === 0 ? (
        <p className="text-center text-lg">Aucune offre trouvée.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          <AnimatePresence>
            {filteredOffres.map((offre, i) => (
              <motion.div
                key={offre.id}
                custom={i}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0, transition: { delay: i * 0.1 } }}
                exit={{ opacity: 0, y: -20, transition: { duration: 0.3 } }}
                className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 flex flex-col"
              >
                <h2 className="text-xl font-semibold mb-2 flex items-center gap-2">
                  <FaBriefcase /> {offre.titre}
                </h2>
                <p className="text-gray-700 dark:text-gray-300 mb-3 flex items-center gap-1">
                  <FaTag /> <span>{offre.domaine}</span>
                </p>
                <p className="text-gray-700 dark:text-gray-300 mb-4 flex items-center gap-1">
                  <FaCalendarAlt />{" "}
                  <span>
                    Date limite:{" "}
                    {new Date(offre.dateLimite).toLocaleDateString("fr-FR")}
                  </span>
                </p>
                <p className="flex-grow whitespace-pre-wrap text-gray-800 dark:text-gray-200">
                  {offre.description.length > 120
                    ? offre.description.slice(0, 120) + "..."
                    : offre.description}
                </p>
                <button
                  onClick={() => openCandidatureModal(offre)}
                  className="mt-4 bg-indigo-600 hover:bg-indigo-700 text-white py-2 rounded-lg font-semibold transition"
                >
                  Postuler
                </button>
              </motion.div>
            ))}
          </AnimatePresence>
        </div>
      )}

      {/* Modal de candidature */}
      <AnimatePresence>
        {selectedOffre && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4"
          >
            <motion.div
              initial={{ scale: 0.8 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0.8 }}
              className="bg-white dark:bg-gray-800 rounded-xl max-w-lg w-full p-6 relative"
            >
              <button
                onClick={closeCandidatureModal}
                className="absolute top-3 right-3 text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white text-2xl"
                title="Fermer"
              >
                <FaTimes />
              </button>

              <h3 className="text-2xl font-bold mb-4">
                Postuler pour : {selectedOffre.titre}
              </h3>

              <textarea
                rows="5"
                placeholder="Votre message de candidature..."
                className="w-full rounded-lg border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 p-3 mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                value={candidatureMessage}
                onChange={(e) => setCandidatureMessage(e.target.value)}
              />

              <button
                onClick={handleCandidatureSubmit}
                disabled={isSubmitting}
                className="bg-indigo-600 hover:bg-indigo-700 text-white py-2 px-6 rounded-lg font-semibold disabled:opacity-50"
              >
                {isSubmitting ? "Envoi en cours..." : "Envoyer la candidature"}
              </button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default OffresDetails;
