import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaChartLine, FaFileAlt, FaUsers, FaSignOutAlt } from 'react-icons/fa';
import { motion } from 'framer-motion';

const EntrepriseHome = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ 
        opacity: 1, 
        backgroundPositionX: ["0%", "100%", "0%"] 
      }}
      transition={{ duration: 30, repeat: Infinity, ease: "linear" }}
      className="min-h-screen bg-gradient-to-r from-blue-100 via-blue-200 to-blue-300 flex flex-col items-center p-6 relative bg-[length:200%_200%]"
    >
      {/* Bouton Déconnexion en haut à droite avec animation au hover */}
      <motion.button
        onClick={handleLogout}
        whileHover={{ scale: 1.1, boxShadow: "0px 0px 12px rgba(220,38,38,0.8)" }}
        whileTap={{ scale: 0.95 }}
        className="absolute top-6 right-6 flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
      >
        <FaSignOutAlt /> Déconnexion
      </motion.button>

      <motion.h1
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5, ease: "easeOut" }}
        className="text-4xl font-bold mb-8 text-blue-900"
      >
        Espace Entreprise
      </motion.h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 w-full max-w-3xl">
        {[{
          path: "/dashboard/dashboardentreprise",
          icon: <FaChartLine className="text-blue-600 text-3xl mb-2" />,
          title: "Dashboard",
          description: "Tableau de bord complet avec statistiques"
        }, {
          path: "/entreprise/offres-disponibles",
          icon: <FaFileAlt className="text-green-600 text-3xl mb-2" />,
          title: "Offres",
          description: "Consulter les offres disponibles"
        }, {
          path: "/entreprise/mescandidatures",
          icon: <FaUsers className="text-purple-600 text-3xl mb-2" />,
          title: "Candidatures",
          description: "Suivi de vos candidatures"
        }].map(({ path, icon, title, description }, index) => (
          <motion.div
            key={index}
            initial={{ y: 30, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: index * 0.15, type: "spring", stiffness: 120, damping: 15 }}
            whileHover={{ scale: 1.07, boxShadow: "0 15px 20px rgba(0,0,0,0.15)" }}
            whileTap={{ scale: 0.95 }}
            className="rounded-xl"
          >
            <Link
              to={path}
              className="bg-white rounded-xl shadow-lg p-8 flex flex-col items-center hover:bg-blue-50 transition-all"
            >
              <motion.div
                animate={{ scale: [1, 1.15, 1], rotate: [0, 10, 0] }}
                transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
                className="flex flex-col items-center"
              >
                {icon}
                <span className="text-2xl font-semibold mb-2 text-blue-700">{title}</span>
                <span className="text-gray-500 text-center">{description}</span>
              </motion.div>
            </Link>
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
};

export default EntrepriseHome;
