import React, { useEffect, useState } from "react";
import axios from "axios";
import { 
  FaTrash, FaMoon, FaSun, FaUserTie, FaBriefcase, 
  FaChartBar, FaBuilding, FaUserShield, FaUser 
} from "react-icons/fa";
import { Bar, Pie } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

const DashboardMinistere = () => {
  const [users, setUsers] = useState([]);
  const [offers, setOffers] = useState([]);
  const [candidatures, setCandidatures] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem("token");
      const [usersRes, offersRes, candidaturesRes] = await Promise.all([
        axios.get("http://localhost:3001/api/users", {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get("http://localhost:3001/api/offers", {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get("http://localhost:3001/api/candidatures", {
          headers: { Authorization: `Bearer ${token}` }
        })
      ]);
      
      setUsers(usersRes.data);
      setOffers(offersRes.data);
      setCandidatures(candidaturesRes.data);
      setLoading(false);
    } catch (error) {
      console.error("Erreur lors du chargement des données :", error.message);
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:3001/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(users.filter(user => user.id !== userId));
    } catch (error) {
      console.error("Erreur lors de la suppression :", error.message);
    }
  };

  const validateOffer = async (offerId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.patch(`http://localhost:3001/api/offers/${offerId}/validate`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchData();
    } catch (error) {
      console.error("Erreur lors de la validation :", error.message);
    }
  };

  const rejectOffer = async (offerId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.patch(`http://localhost:3001/api/offers/${offerId}/reject`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchData();
    } catch (error) {
      console.error("Erreur lors du rejet :", error.message);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Statistiques des utilisateurs par rôle
  const userStats = {
    entreprise: users.filter(u => u.role === 'entreprise').length,
    admin: users.filter(u => u.role === 'admin').length,
    ministere: users.filter(u => u.role === 'ministere').length,
    ministerepublique: users.filter(u => u.role === 'ministerepublique').length
  };

  // Statistiques des offres par statut
  const offerStats = {
    en_attente: offers.filter(o => o.status === 'en_attente').length,
    valide: offers.filter(o => o.status === 'valide').length,
    rejetée: offers.filter(o => o.status === 'rejetée').length
  };

  // Statistiques des candidatures par statut
  const candidatureStats = {
    en_attente: candidatures.filter(c => c.status === 'en_attente').length,
    acceptée: candidatures.filter(c => c.status === 'acceptée').length,
    rejetée: candidatures.filter(c => c.status === 'rejetée').length
  };

  // Données pour les graphiques
  const userRoleData = {
    labels: ['Entreprises', 'Admins', 'Ministère', 'Ministère Public'],
    datasets: [{
      label: 'Utilisateurs',
      data: Object.values(userStats),
      backgroundColor: [
        'rgba(54, 162, 235, 0.6)',
        'rgba(255, 99, 132, 0.6)',
        'rgba(75, 192, 192, 0.6)',
        'rgba(153, 102, 255, 0.6)'
      ],
      borderColor: [
        'rgba(54, 162, 235, 1)',
        'rgba(255, 99, 132, 1)',
        'rgba(75, 192, 192, 1)',
        'rgba(153, 102, 255, 1)'
      ],
      borderWidth: 1
    }]
  };

  const offerStatusData = {
    labels: ['En attente', 'Validées', 'Rejetées'],
    datasets: [{
      label: 'Offres',
      data: Object.values(offerStats),
      backgroundColor: [
        'rgba(255, 206, 86, 0.6)',
        'rgba(75, 192, 192, 0.6)',
        'rgba(255, 99, 132, 0.6)'
      ],
      borderColor: [
        'rgba(255, 206, 86, 1)',
        'rgba(75, 192, 192, 1)',
        'rgba(255, 99, 132, 1)'
      ],
      borderWidth: 1
    }]
  };

  const candidatureStatusData = {
    labels: ['En attente', 'Acceptées', 'Rejetées'],
    datasets: [{
      label: 'Candidatures',
      data: Object.values(candidatureStats),
      backgroundColor: [
        'rgba(54, 162, 235, 0.6)',
        'rgba(75, 192, 192, 0.6)',
        'rgba(255, 99, 132, 0.6)'
      ],
      borderColor: [
        'rgba(54, 162, 235, 1)',
        'rgba(75, 192, 192, 1)',
        'rgba(255, 99, 132, 1)'
      ],
      borderWidth: 1
    }]
  };

  if (loading) {
    return (
      <div className={`flex items-center justify-center min-h-screen ${darkMode ? 'dark:bg-gray-900' : 'bg-gray-50'}`}>
        <div className="text-xl font-semibold">Chargement en cours...</div>
      </div>
    );
  }

  return (
    <div className={`p-6 min-h-screen ${darkMode ? 'dark:bg-gray-900 dark:text-white' : 'bg-gray-50'}`}>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold">Tableau de bord Ministère</h2>
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="p-2 rounded-full bg-gray-200 dark:bg-gray-700"
        >
          {darkMode ? <FaSun className="text-yellow-400" /> : <FaMoon className="text-gray-600" />}
        </button>
      </div>

      {/* Statistiques principales */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-blue-100 dark:bg-blue-900 mr-4">
              <FaUserTie className="text-blue-600 dark:text-blue-300" />
            </div>
            <div>
              <p className="text-sm text-gray-500 dark:text-gray-300">Total Utilisateurs</p>
              <p className="text-2xl font-bold">{users.length}</p>
            </div>
          </div>
        </div>

        <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-green-100 dark:bg-green-900 mr-4">
              <FaBriefcase className="text-green-600 dark:text-green-300" />
            </div>
            <div>
              <p className="text-sm text-gray-500 dark:text-gray-300">Total Offres</p>
              <p className="text-2xl font-bold">{offers.length}</p>
            </div>
          </div>
        </div>

        <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-purple-100 dark:bg-purple-900 mr-4">
              <FaChartBar className="text-purple-600 dark:text-purple-300" />
            </div>
            <div>
              <p className="text-sm text-gray-500 dark:text-gray-300">Total Candidatures</p>
              <p className="text-2xl font-bold">{candidatures.length}</p>
            </div>
          </div>
        </div>

        <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-orange-100 dark:bg-orange-900 mr-4">
              <FaBuilding className="text-orange-600 dark:text-orange-300" />
            </div>
            <div>
              <p className="text-sm text-gray-500 dark:text-gray-300">Entreprises</p>
              <p className="text-2xl font-bold">{userStats.entreprise}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Graphiques */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
        <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <h3 className="text-lg font-semibold mb-4">Répartition des utilisateurs</h3>
          <Pie data={userRoleData} />
        </div>

        <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <h3 className="text-lg font-semibold mb-4">Statut des offres</h3>
          <Pie data={offerStatusData} />
        </div>

        <div className={`p-4 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <h3 className="text-lg font-semibold mb-4">Statut des candidatures</h3>
          <Pie data={candidatureStatusData} />
        </div>
      </div>

      {/* Liste des offres en attente de validation */}
      <div className={`p-6 rounded-lg shadow mb-8 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
        <h3 className="text-xl font-semibold mb-4">Offres en attente de validation</h3>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
            <thead>
              <tr>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Titre</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Domaine</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Date limite</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Entreprise</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200 dark:divide-gray-700">
              {offers.filter(o => o.status === 'en_attente').map(offer => (
                <tr key={offer.id}>
                  <td className="px-4 py-3 whitespace-nowrap">{offer.title}</td>
                  <td className="px-4 py-3 whitespace-nowrap">{offer.domaine}</td>
                  <td className="px-4 py-3 whitespace-nowrap">{new Date(offer.dateLimite).toLocaleDateString()}</td>
                  <td className="px-4 py-3 whitespace-nowrap">
                    {users.find(u => u.id === offer.createdById)?.companyName || 'Inconnu'}
                  </td>
                  <td className="px-4 py-3 whitespace-nowrap space-x-2">
                    <button 
                      onClick={() => validateOffer(offer.id)}
                      className="px-3 py-1 bg-green-500 text-white rounded hover:bg-green-600"
                    >
                      Valider
                    </button>
                    <button 
                      onClick={() => rejectOffer(offer.id)}
                      className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
                    >
                      Rejeter
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Liste des utilisateurs */}
      <div className={`p-6 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-xl font-semibold">Liste des utilisateurs</h3>
          <span className="text-sm text-gray-500 dark:text-gray-300">
            Total: {users.length} utilisateurs
          </span>
        </div>
        
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200 dark:divide-gray-700">
            <thead>
              <tr>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Nom</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Email</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Rôle</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Entreprise</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">Actions</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200 dark:divide-gray-700">
              {users.map((user) => (
                <tr key={user.id}>
                  <td className="px-4 py-3 whitespace-nowrap">{user.username}</td>
                  <td className="px-4 py-3 whitespace-nowrap">{user.email}</td>
                  <td className="px-4 py-3 whitespace-nowrap">
                    <span className={`px-2 py-1 text-xs rounded-full ${
                      user.role === 'admin' ? 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200' :
                      user.role === 'entreprise' ? 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200' :
                      user.role === 'ministere' ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200' :
                      'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200'
                    }`}>
                      {user.role}
                    </span>
                  </td>
                  <td className="px-4 py-3 whitespace-nowrap">
                    {user.companyName || '-'}
                  </td>
                  <td className="px-4 py-3 whitespace-nowrap">
                    <button 
                      onClick={() => handleDeleteUser(user.id)} 
                      className="text-red-600 hover:text-red-800 dark:hover:text-red-400"
                    >
                      <FaTrash />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default DashboardMinistere;
