// src/Pages/OffresEntreprise.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';

const OffresEntreprise = () => {
  const [offres, setOffres] = useState([]);
  const [selectedOffer, setSelectedOffer] = useState(null);
  const [message, setMessage] = useState('');
  const [posting, setPosting] = useState(false);
  const [successMsg, setSuccessMsg] = useState('');

  useEffect(() => {
    fetchOffers();
  }, []);

  const fetchOffers = async () => {
    try {
      const res = await axios.get('/api/offres/validated');
      setOffres(res.data);
    } catch (err) {
      console.error('Erreur récupération offres', err);
    }
  };

  const handlePostuler = async () => {
    if (!message.trim()) return alert('Veuillez écrire un message pour votre candidature.');
    setPosting(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        '/api/candidatures',
        { offreId: selectedOffer.id, message },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSuccessMsg('Candidature envoyée avec succès !');
      setSelectedOffer(null);
      setMessage('');
    } catch (err) {
      alert('Erreur lors de l\'envoi de la candidature');
    }
    setPosting(false);
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6 text-center text-indigo-700 dark:text-indigo-300">
        Offres Publiées
      </h1>

      {successMsg && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="bg-green-100 text-green-700 p-4 rounded-md mb-6 text-center"
        >
          {successMsg}
        </motion.div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {offres.map(offer => (
          <motion.div
            key={offer.id}
            className="p-4 border rounded-lg shadow cursor-pointer dark:bg-gray-800 dark:border-gray-700"
            whileHover={{ scale: 1.03 }}
            onClick={() => setSelectedOffer(offer)}
          >
            <h2 className="font-semibold text-lg text-indigo-600 dark:text-indigo-400">{offer.title}</h2>
            <p className="text-gray-700 dark:text-gray-300 line-clamp-3">{offer.description}</p>
            <p className="mt-2 text-sm text-gray-500 dark:text-gray-400">
              Date limite : {new Date(offer.dateLimite).toLocaleDateString()}
            </p>
          </motion.div>
        ))}
      </div>

      {selectedOffer && (
        <motion.div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center p-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={() => setSelectedOffer(null)}
        >
          <motion.div
            className="bg-white dark:bg-gray-900 rounded-lg p-6 max-w-lg w-full relative"
            onClick={e => e.stopPropagation()}
            initial={{ y: 50 }}
            animate={{ y: 0 }}
          >
            <h3 className="text-xl font-bold mb-4 text-indigo-700 dark:text-indigo-400">{selectedOffer.title}</h3>
            <p className="mb-4 text-gray-800 dark:text-gray-200">{selectedOffer.description}</p>

            <textarea
              rows={4}
              placeholder="Votre message de candidature..."
              className="w-full p-2 border rounded-md dark:bg-gray-700 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 mb-4"
              value={message}
              onChange={e => setMessage(e.target.value)}
            />

            <div className="flex justify-end gap-4">
              <button
                onClick={() => setSelectedOffer(null)}
                className="px-4 py-2 border rounded-md dark:border-gray-600"
              >
                Annuler
              </button>
              <button
                onClick={handlePostuler}
                disabled={posting}
                className={`px-4 py-2 rounded-md text-white ${
                  posting ? 'bg-indigo-300' : 'bg-indigo-600 hover:bg-indigo-700'
                }`}
              >
                {posting ? 'Envoi...' : 'Postuler'}
              </button>
            </div>
          </motion.div>
        </motion.div>
      )}
    </div>
  );
};

export default OffresDeta;
