import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  FaTrash, FaMoon, FaSun, FaUserTie, FaBriefcase,
  FaChartBar, FaBuilding, FaUserShield, FaUser,
  FaBell, FaSearch, FaCalendarAlt, FaMoneyBillWave,
  FaFileContract, FaClipboardList, FaChartPie
} from "react-icons/fa";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS, CategoryScale, LinearScale, BarElement,
  Title, Tooltip, Legend, ArcElement
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

const DashboardMinistere = () => {
  const [users, setUsers] = useState([]);
  const [offers, setOffers] = useState([]);
  const [candidatures, setCandidatures] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem("token");
      const [usersRes, offersRes, candidaturesRes] = await Promise.all([
        axios.get("http://localhost:3001/api/users", { headers: { Authorization: `Bearer ${token}` } }),
        axios.get("http://localhost:3001/api/offers", { headers: { Authorization: `Bearer ${token}` } }),
        axios.get("http://localhost:3001/api/candidatures", { headers: { Authorization: `Bearer ${token}` } })
      ]);
      
      setUsers(usersRes.data);
      setOffers(offersRes.data);
      setCandidatures(candidaturesRes.data);
      setLoading(false);
    } catch (error) {
      console.error("Erreur lors du chargement des données :", error.message);
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:3001/api/users/${userId}`, { headers: { Authorization: `Bearer ${token}` } });
      setUsers(users.filter(user => user.id !== userId));
    } catch (error) {
      console.error("Erreur lors de la suppression :", error.message);
    }
  };

  const validateOffer = async (offerId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.patch(`http://localhost:3001/api/offers/${offerId}/validate`, {}, { headers: { Authorization: `Bearer ${token}` } });
      fetchData();
    } catch (error) {
      console.error("Erreur lors de la validation :", error.message);
    }
  };

  const rejectOffer = async (offerId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.patch(`http://localhost:3001/api/offers/${offerId}/reject`, {}, { headers: { Authorization: `Bearer ${token}` } });
      fetchData();
    } catch (error) {
      console.error("Erreur lors du rejet :", error.message);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Statistiques
  const userStats = {
    entreprise: users.filter(u => u.role === 'entreprise').length,
    admin: users.filter(u => u.role === 'admin').length,
    ministere: users.filter(u => u.role === 'ministere').length,
    ministerepublique: users.filter(u => u.role === 'ministerepublique').length
  };

  const offerStats = {
    en_attente: offers.filter(o => o.status === 'en_attente').length,
    valide: offers.filter(o => o.status === 'valide').length,
    rejetée: offers.filter(o => o.status === 'rejetée').length
  };

  const candidatureStats = {
    en_attente: candidatures.filter(c => c.status === 'en_attente').length,
    acceptée: candidatures.filter(c => c.status === 'acceptée').length,
    rejetée: candidatures.filter(c => c.status === 'rejetée').length
  };

  // Données pour le style de la capture
  const cardData = [
    { title: "Online Status", value: userStats.entreprise, icon: <FaUserTie />, color: "blue" },
    { title: "Play Size", value: offers.length, icon: <FaBriefcase />, color: "green" },
    { title: "Return Rate", value: candidatures.length, icon: <FaChartBar />, color: "purple" },
    { title: "Total", value: userStats.admin, icon: <FaUserShield />, color: "yellow" },
    { title: "Disabled", value: userStats.ministere, icon: <FaBuilding />, color: "red" },
    { title: "Turtle", value: userStats.ministerepublique, icon: <FaUser />, color: "indigo" },
    { title: "Search", value: offerStats.en_attente, icon: <FaSearch />, color: "pink" },
    { title: "Recent Activities", value: offerStats.valide, icon: <FaCalendarAlt />, color: "orange" },
    { title: "Other Status", value: offerStats.rejetée, icon: <FaBell />, color: "teal" },
    { title: "Notifications", value: candidatureStats.en_attente, icon: <FaBell />, color: "blue" },
    { title: "Date Added", value: candidatureStats.acceptée, icon: <FaCalendarAlt />, color: "gray" },
    { title: "Amounts", value: candidatureStats.rejetée, icon: <FaMoneyBillWave />, color: "gray" }
  ];

  if (loading) {
    return (
      <div className={`flex items-center justify-center min-h-screen ${darkMode ? 'dark:bg-gray-900' : 'bg-gray-50'}`}>
        <div className="text-xl font-semibold">Chargement en cours...</div>
      </div>
    );
  }

  return (
    <div className={`min-h-screen ${darkMode ? 'dark:bg-gray-900 dark:text-white' : 'bg-gray-100'}`}>
      {/* Header */}
      <header className={`p-4 ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-md flex justify-between items-center`}>
        <h1 className="text-2xl font-bold">Fusion</h1>
        <button 
          onClick={() => setDarkMode(!darkMode)} 
          className="p-2 rounded-full bg-gray-200 dark:bg-gray-700"
        >
          {darkMode ? <FaSun className="text-yellow-400" /> : <FaMoon />}
        </button>
      </header>

      {/* Main Content */}
      <div className="p-6 grid grid-cols-1 md:grid-cols-5 gap-6">
        {/* Subboard Section */}
        <div className={`p-6 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'} md:col-span-1`}>
          <h2 className="text-xl font-bold mb-4">Subboard</h2>
          <ul className="space-y-2">
            {["Trip", "Attention", "Admissions", "Comments", "Center", "Union", "Train", "Time", "Cross", "View", "Documentation"].map((item) => (
              <li key={item} className="hover:bg-gray-100 dark:hover:bg-gray-700 p-2 rounded cursor-pointer">
                {item}
              </li>
            ))}
          </ul>
        </div>

        {/* Stats Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 md:col-span-4">
          {cardData.map((card, index) => (
            <StatCard
              key={index}
              title={card.title}
              value={card.value}
              icon={card.icon}
              color={card.color}
              darkMode={darkMode}
            />
          ))}

          {/* Graphiques */}
          <div className={`p-6 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'} col-span-1 sm:col-span-2 lg:col-span-3`}>
            <h3 className="text-lg font-semibold mb-4">Statistiques des Utilisateurs</h3>
            <div className="h-64">
              <Pie data={{
                labels: ['Entreprises', 'Admins', 'Ministère', 'Ministère Public'],
                datasets: [{
                  data: Object.values(userStats),
                  backgroundColor: ['rgba(54, 162, 235, 0.6)', 'rgba(255, 99, 132, 0.6)', 'rgba(75, 192, 192, 0.6)', 'rgba(153, 102, 255, 0.6)'],
                  borderColor: ['rgba(54, 162, 235, 1)', 'rgba(255, 99, 132, 1)', 'rgba(75, 192, 192, 1)', 'rgba(153, 102, 255, 1)'],
                  borderWidth: 1
                }]
              }} />
            </div>
          </div>

          <div className={`p-6 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'} col-span-1 sm:col-span-2`}>
            <h3 className="text-lg font-semibold mb-4">Statut des Offres</h3>
            <div className="h-64">
              <Bar data={{
                labels: ['En attente', 'Validées', 'Rejetées'],
                datasets: [{
                  label: 'Offres',
                  data: Object.values(offerStats),
                  backgroundColor: ['rgba(255, 206, 86, 0.6)', 'rgba(75, 192, 192, 0.6)', 'rgba(255, 99, 132, 0.6)'],
                  borderColor: ['rgba(255, 206, 86, 1)', 'rgba(75, 192, 192, 1)', 'rgba(255, 99, 132, 1)'],
                  borderWidth: 1
                }]
              }} />
            </div>
          </div>

          <div className={`p-6 rounded-lg shadow ${darkMode ? 'bg-gray-800' : 'bg-white'} col-span-1 sm:col-span-2`}>
            <h3 className="text-lg font-semibold mb-4">Statut des Candidatures</h3>
            <div className="h-64">
              <Bar data={{
                labels: ['En attente', 'Acceptées', 'Rejetées'],
                datasets: [{
                  label: 'Candidatures',
                  data: Object.values(candidatureStats),
                  backgroundColor: ['rgba(54, 162, 235, 0.6)', 'rgba(75, 192, 192, 0.6)', 'rgba(255, 99, 132, 0.6)'],
                  borderColor: ['rgba(54, 162, 235, 1)', 'rgba(75, 192, 192, 1)', 'rgba(255, 99, 132, 1)'],
                  borderWidth: 1
                }]
              }} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Composant de carte réutilisable
const StatCard = ({ title, value, icon, color, darkMode }) => {
  const colorClasses = {
    blue: { bg: 'bg-blue-100', text: 'text-blue-600', darkBg: 'bg-blue-900/30', darkText: 'text-blue-300' },
    green: { bg: 'bg-green-100', text: 'text-green-600', darkBg: 'bg-green-900/30', darkText: 'text-green-300' },
    purple: { bg: 'bg-purple-100', text: 'text-purple-600', darkBg: 'bg-purple-900/30', darkText: 'text-purple-300' },
    yellow: { bg: 'bg-yellow-100', text: 'text-yellow-600', darkBg: 'bg-yellow-900/30', darkText: 'text-yellow-300' },
    red: { bg: 'bg-red-100', text: 'text-red-600', darkBg: 'bg-red-900/30', darkText: 'text-red-300' },
    indigo: { bg: 'bg-indigo-100', text: 'text-indigo-600', darkBg: 'bg-indigo-900/30', darkText: 'text-indigo-300' },
    pink: { bg: 'bg-pink-100', text: 'text-pink-600', darkBg: 'bg-pink-900/30', darkText: 'text-pink-300' },
    orange: { bg: 'bg-orange-100', text: 'text-orange-600', darkBg: 'bg-orange-900/30', darkText: 'text-orange-300' },
    teal: { bg: 'bg-teal-100', text: 'text-teal-600', darkBg: 'bg-teal-900/30', darkText: 'text-teal-300' },
    gray: { bg: 'bg-gray-100', text: 'text-gray-600', darkBg: 'bg-gray-900/30', darkText: 'text-gray-300' }
  };

  return (
    <div className={`p-4 rounded-lg shadow ${darkMode ? colorClasses[color].darkBg : colorClasses[color].bg}`}>
      <div className="flex justify-between items-start">
        <div className={`text-2xl ${darkMode ? colorClasses[color].darkText : colorClasses[color].text}`}>
          {icon}
        </div>
        <div className="text-right">
          <div className="text-sm text-gray-500 dark:text-gray-300">{title}</div>
          <div className="text-2xl font-bold">{value}</div>
        </div>
      </div>
    </div>
  );
};

export default DashboardMinistere;
