import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const OffreDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [offre, setOffre] = useState(null);
  const [message, setMessage] = useState('');
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetch(`http://localhost:3000/api/offres`)
      .then(res => res.json())
      .then(data => {
        const selected = data.find(o => o.id === id);
        if (selected) setOffre(selected);
        else navigate('/');
      })
      .catch(() => navigate('/'));
  }, [id, navigate]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return navigate('/login');

    fetch('http://localhost:3000/api/candidatures', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({ offerId: offre.id, message })
    })
      .then(res => res.json())
      .then(data => {
        if (data.candidature) {
          setSuccess("Candidature envoyée !");
          setMessage('');
        } else {
          setError(data.error || "Erreur");
        }
      })
      .catch(() => setError("Erreur d'envoi"));
  };

  if (!offre) return <div className="p-4">Chargement...</div>;

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded shadow">
      <h1 className="text-2xl font-bold text-indigo-600 mb-4">{offre.title}</h1>
      <p className="text-gray-700">{offre.description}</p>
      <p className="text-sm text-gray-500 mt-2">Date limite : {new Date(offre.deadline).toLocaleDateString()}</p>
      <hr className="my-4" />
      {success && <p className="text-green-600 mb-2">{success}</p>}
      {error && <p className="text-red-600 mb-2">{error}</p>}
      <form onSubmit={handleSubmit}>
        <label className="block mb-2 font-medium">Message de candidature</label>
        <textarea
          className="w-full p-2 border rounded mb-4"
          value={message}
          onChange={e => setMessage(e.target.value)}
          required
        ></textarea>
        <button type="submit" className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700">
          Postuler
        </button>
      </form>
    </div>
  );
};

export default OffreDetail;
