import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { FaChartLine, FaUsers, FaPlusCircle, FaSignOutAlt } from 'react-icons/fa';
import { motion } from 'framer-motion';
import axios from 'axios';

const MinistereHome = () => {
  const navigate = useNavigate();
  const { user, logout, isLoading: authLoading } = useAuth();
  const [isVerifying, setIsVerifying] = useState(true);

  useEffect(() => {
    const verifyAuth = async () => {
      if (authLoading) return;

      setIsVerifying(true);
      try {
        const token = localStorage.getItem('token');
        
        if (!token || !['ministere', 'admin', 'ministerepublique'].includes(user?.role)) {
          throw new Error('Accès non autorisé');
        }

        await axios.get('/api/auth/verify', {
          headers: { Authorization: `Bearer ${token}` },
        });
      } catch (error) {
        console.error('Erreur de vérification:', error);
        logout();
        navigate('/login', { replace: true });
      } finally {
        setIsVerifying(false);
      }
    };

    verifyAuth();
  }, [user, logout, navigate, authLoading]);

  useEffect(() => {
    const handleBeforeUnload = (e) => {
      if (user) {
        e.preventDefault();
        e.returnValue = 'Êtes-vous sûr de vouloir quitter?';
      }
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => window.removeEventListener('beforeunload', handleBeforeUnload);
  }, [user]);

  if (authLoading || isVerifying) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 dark:bg-gray-900">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
          className="h-12 w-12 border-t-2 border-b-2 border-indigo-500 rounded-full"
        />
      </div>
    );
  }

  const cards = [
    {
      path: "/Dashboard/DashboardMinistere",
      icon: <FaChartLine className="text-indigo-600 dark:text-indigo-300 text-3xl mb-2" />,
      title: "Dashboard",
      description: "Statistiques et analyses des marchés"
    },
    {
      path: "/ministere/creer-offre-nouveau",
      icon: <FaPlusCircle className="text-green-600 dark:text-green-400 text-3xl mb-2" />,
      title: "Créer Offre",
      description: "Publier un nouveau marché public"
    },
    {
      path: "/ministere/gestion-offres",
      icon: <FaChartLine className="text-blue-600 dark:text-blue-400 text-3xl mb-2" />,
      title: "Gérer Offres",
      description: "Valider et gérer les offres"
    },
    {
      path: "/ministere/GestionCandidatures",
      icon: <FaUsers className="text-purple-600 dark:text-purple-300 text-3xl mb-2" />,
      title: "Candidatures par Offre",
      description: "Gérer les candidatures par offre"
    },
    {
      path: "/ministere/gestion-candidatures-toutes",
      icon: <FaUsers className="text-orange-600 dark:text-orange-300 text-3xl mb-2" />,
      title: "Toutes Candidatures",
      description: "Vue globale des candidatures"
    }
  ];

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1, backgroundPositionX: ["0%", "100%", "0%"] }} // animation douce du background
      transition={{ duration: 30, repeat: Infinity, ease: "linear" }}
      className="min-h-screen bg-gradient-to-br from-indigo-100 via-purple-200 to-blue-300 dark:from-gray-900 dark:via-gray-800 dark:to-black p-6 relative bg-[length:200%_200%]"
    >
      {/* Bouton Déconnexion en haut à droite avec animation au hover */}
      <motion.button
        initial={{ opacity: 0, x: 20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ delay: 0.3 }}
        onClick={logout}
        whileHover={{ scale: 1.1, boxShadow: "0px 0px 12px rgba(220,38,38,0.8)" }}
        whileTap={{ scale: 0.95 }}
        className="absolute top-6 right-6 flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-all duration-200 shadow-lg hover:shadow-xl active:transform active:scale-95"
      >
        <FaSignOutAlt /> Déconnexion
      </motion.button>

      <h1 className="text-4xl font-bold text-center mb-10 text-indigo-900 dark:text-white">
        Espace Ministère
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-8 max-w-7xl mx-auto">
        {cards.map((card, index) => (
          <motion.div
            key={index}
            initial={{ y: 40, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{
              delay: index * 0.12,
              type: "spring",
              stiffness: 120,
              damping: 15,
            }}
            whileHover={{ scale: 1.07, boxShadow: "0 15px 20px rgba(0,0,0,0.2)" }}
            whileTap={{ scale: 0.95 }}
            className="rounded-xl"
          >
            <Link
              to={card.path}
              className="block bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8 hover:shadow-xl transition-all"
            >
              <motion.div
                animate={{ scale: [1, 1.15, 1], rotate: [0, 10, 0] }}
                transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
                className="flex flex-col items-center"
              >
                {card.icon}
                <span className="text-2xl font-semibold mb-2">{card.title}</span>
                <span className="text-gray-500 dark:text-gray-300 text-center">
                  {card.description}
                </span>
              </motion.div>
            </Link>
          </motion.div>
        ))}
      </div>

      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5 }}
        className="mt-12 text-center text-sm text-gray-500 dark:text-gray-400"
      >
        Session sécurisée • Dernière connexion: {new Date().toLocaleString()}
      </motion.div>
    </motion.div>
  );
};

export default MinistereHome;
