// src/Pages/DashboardMinistere.jsx
import React, { useEffect, useState } from 'react';

const DashboardMinistere = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    fetch('http://localhost:3000/api/dashboard/ministere', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(setData);
  }, []);

  if (!data) return <p className="p-4">Chargement...</p>;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold text-blue-700 mb-4">Dashboard MinistÃ¨re</h1>
      <p>ðŸ“¢ Offres actives : {data.stats.activeOffers}</p>
      <p>ðŸ“„ Total : {data.stats.totalOffers}</p>
    </div>
  );
};

export default DashboardMinistere;
