import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import { 
  FaArrowLeft, 
  FaUserTie, 
  FaBriefcase, 
  FaChartBar, 
  FaClipboardList,
  FaCheckCircle,
  FaTimesCircle,
  FaClock,
  FaCalendarAlt,
  FaBuilding
} from "react-icons/fa";
import { Bar, Pie, Doughnut } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';
import { DarkModeContext } from '../../Components/DarkModeContext';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

const DashboardMinistere = () => {
  const { darkMode } = useContext(DarkModeContext);
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalOffres: 0,
    offresValides: 0,
    offresEnAttente: 0,
    offresRejetees: 0,
    totalCandidatures: 0,
    candidaturesEnAttente: 0,
    candidaturesValides: 0,
    candidaturesRejetees: 0
  });
  const [offers, setOffers] = useState([]);
  const [candidatures, setCandidatures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [chartData, setChartData] = useState(null);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setError('Token manquant');
        return;
      }

      const response = await axios.get(`${API_BASE_URL}/api/dashboard/ministere`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const { offers, candidatures, stats } = response.data;
      
      setStats(stats);
      setOffers(offers);
      setCandidatures(candidatures);
      prepareChartData(stats);
      
    } catch (error) {
      console.error("Erreur lors du chargement des données :", error);
      setError(error.response?.data?.message || 'Erreur de chargement');
    } finally {
      setLoading(false);
    }
  };

  const prepareChartData = (data) => {
    const colors = darkMode ? {
      success: 'rgba(34, 197, 94, 0.8)',
      warning: 'rgba(251, 191, 36, 0.8)', 
      danger: 'rgba(239, 68, 68, 0.8)',
      primary: 'rgba(59, 130, 246, 0.8)',
      secondary: 'rgba(168, 85, 247, 0.8)',
      info: 'rgba(6, 182, 212, 0.8)'
    } : {
      success: 'rgba(16, 185, 129, 0.7)',
      warning: 'rgba(234, 179, 8, 0.7)',
      danger: 'rgba(220, 38, 38, 0.7)', 
      primary: 'rgba(59, 130, 246, 0.7)',
      secondary: 'rgba(168, 85, 247, 0.7)',
      info: 'rgba(14, 165, 233, 0.7)'
    };

    setChartData({
      offres: {
        labels: ['Validées', 'En attente', 'Rejetées'],
        datasets: [{
          label: 'Offres par statut',
          data: [data.offresValides, data.offresEnAttente, data.offresRejetees],
          backgroundColor: [colors.success, colors.warning, colors.danger],
          borderColor: [colors.success, colors.warning, colors.danger],
          borderWidth: 1
        }]
      },
      candidatures: {
        labels: ['Acceptées', 'En attente', 'Rejetées'],
        datasets: [{
          label: 'Candidatures par statut', 
          data: [data.candidaturesValides, data.candidaturesEnAttente, data.candidaturesRejetees],
          backgroundColor: [colors.success, colors.warning, colors.danger],
          borderColor: [colors.success, colors.warning, colors.danger],
          borderWidth: 1
        }]
      },
      global: {
        labels: ['Total Offres', 'Total Candidatures'],
        datasets: [{
          label: 'Vue d\'ensemble',
          data: [data.totalOffres, data.totalCandidatures],
          backgroundColor: [colors.primary, colors.secondary],
          borderColor: [colors.primary, colors.secondary], 
          borderWidth: 1
        }]
      }
    });
  };

  // Recalculer les couleurs quand le mode sombre change
  useEffect(() => {
    if (stats.totalOffres > 0) {
      prepareChartData(stats);
    }
  }, [darkMode, stats]);

  useEffect(() => {
    fetchData();
  }, []);

  // Options des graphiques adaptées au mode sombre
  const getChartOptions = (type, darkMode) => {
    const textColor = darkMode ? '#e5e7eb' : '#374151';
    const gridColor = darkMode ? 'rgba(75, 85, 99, 0.3)' : 'rgba(156, 163, 175, 0.3)';

    const baseOptions = {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        legend: {
          position: 'top',
          labels: { 
            color: textColor,
            font: {
              size: 12
            }
          }
        }
      }
    };

    if (type === 'bar') {
      return {
        ...baseOptions,
        scales: {
          y: {
            beginAtZero: true,
            ticks: { color: textColor },
            grid: { color: gridColor }
          },
          x: {
            ticks: { color: textColor },
            grid: { color: gridColor }
          }
        }
      };
    }

    return baseOptions;
  };

  if (loading) {
    return (
      <div className={`min-h-screen flex items-center justify-center ${darkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className={`mt-4 ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>Chargement du dashboard...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`min-h-screen flex items-center justify-center ${darkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
        <div className="text-center">
          <div className={`border px-4 py-3 rounded ${
            darkMode 
              ? 'bg-red-900 border-red-600 text-red-200' 
              : 'bg-red-100 border-red-400 text-red-700'
          }`}>
            <p className="font-bold">Erreur</p>
            <p>{error}</p>
          </div>
          <button
            onClick={() => window.location.reload()}
            className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors"
          >
            Réessayer
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
      {/* Header */}
      <div className={`shadow-sm border-b ${
        darkMode 
          ? 'bg-gray-800 border-gray-700' 
          : 'bg-white border-gray-200'
      }`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <button
                onClick={() => navigate('/ministere/home')}
                className={`mr-4 p-2 rounded-full transition-colors ${
                  darkMode 
                    ? 'text-gray-400 hover:text-gray-300 hover:bg-gray-700' 
                    : 'text-gray-500 hover:text-gray-700 hover:bg-gray-100'
                }`}
              >
                <FaArrowLeft className="h-5 w-5" />
              </button>
              <h1 className={`text-2xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                Dashboard Ministère
              </h1>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Statistiques principales */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatCard
            icon={<FaClipboardList className="text-blue-600 dark:text-blue-400" size={24} />}
            title="Total Offres"
            value={stats.totalOffres}
            darkMode={darkMode}
          />
          <StatCard
            icon={<FaCheckCircle className="text-green-600 dark:text-green-400" size={24} />}
            title="Offres Validées"
            value={stats.offresValides}
            darkMode={darkMode}
          />
          <StatCard
            icon={<FaChartBar className="text-purple-600 dark:text-purple-400" size={24} />}
            title="Total Candidatures"
            value={stats.totalCandidatures}
            darkMode={darkMode}
          />
          <StatCard
            icon={<FaClock className="text-yellow-600 dark:text-yellow-400" size={24} />}
            title="Candidatures En Attente"
            value={stats.candidaturesEnAttente}
            darkMode={darkMode}
          />
        </div>

        {/* Graphiques */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-8">
          <ChartContainer
            title="Répartition des Offres"
            icon={<FaBriefcase className="text-blue-600 dark:text-blue-400" />}
            darkMode={darkMode}
          >
            {chartData && <Doughnut data={chartData.offres} options={getChartOptions('doughnut', darkMode)} />}
          </ChartContainer>

          <ChartContainer
            title="Statut des Candidatures"
            icon={<FaUserTie className="text-green-600 dark:text-green-400" />}
            darkMode={darkMode}
          >
            {chartData && <Pie data={chartData.candidatures} options={getChartOptions('pie', darkMode)} />}
          </ChartContainer>

          <ChartContainer
            title="Vue d'ensemble"
            icon={<FaChartBar className="text-purple-600 dark:text-purple-400" />}
            darkMode={darkMode}
          >
            {chartData && <Bar data={chartData.global} options={getChartOptions('bar', darkMode)} />}
          </ChartContainer>
        </div>

        {/* Sections détaillées */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Dernières offres */}
          <div className={`rounded-lg shadow-lg p-6 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
            <h3 className={`text-xl font-semibold mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
              Dernières Offres
            </h3>
            {offers.length > 0 ? (
              <div className="space-y-4">
                {offers.slice(0, 5).map(offer => (
                  <OfferCard key={offer.id} offer={offer} darkMode={darkMode} />
                ))}
              </div>
            ) : (
              <p className={`${darkMode ? 'text-gray-400' : 'text-gray-500'} italic`}>
                Aucune offre disponible
              </p>
            )}
          </div>

          {/* Dernières candidatures */}
          <div className={`rounded-lg shadow-lg p-6 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
            <h3 className={`text-xl font-semibold mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
              Dernières Candidatures
            </h3>
            {candidatures.length > 0 ? (
              <div className="space-y-4">
                {candidatures.slice(0, 5).map(candidature => (
                  <CandidatureCard key={candidature.id} candidature={candidature} darkMode={darkMode} />
                ))}
              </div>
            ) : (
              <p className={`${darkMode ? 'text-gray-400' : 'text-gray-500'} italic`}>
                Aucune candidature reçue
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Composants auxiliaires
const StatCard = ({ icon, title, value, darkMode }) => (
  <div className={`p-6 rounded-xl shadow-lg transition-colors ${
    darkMode ? 'bg-gray-800' : 'bg-white'
  }`}>
    <div className={`flex items-center gap-3 mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
      {icon}
      <h3 className="text-lg font-semibold">{title}</h3>
    </div>
    <p className={`text-3xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>
      {value}
    </p>
  </div>
);

const ChartContainer = ({ title, icon, children, darkMode }) => (
  <div className={`p-6 rounded-xl shadow-lg ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
    <div className={`flex items-center gap-2 mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
      {icon}
      <h3 className="text-xl font-semibold">{title}</h3>
    </div>
    <div className="h-64">
      {children}
    </div>
  </div>
);

const OfferCard = ({ offer, darkMode }) => {
  const getStatusColor = (status) => {
    switch (status) {
      case 'valide':
        return darkMode ? 'bg-green-900 text-green-200' : 'bg-green-100 text-green-800';
      case 'rejetée':
        return darkMode ? 'bg-red-900 text-red-200' : 'bg-red-100 text-red-800';
      default:
        return darkMode ? 'bg-yellow-900 text-yellow-200' : 'bg-yellow-100 text-yellow-800';
    }
  };

  return (
    <div className={`p-4 rounded-lg border transition-colors ${
      darkMode 
        ? 'bg-gray-700 border-gray-600' 
        : 'bg-gray-50 border-gray-200'
    }`}>
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <h4 className={`font-semibold text-sm ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            {offer.title}
          </h4>
          <p className={`text-xs mt-1 ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
            {offer.domaine}
          </p>
          <div className="flex items-center mt-2 text-xs">
            <FaCalendarAlt className={`mr-1 ${darkMode ? 'text-gray-400' : 'text-gray-500'}`} />
            <span className={`${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
              {new Date(offer.createdAt).toLocaleDateString('fr-FR')}
            </span>
          </div>
        </div>
        <span className={`text-xs px-2 py-1 rounded-full font-medium ${getStatusColor(offer.status)}`}>
          {offer.status}
        </span>
      </div>
    </div>
  );
};

const CandidatureCard = ({ candidature, darkMode }) => {
  const getStatusIcon = (status) => {
    switch (status) {
      case 'valide':
        return <FaCheckCircle className="text-green-500" />;
      case 'rejetee':
        return <FaTimesCircle className="text-red-500" />;
      default:
        return <FaClock className="text-yellow-500" />;
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'valide':
        return 'Acceptée';
      case 'rejetee':
        return 'Rejetée';
      default:
        return 'En attente';
    }
  };

  return (
    <div className={`p-4 rounded-lg border transition-colors ${
      darkMode 
        ? 'bg-gray-700 border-gray-600' 
        : 'bg-gray-50 border-gray-200'
    }`}>
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <div className="flex items-center gap-2 mb-2">
            <FaBuilding className={`${darkMode ? 'text-gray-400' : 'text-gray-500'}`} size={12} />
            <h4 className={`font-semibold text-sm ${darkMode ? 'text-white' : 'text-gray-900'}`}>
              {candidature.entreprise?.companyName || 'Entreprise inconnue'}
            </h4>
          </div>
          <p className={`text-xs ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
            {candidature.Offer?.title || `Offre #${candidature.offerId}`}
          </p>
          <div className="flex items-center mt-2 text-xs">
            <FaCalendarAlt className={`mr-1 ${darkMode ? 'text-gray-400' : 'text-gray-500'}`} />
            <span className={`${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
              {new Date(candidature.createdAt).toLocaleDateString('fr-FR')}
            </span>
          </div>
        </div>
        <div className="flex items-center gap-1">
          {getStatusIcon(candidature.status)}
          <span className={`text-xs font-medium ${
            darkMode ? 'text-gray-300' : 'text-gray-600'
          }`}>
            {getStatusText(candidature.status)}
          </span>
        </div>
      </div>
    </div>
  );
};

export default DashboardMinistere;
