// src/Pages/Offre/OffreDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useTheme } from '../../context/ThemeContext';
import axios from 'axios';
import { FaCalendarAlt, FaMapMarkerAlt, FaMoneyBillWave, FaBuilding, FaArrowLeft } from 'react-icons/fa';

const OffreDetails = () => {
  const { id } = useParams();
  const { darkMode } = useTheme();
  const [offre, setOffre] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOffre = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/api/offres/${id}`);
        setOffre(response.data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchOffre();
  }, [id]);

  if (loading) return (
    <div className={`flex justify-center items-center h-64 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
    </div>
  );

  if (error) return (
    <div className={`p-4 ${darkMode ? 'bg-gray-800 text-red-400' : 'bg-white text-red-600'}`}>
      Erreur: {error}
    </div>
  );

  return (
    <div className={`min-h-screen py-8 px-4 sm:px-6 lg:px-8 transition-colors duration-300 ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="max-w-4xl mx-auto">
        <Link 
          to="/home" 
          className={`inline-flex items-center mb-6 ${darkMode ? 'text-blue-400 hover:text-blue-300' : 'text-blue-600 hover:text-blue-800'}`}
        >
          <FaArrowLeft className="mr-2" /> Retour aux offres
        </Link>

        <div className={`rounded-lg shadow-lg overflow-hidden ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <div className={`p-6 sm:p-8 border-b ${darkMode ? 'border-gray-700' : 'border-gray-200'}`}>
            <h1 className="text-2xl sm:text-3xl font-bold mb-2">{offre.title}</h1>
            <h2 className={`text-lg ${darkMode ? 'text-gray-300' : 'text-gray-600'} mb-4`}>{offre.entreprise}</h2>
            
            <div className="flex flex-wrap gap-4 mb-6">
              <span className={`flex items-center ${darkMode ? 'text-blue-400' : 'text-blue-600'}`}>
                <FaCalendarAlt className="mr-2" /> {new Date(offre.dateLimite).toLocaleDateString()}
              </span>
              <span className={`flex items-center ${darkMode ? 'text-green-400' : 'text-green-600'}`}>
                <FaMapMarkerAlt className="mr-2" /> {offre.localisation}
              </span>
              <span className={`flex items-center ${darkMode ? 'text-yellow-400' : 'text-yellow-600'}`}>
                <FaMoneyBillWave className="mr-2" /> {offre.salaire}
              </span>
              <span className={`flex items-center ${darkMode ? 'text-purple-400' : 'text-purple-600'}`}>
                <FaBuilding className="mr-2" /> {offre.typeContrat}
              </span>
            </div>
          </div>

          <div className="p-6 sm:p-8">
            <h3 className="text-xl font-semibold mb-4">Description du poste</h3>
            <div className={`prose max-w-none ${darkMode ? 'prose-invert' : ''}`}>
              {offre.description.split('\n').map((paragraph, i) => (
                <p key={i} className="mb-4">{paragraph}</p>
              ))}
            </div>

            <h3 className="text-xl font-semibold mt-8 mb-4">Compétences requises</h3>
            <div className="flex flex-wrap gap-2 mb-8">
              {offre.competences.map((competence, index) => (
                <span 
                  key={index} 
                  className={`px-3 py-1 rounded-full text-sm font-medium ${darkMode ? 'bg-blue-900 text-blue-200' : 'bg-blue-100 text-blue-800'}`}
                >
                  {competence}
                </span>
              ))}
            </div>

            <div className="mt-8 flex flex-col sm:flex-row gap-4">
              <button 
                className={`px-6 py-3 rounded-lg font-semibold transition-colors ${darkMode ? 'bg-blue-600 hover:bg-blue-700' : 'bg-blue-500 hover:bg-blue-600'} text-white`}
              >
                Postuler maintenant
              </button>
              <button 
                className={`px-6 py-3 rounded-lg font-semibold transition-colors border ${darkMode ? 'border-gray-600 hover:bg-gray-700' : 'border-gray-300 hover:bg-gray-100'}`}
              >
                Enregistrer l'offre
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OffreDetails;
